INSERT INTO `medicine` VALUES ('1', '龙葵', '龙珠;山辣椒', '阳药', '1年生草本。多分枝,有纵棱,棱上疏生细毛。叶互生;叶片卵形或卵状椭圆形,长3~9cm,宽2~5cm,先端急尖或渐尖而钝,基部宽楔形或圆形不对称,稍下延至叶柄,全缘或不规则的波状浅齿,两面无毛或疏生短柔毛。蝎尾状花序近伞形状腋外生,有4-10花;总花梗长1-2.5cm,具短柔毛;花梗长5-10mm,下垂;花萼浅杯状,长约2mm,5裂;花冠白色,辐状,长约7mm,5深裂,裂片椭圆形;雄蕊不伸出花冠外,花丝极短,花药长圆形,黄色,孔裂;子房卵形,无毛,花柱中部以下具白色绒毛,柱头小,头状,与雄蕊等长。浆果球形,直径4-6mm,成熟时紫黑色,有光泽。种子多数,卵形,黄色,具网纹', '多生长于山坡林缘、溪沟边灌草丛中或村庄附近。', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, '', 'longkui;lk;longzhu;lz;shanlajiao;slj', '2018-07-15 13:27:21', null, null, null, '1');
INSERT INTO `medicine` VALUES ('2', '绵毛鹿茸草', '千年霜;白毛松', '阳药', '多年生草本。茎丛生,常有隔年的枯茎,全株有灰白色绵毛,上部并具腺毛。叶对生或3叶轮生,较密集,节间短;基部叶片鳞片状,3~5mm,向上成狭披针形,长1~2.5cm,宽2~3mm,先端急尖,基部渐狭,下延于茎并成狭翅,全缘,两面均密被灰白色绵毛,老时上面毛多少脱落。花少数,单生于茎顶部的叶腋,呈顶生总状花序;花梗长2-7mm,具2叶状小苞片,于萼筒基部;花萼筒状,被腺毛或绵毛与腺毛,萼筒有9条凸起的粗肋,4齿裂,裂片线形或线状披针形,与萼筒等长或稍长;花冠长2~2.5cm,淡紫色或白色,筒部细长,  近喉部扩大,二唇形,上唇盔状,弯曲2裂,下唇3裂,中裂片稍大;雄蕊4,前方1对长于后方1对;子房卵形,花柱细长,先端弯向前方,柱头长圆形。蒴果长圆形,顶端尖锐,有4纵沟。 ', '多生长于向阳山坡、岩石旁或松树林下。', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, '', 'mianmaolurongcao;mmlrc;qiannianshuang;qns;baimaosong;bms', '2018-07-14 21:23:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('3', '白花泡桐', '泡桐', '阳药', '落叶乔木。树皮灰褐色,幼枝、叶、花序及幼果均被黄褐色星状绒毛,叶柄、叶片上面和花梗老时无毛。叶片长卵状心形,卵状心形,长达20cm,先端长渐尖或急尖,新枝上的叶片有时2裂,下面有心状毛及腺,老叶下面密被绒毛,全缘;叶柄长达12cm。花序狭长几呈圆柱形,长25cm;小聚伞花序具3~8花;总花梗与花梗近等长;花萼倒圆锥形,长2~2.5cm分裂至1/4~1/3处,花后毛逐渐脱片卵圆形至三角状卵圆形,至果期变为角形;花冠筒状漏斗形,白色仅背面稍带色或浅紫色,长8-12cm,筒部向上逐渐扩大,上唇较狭,2裂,反卷,下唇3裂先端均有齿痕状齿或凹头,二强雄蕊,隐于花冠筒内;子房2室,花柱细长,内弯。蒴果木质。种子边翅长6~10mm。', '多生长于向阳山坡。有栽培', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'baihuapaotongOther.png', 'baihuapaotong;bhpt;paotong;pt', '2018-07-14 21:23:23', null, null, null, '1');
INSERT INTO `medicine` VALUES ('4', '毛泡桐', '毛桐;毛桐树', '阳药', '落叶乔木。树皮褐灰色,小枝有明显皮孔,幼枝常具粘质短柔毛。叶片心形,长达40cm,先端急尖,全缘或波状浅裂,上面毛稀疏,下面毛较密或较疏,老叶下面密被具柄的树枝状毛或黏质腺毛;叶柄常有粘质短腺毛。花序侧枝不发达,长为中央主枝之半或稍短,花序金字塔形或狭圆锥形,长达50cm;小聚伞花序的总花梗长1-2cm,具3~5花;花萼浅钟形,长约1.5cm,外面绒毛不脱落,分裂至中部或超过中部,裂片卵状长圆形,在花期急尖或稍钝尖至果期钝头;花冠紫色,漏斗状钟形,长5~7.5cm,在离筒基部约5mm处拱曲,向上突然膨大,外面有腺毛;雄蕊长达2.5cm;子房卵圆形,有腺毛,花柱短于雄蕊。蒴果卵圆形,长3~4.5cm,幼时密生黏质腺毛,宿存花萼不反卷。', '栽培', '深秋或冬季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'maopaotongOther.png', 'maopaotong;mpt;maotong;mt;maotongshu;mts', '2018-07-14 21:35:01', null, null, null, '1');
INSERT INTO `medicine` VALUES ('5', '天目地黄', '天芥菜;野芥菜', '阴药', '多年生草本。根茎肉质肥厚,橙黄色。全体被多节长柔毛。茎单一或基部分枝。基生叶多少莲座状排列,叶片卵形或长椭圆形,长6-12cm,宽3-6cm先端钝或急尖,基部逐渐收缩成长的翅柄边缘具不规则圆齿或粗锯齿,或为圆齿的浅裂片,两面疏被白色柔毛,茎生叶发达外形与基生叶相似,向上逐渐缩小。花单生叶腋;花梗长1-4cm,弯曲而后上升;花萼长1-2cm,5裂,裂片披针形或卵状披针形,先端略尖,不等长:花冠紫红色,长5.5~7cm,外面被多节长柔毛,二唇形上唇裂片长卵形,下唇裂片长椭圆形,中间裂片较大;二强雄蕊,后方1对稍短,花丝基部被短腺毛,前方1对稍长,花丝无毛，药室长圆形,基部叉开成一直线;花柱顶端扩大,先端尖或圆钝。蒴果卵形,具宿存的花萼及花柱。种子多粒,卵形至长卵形,表面具网眼。', '多生长于山坡草丛中。', '夏、秋季采收,洗净,鲜用。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'tianmudihuangOther.png', 'tianmudihuang;tmdh;tianjiecai;tjc;yejiecai;yjc', '2018-07-14 21:46:03', null, null, null, '1');
INSERT INTO `medicine` VALUES ('6', '阴行草', '山油麻;脱皮黄;脱力黄;山茵陈', '阳药', '1年生草本。全株干时变为黑色,被柔毛。茎直立,中空,上部多分枝。叶对生,茎上部的叶互生;叶片厚纸质回羽状全裂,裂片约3对,线状披针形,先端急尖,全缘。花对生于茎枝上部,或有时假对生,形成稀疏的总状花序;苞片叶状较花萼短,羽状深裂或全裂;花梗长1-2mm花萼筒长1-1.5cm,有主脉10条,5裂片披针形,近等长;花冠黄色,长约2.5cm,二唇形,上唇镰状拱曲,紫红色,下唇约与上唇等长,顶端3裂;二强雄蕊,花丝下部均被短纤毛;子房长卵形,柱头头状。蒴果披针状长圆形,包藏于宿存的花萼内,约与萼筒等长。种子长卵形,有皱纹,黑色。', '多生长于山坡、草丛中。', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'yinxingcaoOther.png', 'yinxingcao;yxc;shanyouma;sym;tuopihuang;tph;tuolihuang;tlh;shanyinchen;syc', '2018-07-14 22:14:17', null, null, null, '1');
INSERT INTO `medicine` VALUES ('7', '木芙蓉 ', '九头花;芙蓉猎骨皮', '阳药', '落叶灌木。小枝、叶柄、花梗、小苞片和花萼均密被星状毛和短柔毛相混的细绵毛。叶片大宽卵形至卵圆形或心形，常5~7掌状浅裂，基部截形至心形，裂片三角形，先端渐尖，边缘具钝圆锯齿，主脉7 ~11条，上面疏被星状毛，下面密被星状细绒毛；托叶披针形，早落。花大，单生于枝端叶腋或排成总状花序式。蒴果球形，密被淡黄色刚毛和绵毛。种子肾形，黑褐色，背部被长柔毛。', '多栽培，亦有逸生于山坡路边或溪沟边。', '深秋或初冬采收，剥取根皮，洗净，鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'mufurongOther.png', 'mufurong ;mfr ;jiutouhua;jth;furongliegupi;frlgp', '2018-07-15 14:21:33', null, null, null, '1');
INSERT INTO `medicine` VALUES ('22', '白花败酱', '苦益菜,苦野菜,白苦叶菜', '阴药', '多年生草本。地下根状茎长\r\n而横走,偶在地表面生长。茎被粗白毛或仅\r\n两侧各有1列粗短毛。基生叶丛生,叶片宽\r\n卵形或近圆形,长4~10cm,宽2~5cm,\r\n先端渐尖,基部楔形下延,边缘有粗齿,不\r\n分裂或大头状深裂。叶柄较叶片稍长;茎生\r\n叶对生,叶片卵形或窄椭圆形,长4~11cm,\r\n宽2~5cm,先端渐尖,基部楔形下延,边\r\n缘羽状分裂或不裂,两面疏生粗毛,脉上尤\r\n密,茎上部叶渐无柄。聚伞花序多分枝,排\r\n列成伞房状圆锥花序,花序梗上密生或仅两\r\n列粗毛,花序分枝基部有总苞片1对,较狭;\r\n花萼细小,5齿裂;花冠钟状,白色,顶端5裂,\r\n裂片不等形;雄蕊4;伸出;子房发育室边\r\n缘及表面有毛,花柱较雄蕊短。瘦果倒卵形,\r\n基部贴生在增大的圆翅状膜质苞片上。', '多生长于林缘，溪沟边草丛中或山地林下', '夏秋两季采收，洗净，鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'baihuabaijiangOther.png', 'baihuabaijiang;bhbj;kuyicai,kuyecai,baikuyecai;kyc,kyc,bkyc', '2018-07-15 13:12:50', null, null, null, '1');
INSERT INTO `medicine` VALUES ('23', '铁钓竿', '两头牢;两头吊;两头丁;两头生', '阴药', '多年生草本。根茎极短。茎叶无毛。叶片厚纸质,长卵形至卵状披针形,长6~15cm,宽2.5~6cm,先端渐尖,基部多浑圆,边缘具向前偏斜的三角状锯齿,有叶浅波状凹缺,而在凹缺下缘有一个小凸尖,上面多少有光泽。花序近头状,腋生;苞片和萼裂片密生硬睫毛;花冠紫色、淡紫色或蓝紫色,长5~6mm,裂片狭三角形,长约1.5mm;花药长1~1.3mm。蒴果卵形。种子长0.5~0.6mm。', '多生长于山坡林下及灌木丛下', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'tiediaoganOther.png', 'tiediaogan;tdg;liangtoulao;ltl;liangtoudiao;ltd;liangtouding;ltd;liangtousheng;lts', '2018-07-15 13:20:51', null, null, null, '1');
INSERT INTO `medicine` VALUES ('25', '南瓜', '金瓜龙', '阳药', '1年生蔓生或攀援草质藤本。\r\n茎粗壮,具棱或沟,通常节上生根,密被短\r\n刚毛,卷须3~4歧。叶片宽卵形或近圆形,\r\n长15-30cm,先端急尖,基部深心形,5\r\n浅裂或有5角,边缘具细齿,两面被粗毛\r\n下面尤密,上面和沿中脉通常具不规则白色\r\n斑纹;叶柄稍长于叶片,具短毛。花雌雄同\r\n株,单生。雄花花萼钟形,裂片线形,被柔\r\n毛,上部扩大成叶状;花冠黄色,钟形,长\r\n8cm,5中裂,裂片外展,有皱纹,雄蕊3,\r\n花药靠合,药室曲折。雌花单生,花梗短\r\n花萼裂片显著叶状,花冠与雄花相同;花柱\r\n短,柱头3,顶端2裂。果通常扁球形、卵形\r\n长椭圆形。', '栽培', '夏、初秋季节采收,洗净，\r\n鲜用或干燥。', '50', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'nanguaOther.png', 'nangua;ng;jingualong;jgl', '2018-07-15 13:22:10', null, null, null, '1');
INSERT INTO `medicine` VALUES ('26', '王瓜', '山苦瓜郎,山天罗', '阳药', '多年生攀援草质藤本。块\r\n根纺锤形,肥大。茎纤细,多分枝,被短柔\r\n毛,卷须2歧,被毛。叶片宽卵形或圆形\r\n长5-18cm,宽5~12cm,先端钝或渐尖,\r\n基部深心形,常3~5浅裂至深裂,或有时\r\n不裂,边缘具细齿或波状齿,上面被短绒毛\r\n及散生短刚毛,下面密被短茸毛,基出掌状\r\n3-7脉;叶柄长3~10cm,密被短茸毛及\r\n短刚毛状硬毛。花单性异株。雄花花萼筒梗\r\n长5~10cm,被短茸毛:花梗长约5mm，\r\n被茸毛;苞片线状披针形,全缘;花萼筒喇\r\n叭形,被短茸毛,5裂,裂片线状披针形\r\n花冠白色,裂片长圆形,先端有长的丝状裂\r\n片;雄蕊3;花丝短,分离,退化雌蕊刚毛状。\r\n雌花单生,花梗长0.5-1cm;花萼和花冠\r\n与雄花相同;子房圆形,均密被短柔毛。果\r\n实卵球形、卵状椭圆形或球形。种子横长圆\r\n形,3室,中央室呈凸起的增厚环带,两侧\r\n室大,中空。', '多生长于山坡、沟旁疏林', '秋、初冬季节采收,洗净，\r\n鲜用或干燥。', '89', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'wangguaOther.png', 'wanggua;wg;shankugualang,shantianluo;skgl,stl', '2018-07-15 13:29:45', null, null, null, '1');
INSERT INTO `medicine` VALUES ('27', '凌霄', '倒挂金钟;骨地松;肚饥花', '阳药', '落叶木质藤本,借气生根攀附于其他物上。叶对生奇数羽状复叶,小叶通常7-9,小叶片卵形或卵状披针形,长3-7cm,宽1.5~3cm,先端尾状渐尖基部宽楔形,两侧不等大,边缘有粗锯齿,两面无毛,两小叶柄间有淡黄色柔毛。花大,组成大型疏散的圆锥花序,顶生:花萼钟形,长约3cm,5裂至中部,裂片披针形:花冠漏斗状钟形,长约5cm,直径约7cm,内面鲜橘红色,外面橙黄色,5裂,裂片等大,半圆形,扁平而直立:雄蕊生于花冠筒近基部,花丝细长,花药丁字形着生:花柱线形,柱头扁平,2裂。萌果长如豆荚,顶端钝种子多数', '栽培', '深秋或冬季采收,洗净鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'linxiaoOther.png', 'lingxiao;lx;daoguajinzhong;dgjz;gudisong;gds;dujihua;djh', '2018-07-15 13:31:56', null, null, null, '1');
INSERT INTO `medicine` VALUES ('28', '栝楼', '野西瓜,老虎爪。', '阳药', '多年生攀援草质藤本。块根\r\n圆柱形,粗大肥厚,灰黄色。茎具纵棱及槽,\r\n被白色开展柔毛。卷须腋生,细长,3~7\r\n歧。叶片轮廓近圆形或心形,长宽5~20cm,\r\n先端通常圆钝,基部心形,通常3~7掌状\r\n浅裂或中裂,稀深裂或不分裂而仅有不等大\r\n的粗齿,裂片菱状倒卵形、长圆形至卵状披\r\n针形,边缘有疏齿或缺刻状,两面沿脉被长\r\n柔状硬毛,具基出掌状3~5脉;叶柄被长\r\n柔毛。花单性异株。雄花单生或数朵组成总\r\n状花序,或单花与总状花序同生于叶腋;苞\r\n片倒卵形或宽卵形,长1.5-2cm,边缘有\r\n齿;花萼筒圆筒状,长约3.5cm,裂片披针形\r\n全缘;花冠白色,裂片倒卵形,长约2cm,\r\n先端中央具1绿色小尖头,两侧具丝状裂片,\r\n被柔毛;雄蕊3,花丝短,有毛。雌花单生,\r\n花梗被短柔毛;花萼筒圆筒形,长约2.5cm,\r\n裂片与花冠与雄蕊相同;子房椭圆形,长约\r\n2cm,柱头3裂。果实近球形,成熟时红色，\r\n光滑。种子卵状椭圆形，扁平。', '多生长于山坡，山脚，路边或田野草丛中', '秋，初冬季节采收，洗净，鲜用或干燥', '40', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'wangguaOther.png', 'gualou;gl;yexigua,laohuzhao。;yxg,lhz。', '2018-07-15 13:40:09', null, null, null, '1');
INSERT INTO `medicine` VALUES ('29', '轮叶沙参', '山沙参,沙参', '阳药', '多年生草本。根圆锥形,有\r\n横纹。茎无毛。茎生叶3~6轮生,叶片卵\r\n圆形至线状披针形,长2~14cm,宽达2.5cm,\r\n先端渐尖,基部狭窄,边缘有锯齿,两面疏\r\n生短柔毛。花序狭圆锥状,分枝轮生,花下垂；\r\n花萼无毛,筒部倒圆锥状,5裂,裂片钻状,\r\n全缘;花冠筒状钟形,口部稍缢缩,蓝色、\r\n蓝紫色,长7~11mm,无毛,5浅裂,裂片短,\r\n三角形;雄蕊5,稍伸出,花丝基部变宽,\r\n边缘具密柔毛;花盘细筒状;花柱伸出花冠\r\n外。蒴果球状圆锥形或卵圆状圆锥形。种子黄棕色,长圆状圆锥形,稍扁,有1条棱,\r\n并由棱扩展成1条白带。', '多生长于山坡、林缘草地', '秋、初冬季节采收,洗净,\r\n鲜用或干燥。', '163', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'lunyeshashenOther.png', 'lunyeshacan;lysc;shanshacan,shacan;ssc,sc', '2018-07-15 13:48:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('30', '吊石苣苔', '石吊兰;石豇豆;石杨梅;石壁', '阴药', '常绿附生小灌木。茎长25cm,有匍匐茎,分枝或不分枝,幼枝常具短毛。叶在枝端密集,下部的3-4叶轮生,叶片革质,形状变化较大,楔形、线形狭长圆形、狭卵形或倒卵形,长2.5~6cm，宽0.5~2cm,先端钝或急尖,基部楔形,边缘在中部以上有钝状粗锯齿,两面无毛侧脉不明显。聚伞花序顶生,具1-3花苞片革质,早落;花梗长3-10mm;花萼长3~4cm,近无毛,5深裂,裂片狭三角形;花冠白色,稍带紫色,长3.5~4.8cm,无毛。檐部二唇形,上唇2浅裂,下唇3裂,花冠内面具2条黄色肋状突起和深紫色线纹;发育雄蕊2,退化雄蕊3;花盘杯状,有尖齿子房线形,无毛。蒴果线形。种子纺锤形顶端有1条长。', '多生长于阴湿的峭壁岩缝和岩石脚壁下或树上。', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'diaoshijutaiOther.png', 'diaoshijutai;dsjt;shidiaolan;sdl;shijiangdou;sjd;shiyangmei;sym;shibi;sb', '2018-07-15 13:54:07', null, null, null, '1');
INSERT INTO `medicine` VALUES ('31', '轮叶沙参', '山沙参,沙参', '阳药', '多年生草本。根圆锥形,有\r\n横纹。茎无毛。茎生叶3~6轮生,叶片卵\r\n圆形至线状披针形,长2~14cm,宽达2.5cm,\r\n先端渐尖,基部狭窄,边缘有锯齿,两面疏\r\n生短柔毛。花序狭圆锥状,分枝轮生,花下垂；\r\n花萼无毛,筒部倒圆锥状,5裂,裂片钻状,\r\n全缘;花冠筒状钟形,口部稍缢缩,蓝色、\r\n蓝紫色,长7~11mm,无毛,5浅裂,裂片短,\r\n三角形;雄蕊5,稍伸出,花丝基部变宽,\r\n边缘具密柔毛;花盘细筒状;花柱伸出花冠\r\n外。蒴果球状圆锥形或卵圆状圆锥形。种子黄棕色,长圆状圆锥形,稍扁,有1条棱,\r\n并由棱扩展成1条白带。', '多生长于山坡、林缘草地', '秋、初冬季节采收,洗净,\r\n鲜用或干燥。', '163', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'lunyeshashenOther.png', 'lunyeshacan;lysc;shanshacan,shacan;ssc,sc', '2018-07-15 13:57:41', null, null, null, '1');
INSERT INTO `medicine` VALUES ('32', '羊乳', '山菜头,猪娘果,猪母茄, 奶奶头。', '阴药', '多年生缠绕草本植物。根倒\r\n卵状纺锤形。叶在主茎上互生,叶片披针形\r\n或菱状狭卵形,长0.8-1.4cm,宽3~7mm\r\n在小枝顶端通常2~4枚簇生,而近于对生\r\n或轮生状;叶柄短小,叶片菱状卵形、狭卵\r\n形或椭圆形,长3~10cm,宽1.5~4cm，\r\n先端急尖或钝,基部渐狭,通常全缘或有疏\r\n波状锯齿,两面无毛。花单生或对生于小枝\r\n顶端,具短梗;萼5裂,裂片卵状披针形，\r\n长1.5~2cm,宽5-8mm,绿色;花冠钟\r\n形,长2-4cm,5浅裂,裂片先端反卷,\r\n黄绿色或乳白色,内有紫褐色斑点;雄蕊5,\r\n花丝短粗;子房半下位,柱头3裂。蒴果扁\r\n圆锥形,有宿萼。种子有膜质翅。', '多生长于山地灌木林下阴 湿处。', '秋、初冬季节采收,洗净,\r\n鲜用或干燥', '89', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'yangruOther.png', 'yangru;yr;shancaitou,zhuniangguo,zhumuqie, nainaitou。;sct,zng,zmq, nnt。', '2018-07-15 14:06:00', null, null, null, '1');
INSERT INTO `medicine` VALUES ('33', '半边莲', '瓜子草,半边莲。', '阴药', '多年生矮小草本。茎细弱,\r\n常匍匐,节上生不定根。叶互生;叶片长圆\r\n状披针形或线形,长0.8-2cm,宽3-7mm,\r\n先端急尖,基部圆形或宽楔形,全缘或顶部\r\n有波状小齿,无毛。花两性,通常1朵,\r\n生于分枝上部叶腋,基部通常有长约lmm\r\n的小苞片2枚;花萼筒倒长锥状,无毛,5\r\n裂,约与萼筒等长;花冠粉红色或白色,长\r\n1-1.5cm,5裂,背面裂至基部,喉部以下\r\n具白色柔毛;雄蕊5,花丝中部以上连合,\r\n花丝无毛,未连合部分的花丝侧面生柔毛\r\n雌蕊1,子房下位,2室。蒴果倒圆锥状。\r\n种子椭圆状,稍扁压,近肉质。', '多生长于潮湿路边、水田 边、沟河边及潮湿的草地。', '夏、秋季生长茂盛时采收,\r\n洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, '', 'banbianlian;bbl;guazicao,banbianlian。;gzc,bbl。', '2018-07-15 15:05:40', null, null, null, '1');
INSERT INTO `medicine` VALUES ('38', '木槿', '米烫花;咏梅花;新米花', '阳药', '落叶灌木。嫩枝被黄褐色星状绒毛。叶片菱状卵形或三角状卵形，具深浅不同的3裂或不裂，先端渐尖或钝，基部禊形，边缘具整齐粗齿，主脉3条，两面均隆起，下面脉上疏被毛或近无毛；托叶线形。花单生于枝端叶腋，小苞片6 ~ 8，线形；花萼钟状，密被星状绒毛；花柱枝光滑无毛。 蒴果卵圆形，密被黄色星状绒毛。种子肾形， 淡褐色，背部被淡黄色长柔毛。', '栽培', '深秋或初冬采收，剥取根皮，洗净，鲜用或干燥；夏、秋季，选晴天早晨花半开时采摘，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'mujinOther.png', 'mujin;mj;mitanghua;mth;yongmeihua;ymh;xinmihua;xmh', '2018-07-15 14:27:07', null, null, null, '1');
INSERT INTO `medicine` VALUES ('39', '白花单瓣木槿', '白花新米花根', '阳药', '落叶灌木。嫩枝被黄褐色星状绒毛。叶片菱状卵形或三角状卵形，具深浅不同的3裂或不裂，先端渐尖或钝，基部禊形，边缘具整齐粗齿，主脉3条，两面均隆起，下面脉上疏被毛或近无毛；托叶线形。花单生于枝端叶腋，小苞片6 ~ 8，线形；花萼钟状，密被星状绒毛；花柱枝光滑无毛。 蒴果卵圆形，密被黄色星状绒毛。种子肾形， 淡褐色，背部被淡黄色长柔毛。花纯白色，单瓣。', '栽培', '深秋或初冬采收，剥取根皮，洗净，鲜用或干燥', '1', '暂无', '', '', null, null, '1', null, null, null, null, 'baihuadanbanmujinOther.png', 'baihuadanbanmujin;bhdbmj;baihuaxinmihuagen;bhxmhg', '2018-07-15 14:28:42', null, null, null, '1');
INSERT INTO `medicine` VALUES ('41', '铜锤玉带草', '白珍珠,水珍珠', '阴药', '多年生草本。茎平卧,有白\r\n色乳汁,被开展的柔毛,不分枝或在基部有\r\n分枝。叶互生;叶片圆卵形、心形或卵形\r\n长1~2.5cm,宽1~2cm,先端圆钝或急尖,\r\n基部歪心形,边缘具齿,两面疏生腺状短柔\r\n毛,叶脉掌状。花单生叶腋;花梗长1-2cm\r\n花萼筒坛状,无毛,5裂,裂片线状披针形,\r\n伸直,边缘具2-3对小齿;花冠紫红色\r\n淡紫色、绿色或白色,长5~7mm,二唇形\r\n裂片5,上唇2裂片线状披针形,下唇3裂\r\n片披针形,花冠筒外面无毛,内面生柔毛\r\n雄蕊5,花丝中部以上连合,筒部无毛,花\r\n药管长约lmm,外面有短柔毛,下方2花药\r\n具茸毛;柱头头状。浆果紫红色,椭圆状球形\r\n种子近圆球形,表面有小疣突', '多生长于潮湿疏林下、路 旁、山脚草丛中或田埂边', '夏、秋季采收,洗净,鲜\r\n用或干燥', '291', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, '', 'tongchuiyudaicao;tcydc;baizhenzhu,shuizhenzhu;bzz,szz', '2018-07-15 15:05:26', null, null, null, '1');
INSERT INTO `medicine` VALUES ('42', '兰花参', '疯草,绿花白根草', '阳药', '多年生草本。根细长,外面\r\n白色,细胡萝卜状,直径可达4mm。茎自基\r\n部多分枝,有白色乳汁。叶互生;叶片倒披\r\n针形至线状披针形,长1-3cm,宽2-4mm,\r\n先端短尖,基部楔形至圆形,全缘或呈波状\r\n或具疏锯齿;无柄。花顶生或及腋生,具长\r\n花梗,排成圆锥状;花萼筒部,倒卵状椭圆\r\n形,5深裂,裂片线状披针形,直立:花冠\r\n漏斗状钟形,蓝色,长5-8mm,5深裂,\r\n裂片椭圆状长圆形;雄蕊5,花丝基部3裂\r\n有缘毛。蒴果倒圆锥状,有10条不明显的肋\r\n基部窄狭成果颈。种子长圆形,光滑,黄棕色。', '多生长于田边、路边、荒 地及山坡上', '夏、秋季采收,洗净,鲜\r\n用或干燥', '185', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, '', 'lanhuacan;lhc;fengcao,lvhuabaigencao;fc,lhbgc', '2018-07-15 15:05:18', null, null, null, '1');
INSERT INTO `medicine` VALUES ('43', '杏香兔耳风', '铁交杯,叶下红,一支香。', '阴药', '多年生草本。茎直立,密被\r\n棕色长毛,不分枝。叶5~6片,基部假轮生;\r\n叶片卵状长圆形,长3~10cm,宽2~6cm,\r\n先端圆钝,基部心形,全缘,少有稀疏刺状齿,\r\n上面绿色,下面有时紫红色,被棕色长柔毛;\r\n叶柄被毛。头状花序多数,具短梗,排列成\r\n总状;总苞细筒状,长约15mm,总苞片数层,\r\n外层的较短,卵形,内层的狭长圆形,先端\r\n急尖;花全为管状,白色,稍有杏仁气味,\r\n两性,结实。瘦果倒披针状长圆形,压扁,\r\n密被硬毛;冠毛多层,羽毛状,黄棕色。', '多生长于山坡、灌丛下及 沟边草丛中。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '215', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'xingxiangtuerfengOther.png', 'xingxiangtuerfeng;xxtef;tiejiaobei,yexiahong,yizhixiang。;tjb,yxh,yzx。', '2018-07-15 15:04:59', null, null, null, '1');
INSERT INTO `medicine` VALUES ('44', '奇蒿', '天葵草,野葵草,刘奇奴', '阳药', '多年生草本。茎中部以上常\r\n分枝,被柔毛。下部叶片长圆形或卵状披针\r\n形,长7-11cm,宽3-4cm,先端渐尖,\r\n基部渐狭成短柄,边缘有尖锯齿,上面被微\r\n糙毛,下面色浅,被蛛丝状微毛或近无毛\r\n上部叶十较小。头状花序极多数,无梗,密集\r\n于花枝上,在茎枝顶及上部叶腋排列成大型\r\n的圆锥状;总苞近钟形,无毛,总苞片3-4\r\n层,最外层卵圆形,中层椭圆形,内层狭长\r\n椭圆形,边缘宽膜质,带白色;花管状,白色,\r\n均结实;缘花雌性;盘花两性。瘦果徵小,\r\n长圆形,无毛。', '多生长于林缘、山坡、灌 丛及山区公路两旁。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '171_175', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'qihaoOther.png', 'qihao;qh;tiankuicao,yekuicao,liuqinu;tkc,ykc,lqn', '2018-07-15 15:04:21', null, null, null, '1');
INSERT INTO `medicine` VALUES ('45', '艾蒿', '共吾回,回棉,艾棉,艾', '阳药', '多年生草本。茎粗壮上,被白\r\n色绵毛,上部分枝。基生叶在花期枯萎;中、\r\n下部叶宽广,长6~9cm,宽4-8cm,3-5\r\n羽状浅裂或深裂,裂片椭圆形或披针形,顶\r\n生裂片披针形,侧生裂处耳形,先端钝尖,\r\n基部下延,边缘具不规则牙齿,上面散生白\r\n色小腺点和绵毛,下面被灰白色绒毛,叶脉\r\n明显凸起,叶柄长约2cm,基部具假托叶\r\n上部叶片卵状披针形,3深裂至全裂,顶端\r\n花序下的叶常全缘而为披针形近无柄。头状\r\n花序多数,在茎枝端排列成总状或圆锥状;\r\n总苞卵形,总苞片4~5层,被白色绒毛\r\n外层披针形,被短绒毛,内层长椭圆状披针\r\n形,近光滑,边缘膜质;花管状,带紫色,\r\n均结实;缘花雌性,4~6朵;盘花两性,7~8\r\n朵。瘦果椭圆形,褐色,无毛。', '栽培。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'aihaoOther.png', 'aihao;ah;gongwuhui,huimian,aimian,ai;gwh,hm,am,a', '2018-07-15 15:03:59', null, null, null, '1');
INSERT INTO `medicine` VALUES ('46', '爵床', '小青草;辣椒草;小青', '阴药', '1年生匍匐或披散草本,高10~50cm。茎通常具6棱及浅槽,沿棱被倒生短毛,节稍膨大。叶对生:叶片椭圆形或椭圆状长圆形,长1.2-6cm,宽0.6-2cm,先端急尖或钝,基部楔形,全缘或微波状,上面贴生横列的粗大钟乳体,下面沿脉疏生短硬毛。穗状花序顶生或生于上部叶腋,圆柱状,长1-4cm,密生多数小花;苞片与小苞片均为披针形;有缘毛;花萼4深裂,裂片线状披针形或线形,具白色膜质边缘,外面密被粗硬毛;花冠二唇形,淡红色或紫红色,稀白色,长约7mm;雄蕊2,伸出花冠外,2药室一上一下,下方1室无花粉而有尾状附属物:子房卵形,2室,被毛,花柱丝状。蒴果线形。长约6mm,上部有4粒种子,下部实心似柄。有疏柔毛。种子近卵圆形,两侧压扁,黑色,种皮有瘤状皱纹。', '多生长于水沟边阴湿处旷野草地、林下或路旁', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'juechuangOther.png', 'juechuang;jc;xiaoqingcao;xqc;lajiaocao;ljc;xiaoqing;xq', '2018-07-15 15:04:44', null, null, null, '1');
INSERT INTO `medicine` VALUES ('47', '车前', '蛤蟆衣', '阳药', '多年生草本。根茎短而肥厚,着生多数须根。根出叶外展;叶片薄纸质,卵形至宽卵形,长4~12cm,宽4-9cm,先端钝,基部楔形,全缘或有波状浅齿,两面均无毛。叶柄基部膨大。穗状花序排列不紧密,长20-30cm;花绿白色;苞片宽三角形,比萼片短,两者都有绿色的龙骨状突起,花冠裂片三角状长圆形,长约1mm。蒴果椭圆形,近中部开裂,基部有不脱落的花萼。种子6-8粒,卵状或椭圆状多角形黑褐色至黑色。', '多生长于荒地、路旁', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'cheqianOther.png', 'cheqian;cq;gemayi;gmy', '2018-07-15 15:08:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('48', '牡蒿', '火烧菜,马仁菜', '阳药', '多年生草本。茎基部木质\r\n化,被蛛丝状毛或无毛。基生叶长匙形,长\r\n4-5cm,宽2~3cm,3~5深裂,裂片\r\n长约lcm,宽约5mm,先端圆钝具不规则的\r\n缺刻,基部楔形,两面均被微柔毛,中脉不\r\n显著,具长叶柄及假托叶;中部叶片近楔形,\r\n先端具齿或近掌状分裂,叶无柄,有1-2\r\n假托叶;上部叶片3裂或不裂,卵圆形,基\r\n部具假托叶十。头状花序多数,排列成圆锥状,\r\n梗纤细,具线形苞片;总苞卵形,总苞片4层,\r\n外层小,卵状三角形,内层的长圆形,中脉\r\n凸起,边缘宽膜质,无毛;缘花管状,黄色,\r\n3~4朵,雌性,结实;盘花管状,黄色,5~6\r\n朵,两性,不结实。瘦果长圆形,无毛,黑\r\n褐色;冠毛无。', '多生长于路边荒野、疏林 下、林缘及山坡。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'muhaoOther.png', 'muhao;mh;huoshaocai,marencai;hsc,mrc', '2018-07-15 15:11:23', null, null, null, '1');
INSERT INTO `medicine` VALUES ('49', '白苞蒿', '四季菜,假堇菜。', '阳药', '多年生草本。茎无毛,具条\r\n棱。下部叶片花期枯萎,中部叶片倒卵形,\r\n长9~14cm,宽5~8cm,1回或2回羽状\r\n深裂,顶端裂片通常披针形,边缘具不规则\r\n锯齿,先端尾尖或急尖,基部楔形,两面无毛\r\n具叶柄和假托叶;上部叶片3裂或不裂,边\r\n缘具细锯齿,无柄。头状花序多数,排列呈\r\n圆锥状;总苞钟状或卵形,总苞片3-4层,\r\n外层较短,卵形,内层的椭圆形,棕色,边\r\n缘膜质;花管状黄色或白色,均结实;缘花\r\n雌性,4-5朵;盘花两性,67朵。瘦\r\n果圆柱形,褐色,具细条纹,无毛。', '多生长于山坡、沟边、林 下或林缘', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '383', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'baibaohaoOther.png', 'baibaohao;bbh;sijicai,jiajincai。;sjc,jjc。', '2018-07-15 15:16:39', null, null, null, '1');
INSERT INTO `medicine` VALUES ('50', '马勃', '动动烟', '阴药', '子实体近球形，直径15~20cm，无不育基部。包被俩层，薄而易于消失，外包被成熟后易于内包被分离。外包被初乳白色，后转灰棕色至黄褐色，内包被纸质，浅灰色，成熟后与外包被逐渐剥落，尽余一团灰褐色孢体，触之则孢子呈尘土样飞扬，内有棉絮状的丝状物。嗅似尘土，无味。', '夏、秋季见于路边草丛中', '夏、秋季子实体成熟时及时采收，除去泥沙，干燥。', '40_56_235', '暂无', '', '', null, null, '2', null, null, null, null, '', 'mabo;mb;dongdongyan;ddy', '2018-07-18 08:56:50', null, null, null, '1');
INSERT INTO `medicine` VALUES ('53', '赤芝', '枫树蕈', '阴药', '外形呈伞状，菌盖肾形，半圆形或近圆形。菌盖上表面黄棕色至红褐色，有光泽或无，具环状或辐射状棱纹，有的被有粉尘样的黄褐色孢子；下表面黄白色至深棕色，密生小孔状菌管孔，菌肉白色至淡棕色，分3层，上层为皮壳层，极薄，中间为菌肉层，靠近上表面色浅，下层为篦管层。菌柄圆柱形侧生，少偏生，表面黄褐色至紫褐色，光亮，断面白色至棕色，中间色较深，无菌管层。体轻，质柔韧。气特异，味苦涩。', '生于阔叶树根枯干或腐朽的木桩上。有栽培。', '全年可采，除去杂质，剪去附有朽木，泥沙或培养基下端菌柄，阴干或40~50℃烘干。', '33', '暂无', '', '', null, null, '2', null, null, null, null, '', 'chizhi;cz;fengshuxun;fsx', '2018-07-15 20:03:44', null, null, null, '1');
INSERT INTO `medicine` VALUES ('54', '硬皮地星', '山蟹', '阴药', '子实体初呈球形，后从顶端星芒状张开，外包被3层，外层薄而松软，中层纤维质，内层软骨质。成熟时形成6瓣至多瓣，湿时仰翻，干时内卷。外表面灰色至灰褐色，无中轴。孢子球形，褐色。', '生于松树林砂土地上。', '夏、秋季采收，除去杂质，干燥。', '56', '暂无', '', '', null, null, '2', null, null, null, null, '', 'yingpidixing;ypdx;shanxie;sx', '2018-07-15 20:03:10', null, null, null, '1');
INSERT INTO `medicine` VALUES ('55', '松罗', '松鼠须;杉树胡须', '阴药', '地衣体枝状，悬垂形，长达15~50cm。表面淡灰绿色至淡黄绿色。枝体基部直径约3mm，主枝粗3~4mm，次生分支整齐或不整齐多回二叉分支，枝圆柱形，少数末梢扁或有棱角。支干具环状裂隙，如脊椎状。气特异，味淡或略酸。', '生于树干上、枝干上。', '夏、秋季采收，洗净，干燥。', '295_329', '暂无', '', '', null, null, '3', null, null, null, null, '', 'songluo;sl;songshuxu;ssx;shanshuhuxu;sshx', '2018-07-15 20:03:00', null, null, null, '1');
INSERT INTO `medicine` VALUES ('56', '石耳', '石蕈', '阴药', '地衣状单片型，幼小时正圆形，长大后为椭圆形或稍不规则，直径约12cm，大者可达18cm，革质。裂片边缘浅撕裂状。上表面褐色，局部粗糙无光泽，有的具斑点状脱落而露出白色髓层；下表面棕黑色至黑色，具细颗粒状突起，密生粗短而具分叉的假根，中央脐部青灰色至黑色，直径0.5~1.2cm，有时自脐部向四周放射的脉纹明显而突出。子囊盘少见。气微，味淡。', '生于裸露的硅质岩上。', '全年可采，除去杂质，干燥。', '44', '暂无', '', '', null, null, '3', null, null, null, null, '', 'shier;se;shixun;sx', '2018-07-15 20:01:50', null, null, null, '1');
INSERT INTO `medicine` VALUES ('57', '水团花', '水晶树;红水杨梅', '阴药', '常绿灌木或小乔木。高可达5m。小枝褐色,具不整齐的近椭圆形皮孔。叶对生,叶片纸质或坚纸质,倒卵状长圆形倒卵状披针形或长椭圆形,长3-10cm,宽1-3cm,先端渐尖或长渐尖而略钝,基部楔形,全缘,上面深绿色,两面中脉均突起。头状花序单生于叶玻,稀顶生,总花梗长2.5-4.5cm,被微柔毛,下半部有5枚轮生的小苞片:萼筒长1.5-2mm,基部被毛,萼檐5裂,裂片近匙形:花冠白色,长3~4mm,顶端5裂,裂片宽卵形;雄蕊5;花柱8-10mm。萌果模形,具纵棱。', '多生长于山坡谷地及溪沟旁灌木丛中', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'shuituanhuaOther.png', 'shuituanhua;sth;shuijingshu;sjs;hongshuiyangmei;hsym', '2018-07-15 21:13:42', null, null, null, '1');
INSERT INTO `medicine` VALUES ('58', '细叶水团花', '水杨梅', '阴药', '落叶灌木。小枝红褐色,嫩枝有毛,老枝无毛。叶片纸质,卵状椭圆形或宽卵状披针形,长2-4.5cm,宽0.8-2cm,先端短渐尖至渐尖,基部宽楔形,全缘,上面沿中脉被柔毛,下面沿脉被疏柔毛:叶柄极短。头状花序通常单个顶生:总花梗长2~4.5cm,密被微柔毛,近中部具5枚轮生的小苞片或无:萼檐5裂,裂片匙形或匙状棒形:花冠淡紫红色,顶端5裂,裂片三角状卵形;雄蕊5:花柱长8-10mm。蒴果长卵状楔形。', '多生长于山谷、溪边石隙或灌木丛中', '深秋季节采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'xizuoshuituanhuaOther.png', 'xiyeshuituanhua;xysth;shuiyangmei;sym', '2018-07-15 21:21:06', null, null, null, '1');
INSERT INTO `medicine` VALUES ('59', '虎刺', '白老鼠刺;老鼠刺;黄鸡母', '阴药', '常绿小灌木。根有时缢缩呈念珠状。茎多分枝,小枝被糙硬毛,逐节生针状刺,刺长1-2cm,对生于叶柄间。叶片革质或亚革质,卵形至宽卵形,长1-2.2cm,宽0.8-1.5cm,先端急尖,稀短渐尖,基部圆形,略偏斜,全缘,干后不反卷,两面均无毛或仅下面沿中脉疏被柔毛,中脉在上面多少隆起;叶柄短,密被柔毛。花单生或成对生于叶腋,花梗短;萼筒长1.5mm,萼沿4~5裂;花冠白色,长1~1.5cm,顶端4-5裂,裂片三角状卵形子房下位。核果球形,成熟时红色。', '多生长于山谷溪沟边、路旁林下灌木丛中或石隙间。', '深秋季节采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'huciOther.png', 'huci;hc;bailaoshuci;blsc;laoshuci;lsc;huangjimu;hjm', '2018-07-15 21:28:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('60', '猪殃殃', '锉刀草;细粒草;猪娘菜', '阴药', '1年生蔓生或攀援状草本。茎四棱,棱上有倒生小钩刺。叶4-8片轮生;叶片线状倒披针形,长1-3cm,宽2~4mm,先端急尖,有短芒,基部渐狭成长楔形,上面连同叶缘和中脉均具倒生小刺毛,下面无或疏生倒刺毛;无柄。聚伞花序顶生或腋生,单生或2~3个簇生,有花3-10朵;萼筒有钩毛,长约0.5mm,萼檐近截平;花冠黄绿色,4深裂,裂片长圆形长不及lmm;雄蕊伸出,果为2个分果组成,分果近球形,直径约4mm,密生钩毛,果梗直。', '多生长于山坡路边、田边及水沟旁草丛中。', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'zhuyangyangOther.png', 'zhuyangyang;zyy;cuodaocao;cdc;xilicao;xlc;zhuniangcai;znc', '2018-07-15 21:33:14', null, null, null, '1');
INSERT INTO `medicine` VALUES ('61', '栀子', '山里黄根;黄山里;黄枝', '阳药', '常绿灌木,高1-2m。小枝绿色,幼时密被垢状毛,后近无毛。单叶对生或3叶轮生,叶柄短;托叶两片鞘状生于叶柄内侧;叶片革质,倒卵形至倒卵状椭圆形,稀倒卵状披针开或长椭圆形,长6~14cm,宽1~4cm,先端渐尖至急尖,有时略钝,基部楔形,全缘,上面光泽,有的下面脉液内簇生短毛:侧脉羽状,7-12对。花单生于小枝顶端,稀生于叶腋,具短梗,极芳香;花莓绿色,长2~3.5cm,顶端5-7裂,萼筒长1.5-2.5cm;花冠高脚碟状,白色,后变乳黄色,基部合成筒上部5-7裂,旋转排列,裂片倒卵形或倒卵状椭圆形:雄蕊与花冠裂片同数,着生于花冠喉部,花丝极短,花药线形,纵裂,2室雌蕊1,子房下位,1室,果实橙黄至橙红色卵形、倒卵形或长椭圆形,长1.5-2.5cm有5-8(~9)纵棱', '多生长于山谷溪沟边及路旁林下灌木丛中', '秋冬季挖取根,洗净切成厚片,鲜用或晒干', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'zhiziOther.png', 'zhizi;zz;shanlihuanggen;slhg;huangshanli;hsl;huangzhi;hz', '2018-07-15 21:38:59', null, null, null, '1');
INSERT INTO `medicine` VALUES ('62', '金毛耳草', '铺地蜈蚣;塌地蜈蚣;陈头蚣;穿地蜈蚣;大地蜈蚣', '阳药', '多年生匍篇草本。植株干后黄绿色。茎被金黄色柔毛。叶片薄纸质或纸质,椭圆形、卵状椭圆形或卵形,长14-2.8cm,宽0.6-1.5cm,先端急尖基部圆形,干后边缘略反卷,具緣毛,上面黄褐色,疏生短粗毛或无毛,下面黄绿色被金黄色柔毛,在脉上尤密,佣脉下面隆起叶柄长1~3mm:托叶合生,顶端齿裂裂片不等长。花1~3朵生于叶腋;花粳长2mm,被毛:萼筒钟状,长1-1.5mm密被长柔毛,萼檐4裂,裂片披针形,长2-3mm;花冠淡紫色或白色,漏斗状,长5-6mm,4裂,裂片长圆形,与花冠筒等长或稍短:雄蕊着生于花冠筒的喉部,与花冠裂片互生:;花药内藏:花柱丝状,柱头棒状2裂。萌果球形,直径约2mm,被长柔毛,具数条纵棱及宿存的萼裂片,成熟时不开裂。', '多生长于山坡、谷地、路边草丛中及田边', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'jinmaoercaoOther.png', 'jinmaoercao;jmec;pudiwugong;pdwg;tadiwugong;tdwg;chentougong;ctg;chuandiwugong;cdwg;dadiwugong;ddwg', '2018-07-15 21:42:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('63', '白花蛇舌草', '蛇舌草;二叶葎', '阴药', '1年生纤细草本。茎多分枝扁圆柱形,小枝具纵棱。叶片膜质,老时草质,线形,长1-4cm,宽1~3mm,先端急尖至渐尖,基部长楔形,上面无毛,下面有时粗糙,中脉在上面凹陷或略平,下面隆起,无侧脉;无柄:托叶长1-2mm,基部合生,顶端齿裂。花单生或成对生于叶腋花梗长2-10mm,较粗壮;萼筒球形,长约1mm,萼檐4裂,裂片长圆状披针形；花冠白色,管状,长约3.5mm,顶端4裂,裂片卵状长圆形,长约2mm,先端钝:雄蕊着生于花冠筒的喉部,花药突出;花柱顶端2裂蒴果扁球形,直径2~3mm,具宿存花萼裂片,成熟时室背开裂。', '多生长于山坡溪沟边草丛中及田边', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'baihuapaotongOther.png', 'baihuasheshecao;bhssc;sheshecao;ssc;eryelv;eyl', '2018-07-16 21:02:30', null, null, null, '1');
INSERT INTO `medicine` VALUES ('64', '宽卵叶山蚂蝗', '假豆;山角豆', '阴药', '小灌木或半灌木。三出复叶，叶通常全部聚生或近聚生茎顶，顶生小叶片宽卵形，先端渐尖或尾尖，基部稍倾斜，两面被短柔毛。圆锥花序顶生，花在花序上排列较疏松，花冠紫红色。荚果通常具2荚节，两面被短钩状毛，腹缝线近平直，背缝线在种子间缢缩至近腹缝线。种子肾圆形。', '多生长于山谷疏林下或林缘灌木丛中', '夏秋季采收，洗净，鲜用或干燥', '207', '暂无', '', '', null, null, '7', null, null, null, null, 'kuanluanyeshanmahuangOther.png', 'kuanluanyeshanmahuang;klysmh;jiadou;jd;shanjiaodou;sjd', '2018-07-16 15:53:05', null, null, null, '1');
INSERT INTO `medicine` VALUES ('69', '鸡眼草', '塌地隔猛草;隔猛草;锄头草', '阳药', '1年生草本,掌状3小叶,小叶片卵状椭圆形或长椭圆形,有时倒卵形,先端圆钝有小尖头,基部形,两面沿中脉及叶缘被长柔毛,侧脉密而平行,花1-3朵腋生,花冠淡红色,有B时退化成无花瓣。英果成熟时茶褐色,宽卵形,扁平。种子黑色,卵形', '多生长于路边、草地、田边及杂草丛中', '夏,秋季采收,洗净,鲜用或干燥', '402', '暂无', '', '', null, null, '7', null, null, null, null, 'jiyancaoOther.png', 'jiyancao;jyc;tadigemengcao;tdgmc;gemengcao;gmc;chutoucao;ctc', '2018-07-16 15:51:45', null, null, null, '1');
INSERT INTO `medicine` VALUES ('72', '截叶铁扫帚', '白隔猛草;关门草;合草;夜关门', '阳药', '半灌木。枝条有棱,有短柔毛。羽状3小叶,小叶片线状楔形,先端截或圆钝,微凹,具小尖头,基部楔形,上面几无毛,下面密被伏毛,侧生小叶片较小。总状花序腋生,短于复叶,有2-4朵花稀单生,花冠白色或淡黄色。荚果宽卵形或斜卵形,被柔毛。种子赭褐色,肾圆形,光滑无毛', '多生长于路边、草地、林隙或山坡', '夏、秋季采收,洗净,鲜用或干燥。', '7', '暂无', '', '', null, null, '7', null, null, null, null, 'jieyetiesaozhouOther.png', 'jieyetiesaozhou;jytsz;baigemengcao;bgmc;guanmencao;gmc;hecao;hc;yeguanmen;ygm', '2018-07-16 15:52:26', null, null, null, '1');
INSERT INTO `medicine` VALUES ('73', '大叶胡枝子', '大叶马殿西', '阳药', '落叶灌木。小枝具明显的条棱,密被柔毛,老枝具木栓翅。羽状3小叶小叶片宽椭圆形、宽倒卵形或近圆形,先端微钝或微凹,基部圆形或宽楔形,两面密被短柔毛,下面尤密。总状花序腋生,在枝顶呈圆锥花序,花密集,花冠紫红色。荚果斜卵形、倒卵形或椭圆形,顶端具短尖,密被绢毛。种子成熟时豆青色,干后暗色,扁平光滑无毛。', '多生长于向阳山坡、沟边灌木丛中或疏林下', '深秋季采收,洗净,鲜用或干燥', '50', '暂无', '', '', null, null, '7', null, null, null, null, 'dayehuzhiziOther.png', 'dayehuzhizi;dyhzz;dayemadianxi;dymdx', '2018-07-16 15:53:57', null, null, null, '1');
INSERT INTO `medicine` VALUES ('74', '美丽胡枝子', '乌梢根;马殿西', '阳药', '落叶灌木。枝稍具棱。羽状3小叶,顶生小叶片厚纸质或薄革质,卵形、倒卵形或近圆形,稀椭圆形,先端圆钝、微凹缺,稀钝头,具小尖头,下面贴生短柔毛侧生小叶较小。总状花序腋生,花萼钟状,萼齿卵形或椭圆形,长于或近等长于尊筒,花冠紫红色。荚果斜卵形或长圆形,顶端具小尖头,贴生柔毛。', '多生长于向阳山坡,山谷,路边灌木丛或林缘', '深秋季采收,洗净,鲜用', '50', '暂无', '', '', null, null, '7', null, null, null, null, '美丽胡枝子.png', 'meilihuzhizi;mlhzz;wushaogen;wsg;madianxi;mdx', '2018-07-16 15:54:17', null, '错的', null, '0');
INSERT INTO `medicine` VALUES ('76', '菰', '茭白;茭笋', '阴药', '具根茎，须根粗壮。秆基部节上具不定根。叶鞘肥厚，长于节间，基部者常具横脉纹；叶舌膜质，略三角形，长 6 一 15mm，叶片扁平，长 30 一 100cm ，上面粗糙，下面光滑。圆锥花序 30 一 60cm ，分枝多数簇生，雄性小穗着生在下部的分枝上，常呈紫色，长 10 一15mm,外稃具 5 脉，先端渐尖或具短芒；内稃具 3 脉；雄蕊 6 ，花药长 5 一 9mm ；雌性小穗着生上部的分枝上，长 15 一 25mm ; 外稃具 5 条粗糙的脉，芒长 1 . 5 一 3cm ；内稃具 3 脉。颖果圆柱形，长约 10mm 。', '多年生水生草本。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'gu;g;jiaobai;jb;jiaosun;js', '2018-07-16 13:53:28', null, null, null, '1');
INSERT INTO `medicine` VALUES ('77', '白花美丽胡枝子', '白花美丽胡枝子', '阳药', '与美丽胡枝子相似,主要区别为花冠白色', '多生长于低海拔山坡、路旁空旷地或灌木丛中', '深秋季采收,洗净,鲜用或干燥', '289', '暂无', '', '', null, null, '7', null, null, null, null, 'D:\\sheyaoImg\\baihuameilihuzhiziOther.png', 'baihuameilihuzhizi;bhmlhzz;baihuameilihuzhizi;bhmlhzz', '2018-07-16 15:44:28', null, null, null, '1');
INSERT INTO `medicine` VALUES ('78', '碎米莎草', '三角黄麻;三棱草;掰儿草;三方草', '阴药', ' 1 年生草本，具多数须根。秆丛生，扁三棱形，下部具多数叶，无毛。叶短于秆；叶片线形，扁平，宽 2 ~ 5mm ; 叶鞘红棕色或紫棕色。苞片 3 ，叶状，长于花序；聚伞花序复出，少为简单；穗状花序卵形或长圆状卵形，长 1 . 5 ~4cm ，具 5 至多数小穗；小穗排列松散，斜展开，长圆形、披针形或线状披针形，压扁，长 5 ~ 10mm , 具 6 ~ 20 朵花，小穗轴近无翅；鳞片宽倒卵形，长约 1.5mm ，先端凹或钝圆，具不显著的短尖，尖头不突出于鳞片的顶端，背面龙骨状，绿色，具 3 一 5 脉，两侧呈黄色或麦秸色；雄蕊 3 ，花药短，椭圆形；花柱短，柱头 3 个。小坚果倒卵形或椭圆形，三棱状，与鳞片等长，褐色，具密的微突细点。', '多生长于田边、路边、溪边或林缘潮湿处。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'suimisuocao;smsc;sanjiaohuangma;sjhm;sanlengcao;slc;baiercao;bec;sanfangcao;sfc', '2018-07-16 14:03:41', null, '？', null, '0');
