INSERT INTO `medicine` VALUES ('87', '美丽胡枝子', '乌梢根;马殿西', '阳药', '落叶灌木。枝稍具棱。羽状3小叶,顶生小叶片厚纸质或薄革质,卵形、倒卵形或近圆形,稀椭圆形,先端圆钝、微凹缺,稀钝头,具小尖头,下面贴生短柔毛侧生小叶较小。总状花序腋生,花萼钟状,萼齿卵形或椭圆形,长于或近等长于尊筒,花冠紫红色。荚果斜卵形或长圆形,顶端具小尖头,贴生柔毛。', '多生长于向阳山坡,山谷,路边灌木丛或林缘', '深秋季采收,洗净,鲜用', '50', '暂无', '', '', null, null, '7', null, null, null, null, 'meilihuzhiziOther.png', 'meilihuzhizi;mlhzz;wushaogen;wsg;madianxi;mdx', '2018-07-16 15:54:17', null, '错的', null, '0');
INSERT INTO `medicine` VALUES ('88', '碎米莎草', '三角黄麻;掰儿草;三方草;三棱草', '阴药', ' 1 年生草本，具多数须根。秆丛生，扁三棱形，下部具多数叶，无毛。叶短于秆；叶片线形，扁平，宽 2~5mm ; 叶鞘红棕色或紫棕色。苞片 3 ，叶状，长于花序；聚伞花序复出，少为简单；穗状花序卵形或长圆状卵形，长 1.5~4cm ，具 5 至多数小穗；小穗排列松散，斜展开，长圆形、披针形或线状披针形，压扁，长 5~10mm , 具 6 ~ 20 朵花，小穗轴近无翅；鳞片宽倒卵形，长约 1 . 5mm ，先端凹或钝圆，具不显著的短尖，尖头不突出于鳞片的顶端，背面龙骨状，绿色，具 3~5 脉，两侧呈黄色或麦秸色；雄蕊 3 ，花药短，椭圆形；花柱短，柱头 3 个。小坚果倒卵形或椭圆形，三棱状，与鳞片等长，褐色，具密的微突细点。', '多生长于田边、路边、溪边或林缘潮湿处。', '秋季采收，洗净，鲜用或干燥。 ', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'suimisuocao;smsc;sanjiaohuangma;sjhm;baiercao;bec;sanfangcao;sfc;sanlengcao;slc', '2018-07-16 14:12:32', null, null, null, '1');
INSERT INTO `medicine` VALUES ('89', '铁马鞭', '野花生;细叶马殿西', '阳药', '半灌木。全体密被淡黄色或棕黄色长柔毛。羽状3小叶,顶端叶片宽卵形或倒卵形,先端钝圆。截形或微凹,有短尖头,基部圆形或宽楔形,两面密生长柔毛侧生小叶片明显较小。总状花序腋生,总花梗或花梗极短或几无梗,呈簇生状,花冠黄白色或白色。英果宽卵形,凸镜状,顶端具喙,两面密生长柔毛。种子灰绿色,椭圆形,光滑无毛。', '多生长于向阳山坡、路边、田边灌木丛中或疏林下', '夏、秋季采收,洗净,鲜用或干燥', '97', '暂无', '', '', null, null, '7', null, null, null, null, 'tiemabianOther.png', 'tiemabian;tmb;yehuasheng;yhs;xiyemadianxi;xymdx', '2018-07-16 14:12:38', null, null, null, '1');
INSERT INTO `medicine` VALUES ('98', '香附子', '地口姜', '阳药', '多年生草本。根状茎长，匍匐，具圆锥形块茎。秆细瘦，锐三棱形，平滑，下部具多数叶。叶片短于秆，扁平，宽 2~5mm ；叶鞘棕色，常裂成纤维状。苞片 2 ~ 4 ，叶状，通常长于花序；聚伞花序简单或复出，具 3 ~ 8 不等长辐射枝；穗状花序有 3 ~ 10 小穗；小穗斜展开，线状披针形，长 l ~ 3cm ，宽 1 . 5 ~ 2mm ，压扁，具花 10 ~ 36 朵，小穗轴有白色透明较宽的翅；鳞片密，覆瓦状排列，膜质，卵形或长圆状卵形，长 2 ~ 3mm ，先端钝，中间绿色，两侧紫红色或红棕色，具 5 ~ 7 脉；雄蕊 3 , 花药线形，暗血红色；花柱细长，柱头 3 , 伸出鳞片外。小坚果长圆状倒卵形，三棱形，长约 1mm。', '多生长于山坡荒地、路边草丛或溪沟边潮湿处。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'xiangfuzi;xfz;dikoujiang;dkj', '2018-07-16 21:01:06', null, null, null, '1');
INSERT INTO `medicine` VALUES ('99', '香花崖豆藤', '红血藤;红茱莉水绳;血藤;马立须;狗毯筋叶;鸡血藤', '阴药', '常绿木质藤本。根茎及根粗壮,折断时均有红色液汁。羽状复叶,小叶5枚,小叶片椭圆形、长圆形、披针形或卵形,先端渐尖至圆钝,边缘向下反卷。圆锥花序顶生,密被黄褐色绒毛,分枝细弱,常下垂,花松散着生,花冠紫红色,旗瓣基部无胼胝体状附属物。荚果近木质,线形,密被灰色绒毛,有种子3~5粒。种子紫棕色,长圆形。', '多生长于山坡、山谷、沟边林缘或灌木丛中。', '夏、秋季采收,洗净,鲜用或干', '55', '暂无', '', '', null, null, '7', null, null, null, null, 'xianghuayadoutengOther.png', 'xianghuayadouteng;xhydt;hongxieteng;hxt;hongzhulishuisheng;hzlss;xieteng;xt;malixu;mlx;goutanjinye;gtjy;jixieteng;jxt', '2018-07-16 14:30:15', null, null, null, '1');
INSERT INTO `medicine` VALUES ('100', '网络崖豆藤', '白血藤;白嘎狗乱筋', '阳药', '半常绿或落叶木质藤本。羽状复叶,有小叶5~9枚,小叶片革质,卵状椭圆形、长椭圆形或卵形,先端尾尖,钝微凹,基部圆形,两面无毛。圆锥花序顶生,下垂。总花梗被黄色疏柔毛,花冠紫红色或玫瑰红色。荚果紫褐色,线状长圆形至倒披针状长圆形。种子间略缢缩,顶端具喙,果瓣木质,有3-10粒种子。种子褐色,具花纹,扁圆形', '多生长于山地,沟谷灌木从或疏林下,有栽培', '夏、秋季采收,洗净,用或干燥', '53', '暂无', '', '', null, null, '7', null, null, null, null, 'wangluoyadoutengOther.png', 'wangluoyadouteng;wlydt;baixieteng;bxt;baigagouluanjin;bgglj', '2018-07-16 14:33:36', null, null, null, '1');
INSERT INTO `medicine` VALUES ('101', '花榈木', '花榈木', '阳药', '常绿小乔木或乔木。树皮青灰色,光滑。奇数羽状复叶,小叶5~9枚叶轴密被绒毛:无托叶:小叶片革质,长椭圆状卵形、长椭圆形、长圆状倒披针形,先端急尖或短渐尖,基部圆形或宽模形,全缘下面密被灰黄色毡毛状绒毛。圆锥花序顶生或腋生,或总状花序腋生,花冠黄白色。英果木质,长圆形,扁平稍有喙,无毛,有种子2-7粒。种子鲜红色,椭圆形。', '多生长于山谷沟边两旁山坡林中或林缘。', '夏、秋季采收,洗净,鲜用或干燥', '50', '暂无', '', '', null, null, '7', null, null, null, null, 'hualvmuOther.png', 'hualvmu;hlm;hualvmu;hlm', '2018-07-16 14:36:15', null, null, null, '1');
INSERT INTO `medicine` VALUES ('102', '野葛', '野葛', '阴药', '圆柱形。茎基粗壮,木质化,上部多分枝。羽状3小叶,顶生小叶片菱状卵形,基部圆形,侧生小叶片较小,斜卵形。总状花序腋生,有时具分枝,花冠紫红色。荚果线形,扁平,密被黄色长硬毛。种子赤褐色,扁圆形,有光泽', '多生长于山坡草地、沟边、路边或疏林中', '深秋或冬季采收,洗净,鲜用或干燥', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'yegeOhter.png', 'yege;yg;yege;yg', '2018-07-16 14:39:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('103', '蚕豆', '佛豆', '阴药', '2年生草本。茎常具棱。数羽状复叶有2~6枚小叶,小叶片椭圆形、宽椭圆形或倒卵状长圆形,先端圆钝、稀急尖,具小尖头,基部宽楔形,两面无毛;托叶半箭头状,边缘有细齿,基部贴生在叶柄上;叶轴顶端具不发达刺毛状卷须。花1朵至数朵腋生,旗瓣白色,有紫色条纹;翼瓣白色,有黑色斑块。荚果大,肥厚,有种子2~5粒', '普遍有栽培。', '春季采收,洗净,鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'candouOther.png', 'candou;cd;fodou;fd', '2018-07-16 14:43:28', null, null, null, '1');
INSERT INTO `medicine` VALUES ('104', '绿豆', '绿豆', '阳药', '1年生直立草本。茎有时稍伸长成蔓生状。羽状3小叶,顶生小叶片宽卵形或菱状卵形,先端渐尖,基部宽楔形圆形或截形;侧生小叶片基部基部偏斜。总状花序腋生,花冠黄色。荚果成熟时黑色,圆柱形,密被长硬毛。种子绿色,短长圆形,偶有白色突起的种脐。', '普遍有栽培。', '秋季果实成熟时采收,洗净,鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'lvdouOther.png', 'lvdou;ld;lvdou;ld', '2018-07-16 14:45:57', null, null, null, '1');
INSERT INTO `medicine` VALUES ('105', '野豇豆', '山赤豆;鬼绿豆', '阳药', '多年生缠绕本草。主根圆柱形或圆锥形,肉质,外皮橙黄色。茎略具线纹。羽状3小叶,顶生小叶片变化大,宽卵形.菱状卵形至披针形,先端急尖至渐尖,基部圆形至截形,两面被淡黄色糙毛。花2-4朵生于花序上部,花冠紫红色至紫褐色,龙骨瓣先端曚状。荚果圆柱形,被粗毛,顶端具曚。种子黑色,长圆形或近方形,有光泽', '多生长于山坡林缘或草丛', '深秋或初冬季节采收,洗净,鲜用或干燥', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'yejiangdouOther.png', 'yejiangdou;yjd;shanchidou;scd;guilvdou;gld', '2018-07-16 14:49:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('106', '紫藤', '黄景花;黄锦花;南绳', '阳药', '落叶木质藤本。奇数羽状复叶,有小叶7-13枚,小叶片卵状披针形或卵状长圆形,先端渐尖或尾尖,基部圆形或宽楔形。总状花序生于去年生枝顶端,下垂,花密集,花冠紫色或深紫色。英果线形或线状倒披针形,扁平,密被灰黄色绒毛。种子灰褐色,扁圆形,种皮有花纹。', '多生长于向阳山坡、沟谷旷地、灌木丛中或疏林下', '深秋或初冬季节采收,洗净,鲜用或干燥', '64', '暂无', '', '', null, null, '7', null, null, null, null, 'D:\\sheyaoImg\\zitengOther.png', 'ziteng;zt;huangjinghua;hjh;huangjinhua;hjh;nansheng;ns', '2018-07-16 14:53:19', null, null, null, '1');
INSERT INTO `medicine` VALUES ('107', '苦参', '苦骨;苦葛根', '阳药', '多年生草本或半灌木。根圆柱形,外皮黄白色,有刺激性气味。茎有不规则纵沟。奇数羽状复叶,有小叶11-35枚小叶片披针形或线状披针形,稀椭圆形,先端渐尖,基部楔形,叶缘下向反卷,下面密生平贴柔毛;托叶线形,早落。总状花序顶生,具多数花,花冠黄白色。荚果革质,线形,种子间徵缢缩,呈不明显串珠状,顶端具1~1.5 cm的喙,疏生短柔毛。种子棕黑色卵圆形。', '多生长于向阳山坡草地、路边、溪沟边。有栽培。', '深秋或初冬季节采收,洗净,鲜用或干燥。', '291', '暂无', '', '', null, null, '7', null, null, null, null, 'kushenOther.png', 'kucan;kc;kugu;kg;kugegen;kgg', '2018-07-16 14:58:53', null, null, null, '1');
INSERT INTO `medicine` VALUES ('108', '酢浆草', '酸草;老鸦饭;酸酸草', '阳药', '多年生草本,多分枝,被疏生柔毛。无鳞茎,茎柔弱,常平卧,有时节上生不定根。掌状三出复叶互生,小叶片倒心形,被疏柔毛;:托叶小与叶柄合生。伞形 聚伞花序腋生,花1至数朵,花瓣黄色,倒 卵形。蒴果近圆柱形,有3条纵沟,被柔毛。种子黑褐色,有皱纹。', '多生长于房前屋后、路边田野等', '夏、秋季采收,洗净,鲜用或干燥。', '1_350', '暂无', '', '', null, null, '7', null, null, null, null, 'zuojiangcaoOther.png', 'zuojiangcao;zjc;suancao;sc;laoyafan;lyf;suansuancao;ssc', '2018-07-16 15:02:14', null, null, null, '1');
INSERT INTO `medicine` VALUES ('109', '直立酢浆草', '盐酸草;老鸦饭', '阳药', '多年生草本,茎单一或少分枝,全株伏生白色。根茎节处疏生鳞片。状三出复叶互生,小叶片倒宽心形,先端凹陷,基部楔形,边缘有睫毛:无托叶。伞形花序腋生,花1~6朵,花瓣黄色,长圆状倒卵形。蒴果近圆柱形,略具5棱,顶端尖, 外面疏生伏毛。种子成熟时褐色或棕红扁平,椭圆形,具条纹。', '多生长于山沟、路旁及山区耕地附近多石砾处', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'zhilizuojiangcaoOther.png', 'zhilizuojiangcao;zlzjc;yansuancao;ysc;laoyafan;lyf', '2018-07-16 15:06:22', null, null, null, '1');
INSERT INTO `medicine` VALUES ('110', '野老鹳草', '五叶草;水积草', '阳药', '1年生草本。茎基部分枝叶在茎下部的互生,上部的对生,叶片圆肾形,掌状5-7深裂,裂再3-5浅裂至中小裂片线形,全缘,先端锐尖,两面有短伏毛,基生叶有长柄:茎生叶柄短。花成对生于茎顶或上部叶腋,花瓣淡红色,5枚。萌果顶端有长喙。种子棕褐色,椭圆形,外面有细网纹', '多生长于荒野、路旁,田或沟边', '夏,秋季采收,洗净,鲜用或干燥', '82', '暂无', '', '', null, null, '7', null, null, null, null, 'yelaoguancao2Other.png', 'yelaoguancao;ylgc;wuyecao;wyc;shuijicao;sjc', '2018-07-16 15:10:25', null, null, null, '1');
INSERT INTO `medicine` VALUES ('111', '枸橘', '野黄柏', '阳药', '枝绿色,扁平,有棱角,密生粗壮棘刺,棘刺基部扁平。羽状三出复叶互生,叶柄有翅:小叶片近革质,卵形、椭圆形或倒卵形,先端圆钝,微凹头,基部楔形,边缘有圆齿或近全缘。花常先叶开放,单生或成对腋生,花瓣黄白色或略带淡紫色。柑果成熟时橙黄色,球形,具绒毛', '栽培', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'goujuOther.png', 'gouju;gj;yehuangbai;yhb', '2018-07-16 15:12:52', null, null, null, '1');
INSERT INTO `medicine` VALUES ('112', '樗叶花椒', '鼓丁柴', '阳药', '落叶乔木;树干上有圆锥形凸起鼓钉状大皮刺。奇数羽状复叶互生,长25~60 cm,最长可达1 m,有小叶9~27枚,小叶对生,小叶片纸质,狭长圆形、椭圆形或椭圆状长圆形,先端长渐尖或尾尖,钝头或微凹,基部圆形,稍不对称,边缘具浅钝锯齿,齿缝处有半透明油点,两面散生多或少量油点,下面灰白色,粉霜状,无毛。伞房状圆锥花序,顶生,花多数,花瓣淡青色或白色。瞢果成熟时红色。种子黑色,有光泽', '多生长于密林中阴湿处。', '夏、秋季采收,洗净,鲜用或干燥。', '383', '暂无', '', '', null, null, '7', null, null, null, null, 'chuyehuajiaoOther.png', 'chuyehuajiao;cyhj;gudingchai;gdc', '2018-07-16 15:17:26', null, null, null, '1');
INSERT INTO `medicine` VALUES ('113', '竹叶椒', '白夫桃', '阴药', '常绿灌木或小乔木。枝散生劲直扁皮刺,老枝皮刺顶部木栓化。奇数羽状复叶,有小叶3~9枚,通常为3-5枚;叶轴或叶柄有宽翅,叶柄基部有1对于托叶状皮刺:小叶片薄革质,形状变异很大,通常披针形,有时卵形、椭圆形或线状披针先端急尖至渐尖,基部楔形至宽楔形,边有细小圆齿,齿缝有1粗大油点。聚伞状锥花序,腋生或生于侧枝顶端,花单性,细小,黄绿色。膏荧果成熟时红色,外面有凸起腺点。种子黑色,卵', '多生长于山坡疏林下或灌木丛中。', '夏、秋季采收,洗净,鲜用或干燥。', '266', '暂无', '', '', null, null, '7', null, null, null, null, 'zhuyejiaoOther.png', 'zhuyejiao;zyj;baifutao;bft', '2018-07-16 15:21:03', null, null, null, '1');
INSERT INTO `medicine` VALUES ('114', '臭椿', '包罗香;苦马霜', '阳药', '落叶乔木。树皮平滑,有直的浅裂纹。奇数羽状复叶互生,有小叶13-25枚,小叶对生,小叶片革质,搓揉后有臭味,卵状披针形至披针形,先端渐尖基部近圆形,偏斜,近基部边缘有1-2对大锯齿,齿端下面有1大腺体。圆锥花序顶生,花小,雄花开放时有臭味,花瓣白色带绿翅果成熟时黄褐色,长椭圆形,有1粒种子种子位于翅果近中部', '多生长于阳坡疏林中,林缘、灌木丛中。有栽培', '深秋季采收,洗净,鲜用或干燥', '7', '暂无', '', '', null, null, '7', null, null, null, null, 'chouchunOther.png', 'chouchun;cc;baoluoxiang;blx;kumashuang;kms', '2018-07-16 15:23:42', null, null, null, '1');
INSERT INTO `medicine` VALUES ('115', '楝树', '苦楝;苦楝树', '阳药', '落叶乔木。树皮纵裂,小枝有叶痕,具白色皮孔。叶二至三回羽状复叶互生,小叶片卵形,椭圆状卵形或卵状披针形至披针形,先端渐尖至长渐尖,基部楔形至圆形,边缘具粗钝锯齿。圆锥花序腋生,花芳香,花瓣紫色。核果成熟时淡黄色,近球形或卵形。', '多生长于山脚、溪边、村庄旁。有栽培', '夏、秋季采收,洗净,鲜用或干燥。', '387', '暂无', '', '', null, null, '7', null, null, null, null, 'lianshuOther.png', 'lianshu;ls;kulian;kl;kulianshu;kls', '2018-07-16 15:27:38', null, null, null, '1');
INSERT INTO `medicine` VALUES ('116', '瓜子金', '瓜子草;金钥匙;土远志', '阳药', '多年生草本。根木质。茎丛生。叶片果期近革质或厚纸质,卵形、长圆形或卵状披针形至披针形,先端急尖,有时具小尖头,基部圆钝或楔形,边缘反卷,两 面中脉上被短柔毛,叶脉两面均明显隆起。总状花序与叶对生或腋外生,最上1个花序 低于茎;花白色或堇色,花瓣3枚。蒴果近 圆形,庄扁,顶端微凹,边缘具宽翅,无缘毛,有2粒种子。种子黑色,扁卵形,被白绢毛。', '多生长于山坡草地、路旁或耕地附近', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'guazijinOther.png', 'guazijin;gzj;guazicao;gzc;jinyaoshi;jys;tuyuanzhi;tyz', '2018-07-16 15:30:32', null, null, null, '1');
INSERT INTO `medicine` VALUES ('117', '铁苋菜', '山落麻;野落麻', '阴药', '1年生草本。茎直立,自基部分枝,伏生白色硬毛。叶互生,叶片卵形至椭圆状披针形,先端渐尖或钝尖,基部渐狭或宽楔形,上面被疏柔毛或近无毛,下面毛稍密,沿中脉伏生硬毛。穗状花序腋生,雄花簇生于花序上部,萼片卵形,背面被毛;雌花生于花序下部,有叶状肾形苞片。蒴果三角状半圆形,外面被毛。种子黑褐色,卵', '多生长于低山坡、沟边路旁或田野中。', '夏秋季采收,洗净,鲜用或干燥。', '1_338', '暂无', '', '', null, null, '7', null, null, null, null, 'tiexiancaiOther.png', 'tiexiancai;txc;shanluoma;slm;yeluoma;ylm', '2018-07-16 15:39:24', null, null, null, '1');
INSERT INTO `medicine` VALUES ('118', '泽漆', '雷公草;王虎丹', '阳药', '1年或2年生草本。茎基常带紫红色,多分枝。叶互生,叶片倒卵形或匙形,向下逐渐变小,先端圆钝或微凹,基部楔形。边缘中部以上有锯齿,上面无毛,下面疏被长柔毛:无柄:花序基部有5枚较大的轮生叶。多歧聚伞花序顶生,通常有5枚伞梗,每伞梗再分出2-3枚小伞梗:总苞钟形,顶端4浅裂,花柱3,顶端2裂。蒴果球形,无毛。种子暗褐色,卵形,表面有明显凸起的网纹', '多生长于沟边、路旁及荒废农田。', '夏季开花时采收,洗净,鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, 'zeqiOther.png', 'zeqi;zq;leigongcao;lgc;wanghudan;whd', '2018-07-16 15:43:27', null, null, null, '1');
INSERT INTO `medicine` VALUES ('122', '美丽胡枝子', '乌梢根;马殿西', '阳药', '落叶灌木。枝稍具棱。羽状3小叶,顶生小叶片厚纸质或薄革质,卵形、倒卵形或近圆形,稀椭圆形,先端圆钝、微凹缺,稀钝头,具小尖头,下面贴生短柔毛,侧生小叶较小。总状花序腋生,花萼钟状,萼齿卵形或椭圆形,长于或近等长于尊筒,花冠紫红色。荚果斜卵形或长圆形,顶端具小尖头,贴生柔毛。', '多生长于向阳山坡,山谷,路边灌木丛或林缘', '深秋季采收,洗净,鲜用', '50_55', '暂无', '', '', null, null, '7', null, null, null, null, 'meilihuzhiziOther.png', 'meilihuzhizi;mlhzz;wushaogen;wsg;madianxi;mdx', '2018-07-16 15:57:52', null, null, null, '1');
INSERT INTO `medicine` VALUES ('123', '大叶白纸扇', '大叶山田青', '阳药', '落叶直立或攀援状灌木。小枝被黄褐色短柔毛。叶对又生;叶片膜质或薄纸质,宽卵形或宽椭圆形,长8-18cm,宽5~11cm,先端渐尖或短渐尖,基部长楔形,全缘,两面疏被柔毛,沿脉发达,中脉在上面稍隆起,下面明显隆起,侧脉约9对叶柄长1-3.5cm,被短柔毛;托叶卵状披针形,长8-10mm,先端通常2裂。花组成伞房式聚伞花序,疏散,密被柔毛;花具短梗;萼筒陀螺形,长3-4mm,萼檐裂片披针形,长7~10mm,外面密被柔毛,花瓣状萼裂片白色,倒卵形,长3-4cm,宽1.5-2cm;花冠黄色,长14cm,外面密被平伏长柔毛,裂片卵形,长2-3mm,内有金黄色绒毛。果实近球形,直径约lcm,被疏柔毛,顶端具环纹。', '多生长于山坡、溪沟边、路旁及林下灌木丛中。', '夏、秋季采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'dayebaizheshanOther.png', 'dayebaizhishan;dybzs;dayeshantianqing;dystq', '2018-07-16 21:07:05', null, null, null, '1');
INSERT INTO `medicine` VALUES ('124', '蛇根草', '雪里花;四季青', '阳药', '多年生草本。茎圆柱形,褐色,密被锈色曲柔毛,幼枝具棱。叶片膜质或薄纸质,卵形、卵状椭圆形或椭圆形,长2.5-8cm,宽1.3-3cm,先端急尖或略钝,基部楔形、宽楔形至圆形,全缘,上面被稀疏短硬毛,下面红褐色,沿脉被短毛;叶柄长1~2.5cm,密被曲柔毛。聚伞花序顶生,二歧分枝,密被柔毛,有花7~20朵;苞片线形,疏被柔毛;萼筒宽陀螺状球形,外面密被柔毛;花冠白色或淡红色,先端5裂雄蕊5,着生于喉部以下;花盘肉质,环状;子房下位。蒴果倒三角形。', '多生长于山坡谷地、溪沟路边的林下阴湿地或岩石上', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'shegengcaoOther.png', 'shegencao;sgc;xuelihua;xlh;sijiqing;sjq', '2018-07-16 21:10:30', null, null, null, '1');
INSERT INTO `medicine` VALUES ('125', '鸡屎藤', '鸡矢藤;介鸡毛;介鸡藤;对叶肾', '阳药', '多年生半木质藤本。茎灰褐色,幼时被柔毛,后渐脱落变无毛。叶片纸质,卵形、长卵形或卵状披针形,长5-15cm,宽3-8cm,先端急尖至短渐尖,基部心形至圆形,全缘,上面无毛或沿脉被柔毛,或散被粗毛,下面或沿脉被柔毛,脉腋内有簇毛,后渐脱落,侧脉4-6对,连同中脉在两面均隆起;叶柄长1.5~7cm;托叶长3~5mm,初时被缘毛。圆锥状聚伞花序腋生或顶生,扩展,被疏柔毛;萼筒陀螺形,长1~2mm,萼檐5裂,裂片三角形;花冠浅紫色,钟状,长约1cm,外面被灰白色细绒毛,内面被绒毛,顶端5裂。果实球形,成熟时蜡黄色,平滑,具光泽。', '多生长于山坡谷地及溪沟边路旁林下的灌木丛中', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'jishitengOther.png', 'jishiteng;jst;jishiteng;jst;jiejimao;jjm;jiejiteng;jjt;duiyeshen;dys', '2018-07-16 21:14:04', null, null, null, '1');
INSERT INTO `medicine` VALUES ('126', '茜草', '染卵草;染黄草;鸡卵草;擦草', '阴药', '多年生攀援草本。根数条至数十条簇生,外皮紫红色或橙红色。茎具棱,棱上有倒生小刺。叶四片轮生,主茎上有时有六片轮生,具长柄,叶片的形状变化很大,卵形,三角状卵形,卵状心形,卵状披针形至近线状披针形,先端通常急尖,基部心形,上面粗糙,下面沿中脉及叶柄均有倒刺,全缘,基出脉5,稀有3脉或7脉,在上面凹下面凸起。聚伞花序圆锥状,腋生及顶生花小,黄绿色,5数;花萼不明显;花冠辐状,直径约4mm,5裂,裂片卵状三角形,先端急尖,雄蕊5,着生于花冠筒喉部;子房2室,下位,花柱上部2裂。果球形,直径4~6mm,红色后转黑色。', '多生长于山坡路边,溪沟湿润之林下灌木丛中或林缘', '深秋或初冬采收,洗净,鲜用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'qiancaoOther.png', 'qiancao;qc;ranluancao;rlc;ranhuangcao;rhc;jiluancao;jlc;cacao;cc', '2018-07-16 21:17:39', null, null, null, '1');
INSERT INTO `medicine` VALUES ('127', '六月雪', '千年勿大树;白马骨;鸡骨', '阳药', '小灌木。小枝灰白色。叶片坚纸质,狭椭圆形或狭椭圆状倒卵形,长6~15mm,宽2~6mm,先端急尖,有小尖头,基部长楔形,全缘,具缘毛,后渐脱落,干后反卷,上面沿中脉被短柔毛,下面沿脉疏被柔毛,后渐脱落,叶脉在两面均凸起叶柄极短:托叶基部宽,先端分裂成刺毛状花单生或数朵簇生,腋生或顶生,无梗;萼檐4-6裂,裂片三角形,长约lmm,花冠白色而带红紫色,长1-1.5cm,顶端4~6裂。核果近球形,干燥。', '多生长于山坡谷地及溪边路旁林下或岩石上', '秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'liuyuexueOther.png', 'liuyuexue;lyx;qiannianwudashu;qnwds;baimagu;bmg;jigu;jg', '2018-07-16 21:21:28', null, null, null, '1');
INSERT INTO `medicine` VALUES ('128', '白马骨', '六月雪;白马骨;鸡骨柴', '阳药', '小灌木。小枝灰白色。叶片纸质或坚纸质,通常卵形或长圆状卵形,长1~3cm,宽5-12mm,先端急尖,具短尖头基部楔形至长楔形,全缘,干后稍反卷,有时略具缘毛,上面沿中脉被短柔毛,下面沿脉疏被柔毛,叶脉在两面均凸起;叶柄极短托叶膜质,基部宽,先端分裂成刺毛状。花数朵簇生,无梗;:萼沿4-6裂,裂片钻状披针形,长3~4mm,边缘有缘毛;花冠白色,漏斗状,长约5mm,顶端4-6裂。核果球形,干燥。', '多生长于山坡路旁、溪沟边林下灌木丛中或石隙中', '秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'baimaguOther.png', 'baimagu;bmg;liuyuexue;lyx;baimagu;bmg;jiguchai;jgc', '2018-07-16 21:25:35', null, null, null, '1');
INSERT INTO `medicine` VALUES ('129', '钩藤', '搭钩藤;金钩吊;双钩', '阳药', '常绿攀缘灌木。小枝四棱状柱形,光滑无毛。叶片纸质或厚纸质,椭圆形宽椭圆形或宽卵形,长6-12cm,宽3~6cm,先端渐尖,基部圆形或宽楔形,全缘,干后上面暗红褐色,无毛或被极稀粗短毛,下面粉红褐色或锈红色,无毛或沿中脉有疏柔毛脉腋内有簇毛;叶柄长5-15mm;托叶2深裂,裂片线形,早落。头状花序单个腋生或数个组成顶生的总状花序:不育花序的总花梗在叶腋上方弯转成钩状刺,发育花序的总花梗长2~4cm,上部1/3处着生数枚苞片,脱落后具痕迹;萼筒长约2mm,密被柔毛花冠黄色,长6~8mm,裂片舌形,边缘具柔毛。蒴果倒圆锥形,被疏柔毛。', '多生长于山谷坡地及溪沟边路旁林下灌木丛中', '秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'goutengOther.png', 'gouteng;gt;dagouteng;dgt;jingoudiao;jgd;shuanggou;sg', '2018-07-16 21:29:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('130', '忍冬', '双色花;变色花;金银花', '阳药', '半常绿木质藤本。茎皮条状剥离,多分枝,茎中空,幼枝暗红褐色,密被黄褐色开展糙毛及腺毛,下部常无毛。叶片纸质,卵形至长圆状卵形,稀倒卵形,长2-8cm,宽1-5.5cm,先端短尖至渐尖,基部圆形或心形,边缘具毛,小枝上部叶两面均密被短柔毛,下部叶常无毛而下面带灰绿色,入冬略带红色;叶柄长4~8m,被毛。花双生,总花梗常单个生于小枝上部叶腋,密被短柔毛和腺毛;:苞片叶状,长1.5~3cm,两面均被毛或稀无毛;小苞片小,长约1mm,缘毛明显;萼筒长约2mm,无毛,萼齿外面密被毛,齿端被长毛;花冠白色,后变黄色,唇形,外面被倒生糙毛和腺毛雄蕊5,与花柱均长于花冠。果离生,圆球形,成熟时蓝黑色。', '多生长于山坡灌丛边缘山坡岩石上山麓等', '4~6月份采收花;秋季采收茎,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'rendongOther.png', 'rendong;rd;shuangsehua;ssh;biansehua;bsh;jinyinhua;jyh', '2018-07-16 21:33:53', null, null, null, '1');
INSERT INTO `medicine` VALUES ('131', '接骨草', '燥棒;帅棒;青淳伞;陆英', '阴药', '多年生草本或半灌木。茎圆柱形,具紫色棱条,髓部白色。奇数羽状复叶,有小叶3~9,侧生小叶片披针形、椭圆状披针形,长5~17cm,宽2.5-6cm,先端渐尖,基部偏斜或宽楔形,边缘具细密的锐锯齿,上面深绿色,散生糠屑状细毛,下面绿色,光泽,中脉和侧脉在下面明显隆起小叶柄短或近无柄;叶片搓揉后有臭味;托叶早落。复伞形花序大而疏散,顶生;总花梗基部托有叶状总苞片,第1级辐射枝3~5出,不育性花变成黄色杯状腺体,不脱落发育性花小,白色或略带黄色,辐射状;萼筒杯状,长约1.5mm,萼齿三角形:花冠5深裂,裂片宽椭圆状卵形;雄蕊5,着生于花冠喉部,不伸出花冠外;子房3室,花柱短,柱头3浅裂。果实近球形,成熟时橙黄色至红色;果核2~3,卵形,表面有瘤状突起。', '多生长于山坡、林缘、村庄农舍附近及山谷溪沟边', '夏、秋季采收,洗净,鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'jiegucaoOther.png', 'jiegucao;jgc;zaobang;zb;shuaibang;sb;qingchunsan;qcs;luying;ly', '2018-07-16 21:39:10', null, null, null, '1');
INSERT INTO `medicine` VALUES ('132', '野艾蒿', '茶水蓬,青蓬。', '阳药', '多年生草本。高30-90cm,\r\n具纵肋,多分枝,被密短毛。叶大型,具长\r\n柄及假托叶;基部叶在花期枯萎;中部叶片\r\n长椭圆形,长5-8cm,宽3.5~5cm,2\r\n回羽状深裂,裂片1~3对,线状披针形,长\r\n3~6cm,宽约0.7cm,先端渐尖,基部下延,\r\n边缘反卷,上面被短柔毛及白色腺点,下面\r\n密被灰白色绵毛,中脉凸起,无毛;上部叶\r\n片小,披针形,全缘。头状花序多数,具短\r\n梗及线形苞叶,下垂,着生于茎枝顶端,排\r\n列呈圆锥状;总苞长圆形,直径约3mm,被\r\n蛛丝状毛,总苞4层,外层较短,卵圆形,\r\n内层椭圆形;花管状,红褐色,均结实;缘\r\n花雌性,6~7朵;盘花两性8-10朵。瘦\r\n果椭圆形,无毛。', '多生长于山坡、路旁及草地', '夏季花未开放时,割取地上部分,除去杂质鲜用或阴干或置阴凉通风\r\n处干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'yeaihaoOther.png', 'yeaihao;yah;chashuipeng,qingpeng。;csp,qp。', '2018-07-17 08:17:09', null, null, null, '1');
INSERT INTO `medicine` VALUES ('133', '三脉紫菀', '苦连板，山白菊', '阳药', '多年生草本。根状茎粗壮。\r\n下部叶在花期枯萎;中部叶片长圆状披针形\r\n或狭披针形,长6~16cm,宽1~5cm,\r\n先端急尖,中部以下急狭成楔形具宽翅的柄,\r\n边缘有3-7对于粗齿;上部口叶片较小,有浅\r\n齿或全缘:;全部叶片上面密被糙毛,下面被\r\n疏短柔毛或除沿脉外无毛,稍有腺点,通常\r\n离基三出脉,网脉常明显。头状花序排列呈\r\n伞房状或圆锥状;总苞倒锥状半球形;总苞\r\n片3层,线状长圆形,上部绿色或紫褐色;\r\n缘花舌状,紫色,浅红色;盘花管状,黄色。\r\n瘦果倒卵状长圆形,灰褐色,有边肋,被短\r\n粗毛;冠毛浅红褐色或污白色。', '多生长于山坡路旁、林缘、 荒山', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '296', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'sanmaiziwanOther.png', 'sanmaiziwan;smzw;kulianban，shanbaiju;klb，sbj', '2018-07-17 08:26:02', null, null, null, '1');
INSERT INTO `medicine` VALUES ('134', '陀螺紫菀', '草鞋芎草,毛舌,老虎舌, 大猫舌,牛舌草。', '阴药', '多年生草本。茎单生,被\r\n糙毛。下部叶在花期枯萎;中部叶片无柄\r\n长圆形或椭圆状披针形,长3~15cm,宽\r\n1~3cm,先端尖或渐尖,边缘有浅齿,基\r\n部有抱茎的圆形小耳;上部叶片渐小卵圆形\r\n或披针形;全部叶片厚纸质,两面被短糙毛,\r\n下面沿脉有长糙毛,有离基三出脉及2~3\r\n对侧脉,中脉在下面突起。头状花序单生或\r\n2-3个簇生上部叶腋:总苞倒锥形,总苞\r\n片多层,覆瓦状排列,外层卵圆形,先端圆\r\n形或急尖,内层长圆状线形,先端圆形:缘\r\n花舌状,蓝紫色;盘花管状。瘦果倒卵状长\r\n圆形,两面有肋,密被粗毛:冠毛白色,有\r\n近等长的微糙毛。', '多生长于山坡、林下阴地。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '284', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'tuoluoziwanOther.png', 'tuoluoziwan;tlzw;caoxiexiongcao,maoshe,laohushe, damaoshe,niushecao。;cxxc,ms,lhs, dms,nsc。', '2018-07-17 08:29:48', null, null, null, '1');
INSERT INTO `medicine` VALUES ('135', '鬼针草', '一包针,介狗针', '阳药', '1年生草本。茎钝四棱形,\r\n上部被极稀疏的柔毛。茎下部叶较小,3裂\r\n或不分裂,通常在开花前枯萎:中部叶3裂\r\n先端急尖,基部近圆形或宽楔形,有时偏斜,\r\n不对称,边缘具锯齿,具短柄,顶生裂片较\r\n大,长椭圆形或卵状长圆形,长3.5-7cm,\r\n先端渐尖,基部渐狭或近圆形,边缘有锯齿;\r\n上部叶片小,3裂或不分裂,线状披针形\r\n头状花序花梗长1~6cm;总苞基部被短柔\r\n毛,总苞片7~8,线状匙形,上部稍宽,\r\n草质;外层苞片披针形,干膜质,内层线状\r\n披针形;缘花舌状,白色或黄色,1-4朵,\r\n或无舌状花:盘花管状,黄褐色,顶端5齿裂\r\n两性,结实。瘦果黑色,线状披针形,具棱,\r\n上部具稀疏瘤状突起及刚毛,顶端3-4条\r\n具倒刺毛', '多生长于路边、村旁及荒 地上。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '45', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'guizhencaoOther.png', 'guizhencao;gzc;yibaozhen,jiegouzhen;ybz,jgz', '2018-07-17 08:35:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('136', '长圆叶艾纳香', '庙风,大黄叶,四面风。', '阳药', '多年生草本。主根粗壮,纺\r\n锤形。茎基部分枝,具条棱,被长柔毛。基\r\n生叶花期宿存或凋萎,通常小于中部叶:中\r\n部叶长圆形或椭圆状长圆形,长9~13cm\r\n宽4~6cm,先端急尖或钝,基部楔形,边\r\n缘有不规则的重锯齿,上面被短柔毛,下面\r\n多少被长柔毛,中脉在两侧突起,网脉通常\r\n在下面明显,近无叶柄;上部叶渐小,叶片\r\n长圆状披针形或长圆形,长4~4.5cm,宽\r\n1-1.5cm,先端急尖,基部楔形,边缘具\r\n尖齿或角状疏齿,稀全缘,无柄。头状花序\r\n多数,具梗,排列呈顶生疏圆锥状:总苞球\r\n状钟形,总苞片约4层,绿色,外层的线状\r\n披针形,先端尾状渐尖,外面被密长柔毛,中\r\n内层线形或线状披针形,先端尾尖,外面柔\r\n毛:花序托蜂窝状,被白色粗毛:花全为管\r\n状,黄色;缘花多数,顶端3~4裂齿,雌性;\r\n盘花少数,顶端5裂,裂片三角形,被白色\r\n疏毛和腺体,两性。瘦果圆柱形,被疏白色\r\n粗毛,具条棱:冠毛糙毛状,白色', '多生长于路边、山坡的草 丛或田边', '夏、秋季采收,洗净,鲜\r\n用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'changyuanyeainaxiangOther.png', 'changyuanyeainaxiang;cyyanx;miaofeng,dahuangye,simianfeng。;mf,dhy,smf。', '2018-07-17 08:41:14', null, null, null, '1');
INSERT INTO `medicine` VALUES ('137', '天名精', '张老花,野烟。', '阳药', '多年生草本。茎下部木质,\r\n近于无毛,上部密被短柔毛,有明显的纵\r\n条纹,多分枝,二叉状。基生叶于开花前\r\n调萎:茎下部叶片宽椭圆形或长椭圆形,长\r\n短柔毛,有细小腺点,叶柄密被短柔毛;茎\r\n上部叶片长椭圆形或椭圆状披针形,先端渐\r\n尖或急尖,基部宽楔形,无柄或具短柄。头\r\n状花序多数,近无梗,生于茎端及沿茎、枝\r\n一侧着生于叶腋,着生于枝端者具椭圆形或\r\n披针形苞片叶2-4枚,腋生者无苞叶或有\r\n时有1-2枚甚小的苞叶;总苞钟形或半球\r\n形,总苞片3层,外层的较短,卵圆形,先\r\n端钝或渐尖,膜质或顶端草质,具缘毛,外\r\n面被短柔毛,内层的长圆形,先端圆钝或具\r\n不明显的啮蚀状小齿;花全为管状,黄色;\r\n缘花1至多层,雌花结实;盘花顶端5齿裂\r\n两性,结实。瘦果顶端有短喙。', '多生长于路边荒地、村旁 空旷地、溪边及林缘。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'tianmingjingOther.png', 'tianmingjing;tmj;zhanglaohua,yeyan。;zlh,yy。', '2018-07-17 08:43:14', null, null, null, '1');
INSERT INTO `medicine` VALUES ('138', '石胡荽', '塌地胡椒,地胡椒', '阴药', '多年生小草本。茎多分枝,\r\n匍匐状。叶互生;叶片楔状倒披针形,长\r\n0.7~2cm,宽3-5mm,先端钝,基部楔形,\r\n边缘有不规则的疏齿,无毛或下面尾稍有细\r\n毛,及腺点。头状花序小,直径3~4mm,\r\n扁球形,单生于叶腋,无梗或具极短的梗;\r\n总苞半球形,总苞片2层,外层较大,椭圆\r\n状披针形,绿色,边缘透明膜质;缘花细管状,\r\n多层,顶端2~3处微裂,雌性;盘花管状,\r\n淡紫红色,顶端4处深裂,两性,结实。瘦\r\n果圆柱形,具4棱,棱上有长毛;冠毛鳞片\r\n状或缺。', '多生长于路边及田野阴 湿处。', '秋季开花时于采收,洗净,\r\n鲜用或干燥。', '289', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'shihusuiOther.png', 'shihusui;shs;tadihujiao,dihujiao;tdhj,dhj', '2018-07-17 08:48:49', null, null, null, '1');
INSERT INTO `medicine` VALUES ('139', '蓟', '牛尿刺,大叶牛须刺,牛节 刺,牛须刺,牛口刺。', '阳药', '多年生小草本。块根纺锤状\r\n或萝卜状。茎被稠密或稀疏的多节毛。基生\r\n叶花期存在,叶片卵形、长倒卵状椭圆形或\r\n长椭圆形,长8-22cm,宽2.5-10cm,\r\n羽状深裂或几全裂,裂片5~6对,边缘有\r\n大小不等的锯齿,齿端有针刺,基部下延成\r\n翼柄;中部叶片长圆形,羽状深裂,裂片和\r\n裂齿顶端均有针刺,基部抱茎;上部叶片较\r\n小。头状花序球形,顶生和腋生;总苞钟状,\r\n直径3cm,总苞片多层,覆瓦状排列,向内\r\n层渐长,外层和中层卵状三角形至长三角形,\r\n先端长渐尖,有短刺,内层披针形或线状披\r\n针形,先端渐尖呈软针刺状;花全为管状,\r\n紫色或玫瑰色,顶端不等5浅裂,两性,结\r\n实。瘦果压扁,偏斜楔状倒披针形,有光泽,\r\n具不明显的5棱,顶端斜截形:冠毛多层,\r\n羽毛状,浅褐色,基部连合成环。', '多生长于路边、田边或 荒地。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '133', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'jiOther.png', 'ji;j;niuniaoci,dayeniuxuci,niujie ci,niuxuci,niukouci。;nnc,dynxc,nj c,nxc,nkc。', '2018-07-17 08:52:29', null, null, null, '1');
INSERT INTO `medicine` VALUES ('140', '刺儿菜', '小叶牛须刺,野红花。', '阳药', '多年生草本。幼茎被白色蛛\r\n丝状毛,上部有分枝。基生叶和中部茎生叶\r\n片椭圆形,长椭圆形或椭圆状倒披针形,长\r\n7~10cm,宽1.5~2.5cm,先端钝或圆,\r\n基部楔形,近全缘或有疏锯齿,两面有疏密\r\n不等的白色蛛丝状毛;叶无柄。头状花序直\r\n立,雌雄异株,雌花序较雄花序大;雄花序\r\n总苞长约18mm,雌花序总苞长约25mm\r\n单生茎端或在茎端排列成伞房状:总苞卵形\r\n长卵形或卵圆形,直径1.5-2cm,总苞片\r\n约6层,覆瓦状排列,向内层渐长,外层甚短\r\n长椭圆状披针形,中层以内的披针形,先端\r\n长尖,有刺;:花管状,紫红色或白色,雄花\r\n花冠长1.8cm,雌花花冠长2.4cm。瘦果淡\r\n黄色,椭圆形或长卵形,略扁平;冠毛多层,\r\n羽毛状,污白色', '多生长于山坡、河边或 荒地。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'ciercaiOther.png', 'ciercai;cec;xiaoyeniuxuci,yehonghua。;xynxc,yhh。', '2018-07-17 08:55:44', null, null, null, '1');
INSERT INTO `medicine` VALUES ('141', '野菊', '吉花,艾花,黄菊花,苦花。', '阳药', '多年生草本。茎基部常匍匐,\r\n上部分枝,有棱角,被细柔毛。叶互生;基\r\n生叶在花期脱落;中部茎生叶片卵形或长圆\r\n状卵形,长3~9cm,宽1.5~3cm,羽状深裂\r\n顶裂片大,侧裂片常2对,卵形或长圆形,\r\n全部裂片边缘浅裂或有锯齿;上部叶片渐小;\r\n全部叶片上面有腺体及疏柔毛,深绿色,下\r\n面毛较多,灰绿色,基部渐狭成有翅的叶柄\r\n假托叶有锯齿。头状花序在枝端排列呈伞房\r\n圆锥花序式或不规则的伞房状;总苞半球形,\r\n总苞片4层,外层卵形或卵状三角形,中层\r\n卵形,内层长椭圆形,全部苞片边缘宽膜质;\r\n缘花舌状,黄色,雌性;盘花管状,两性。\r\n瘦果倒卵形,稍扁,无毛,黑色,有光泽,\r\n具数条纵细肋;冠毛无。', '多生长于旷野、山坡。', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '84', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'yejuOther.png', 'yeju;yj;jihua,aihua,huangjuhua,kuhua。;jh,ah,hjh,kh。', '2018-07-17 09:00:11', null, null, null, '1');
INSERT INTO `medicine` VALUES ('142', '东风菜', '哈卢弟,哈罗丁,海螺丁。', '阴药', '多年生草本。根状茎粗状,\r\n横卧,常烂去一半,棕褐色,旁生多数须根\r\n茎直立,高20-90cm,上部分枝,被微毛\r\n基部叶在花期枯萎,叶片心形,长8-15cm,\r\n宽6-15cm,先端尖,基部急狭成柄,边\r\n缘具小尖头的齿,叶柄长10-15cm;中部\r\n叶片较小,卵状三角形,基部圆形或稍截形,\r\n有具翅的短柄;上部叶片长圆状披针形或线\r\n形;全部叶片质厚,两面被微糙毛,网脉明\r\n显。头状花序少数,直径1.8-2.4mm:总\r\n苞片约3层,先端尖或钝,无毛有缘毛;缘\r\n花舌状,白色,约10朵,舌片线状长圆形,\r\n雌性盘花管状,项端裂片线状披针形,两性\r\n瘦果倒卵圆形或椭圆形,具5肋,无毛:冠\r\n毛污黄白色,长3.5-4mm,具多数微糙毛\r\n', '多生长于山地林缘、山坡 草地、灌木丛中或溪谷旁草丛中。', '夏、秋季采收,洗净,鲜\r\n用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'dongfengcaiOther.png', 'dongfengcai;dfc;haludi,haluoding,hailuoding。;hld,hld,hld。', '2018-07-17 09:03:44', null, null, null, '1');
INSERT INTO `medicine` VALUES ('143', '鳢肠', '墨黑草,日花草,墨汁草, 乌黑草。', '阳药', '1年生草本。茎匍匐状或近\r\n直立,自基部分枝,被糙硬毛,全株干后常\r\n变黑色。叶片长圆状披针形或线状披针形\r\n长3-10cm,宽5-15mm,先端渐尖,基\r\n部楔形,全缘或有细齿,两面密被硬糙毛,\r\n基出3脉;无叶柄。头状花序1~2个腋生\r\n或顶生,卵形,有梗;总苞球状钟形,总苞\r\n片5~6个排成2层,卵形或长圆形,先端\r\n钝或急尖,外部被紧贴的糙硬毛;缘花舌状\r\n白色,2层,顶端2浅裂或全裂,两性,结实\r\n盘花管状,白色,顶端4齿裂,两性\r\n结实\r\n雌花的瘦果三角形,两性花的瘦果扁四棱形\r\n顶端截形,具1~3个细齿,基部稍缩小\r\n边缘具白色的肋,表面小瘤状突起,无毛\r\n冠毛退化成2-3个小鳞片。', '多生长于路旁草丛、田埂 沟边草地。', '夏、秋季采收,洗净,鲜用或干燥', '8', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'lichangOther.png', 'lichang;lc;moheicao,rihuacao,mozhicao, wuheicao。;mhc,rhc,mzc, whc。', '2018-07-17 09:11:06', null, null, null, '1');
INSERT INTO `medicine` VALUES ('144', '细红背叶', '叶下红,一点红。', '阳药', '1年生柔弱草本。基生叶密\r\n集,上部稀疏,互生;叶片卵形、卵圆形、\r\n倒披针形至线状长圆形,长2.5~7cm,宽\r\n1~2cm,先端急尖或钝圆,基部楔形,下\r\n延至叶柄或戟形,全缘,具波状齿或稀疏锯\r\n齿,上面绿色,下面紫红色;下部的具柄,\r\n具狭翅,上部的叶无柄。头状花序具长梗;\r\n总苞圆筒状,绿色,总苞片1层,等长,短\r\n于花冠;花管状,紫红色,5齿裂;花柱分\r\n枝上端尖。瘦果圆柱形,有5纵肋;冠毛白\r\n色而软。', '多生长于路边、溪沟边 草地', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '235', '暂无', '暂无', '暂无', null, null, '8', null, null, null, null, 'xihongbeiyeOther.png', 'xihongbeiye;xhby;yexiahong,yidianhong。;yxh,ydh。', '2018-07-17 09:14:46', null, '重复', null, '0');
INSERT INTO `medicine` VALUES ('145', '细红背叶', '叶下红,一点红。', '阳药', '1年生柔弱草本。基生叶密\r\n集,上部稀疏,互生;叶片卵形、卵圆形、\r\n倒披针形至线状长圆形,长2.5~7cm,宽\r\n1~2cm,先端急尖或钝圆,基部楔形,下\r\n延至叶柄或戟形,全缘,具波状齿或稀疏锯\r\n齿,上面绿色,下面紫红色;下部的具柄,\r\n具狭翅,上部的叶无柄。头状花序具长梗;\r\n总苞圆筒状,绿色,总苞片1层,等长,短\r\n于花冠;花管状,紫红色,5齿裂;花柱分\r\n枝上端尖。瘦果圆柱形,有5纵肋;冠毛白\r\n色而软。', '多生长于路边、溪沟边 草地', '夏、秋季采收,洗净,鲜\r\n用或干燥。', '235', '暂无', '暂无', '暂无', null, null, '8', null, null, null, null, 'xihongbeiyeOther.png', 'xihongbeiye;xhby;yexiahong,yidianhong。;yxh,ydh。', '2018-07-17 09:14:46', null, '重复', null, '0');
INSERT INTO `medicine` VALUES ('146', '短毛金线草', '天油草，铁丁头，大叶蓼，人字草', '阴药', '多年生草本，茎疏被毛或近无毛。地下茎粗壮而短，呈结节状。茎直立，基部圆柱形，上部具细沟纹，节稍膨大，很少分枝。叶片椭圆形或长椭圆形，先端长渐尖，基部宽楔形稀圆形，全缘，两面被短伏毛或近无毛。瘦果椭圆形，双凸镜状，褐色，稍露出外包宿存花被', '生于山地林下阴湿处，沟谷溪边草丛中', '夏，秋季采收，除去杂质，洗净，鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'duanmaojinxiancaoOther.PNG', 'duanmaojinxiancao;dmjxc;tianyoucao，tiedingtou，dayeliao，renzicao;tyc，tdt，dyl，rzc', '2018-07-17 13:31:38', null, null, null, '1');
INSERT INTO `medicine` VALUES ('147', '野荞麦', '山花麦，天花麦，假花麦', '阳药', '多年生无毛草本，地下有粗壮浅沟纹结节状坚硬块根。茎直立，质柔软，具浅沟纹，中空；分枝常具乳头状突起。叶片宽三角形或卵状三角形，先端渐尖或尾尖，基部心状楔形，边缘及两面脉上具乳头状突起；托叶鞘膜质，筒状，顶端楔形，无缘毛。花白色，花簇排列成顶生或腋生的总状花序，再组成伞房花序。瘦果卵状三棱形，褐色。', '生于山坡荒地，旷野路边及水沟旁', '深秋季采收，除去杂质，洗净，切片，鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'yeqiaomaiOther.PNG', 'yeqiaomai;yqm;shanhuamai，tianhuamai，jiahuamai;shm，thm，jhm', '2018-07-17 13:46:38', null, null, null, '1');
INSERT INTO `medicine` VALUES ('148', '萹蓄', '日头花草，泻肚药，竹节草', '阳药', '1年生无毛草本，主根较粗，生多数褐色或黄褐色须根。茎自基部分枝，匍匐或斜向上生，绿色，具沟纹。叶互生；叶片长椭圆形、长圆状倒披针形、线状披针形或线性，先端钝或急尖，基部狭窄成有关节的短柄，下面灰白色；托叶鞘膜质，顶端数裂，有明显脉纹。花1~5朵簇生叶腋，绿色，具白色或粉红色边缘。瘦果卵状三角形，褐色', '生于山坡荒地杂草丛中，路边、草地及沙地上', '夏、秋季生长旺盛时采收，除去杂质，洗净，鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'bianxuOther.PNG', 'bianxu;bx;ritouhuacao，xieduyao，zhujiecao;rthc，xdy，zjc', '2018-07-17 13:59:12', null, null, null, '1');
INSERT INTO `medicine` VALUES ('149', '虎杖', '虎枪，斑竹根，花斑竹', '阴药', '多年生无毛草本，地下有横走的木质根茎。茎丛生，粗壮直立，圆柱形，表面有沟纹，常散生红色或带紫色的斑点，节间中空。叶互生；叶片宽卵形或近圆形，先端短凸尖，基部圆形，截形或宽楔形，全缘，下面有褐色脉点；托叶鞘膜质，圆筒形，褐色，易破裂脱落。花单性，雌雄异株，花被白色或淡绿白色。瘦果卵状三棱形，黑褐色，有光泽。', '生于山谷沟边、沟旁及路边草丛中', '深秋季采收，除去杂质，洗净，切片，鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'huzhangOther.PNG', 'huzhang;hz;huqiang，banzhugen，huabanzhu;hq，bzg，hbz', '2018-07-17 14:10:52', null, null, null, '1');
INSERT INTO `medicine` VALUES ('150', '水蓼', '水辣蓼，辣蓼', '阳药', '一年生无毛草本。茎直立，下部伏卧，节上可生不定根，红紫色或红色，节间短，节部膨大，通常有分枝。叶互生；叶片有辛辣味，披针形或长圆状披针形，先端渐尖或稍钝，基部楔形，两面密被腺点，无毛或沿中脉或叶缘上有小糙伏毛；托叶鞘膜质，筒状，顶端有长2~5mm的缘毛。穗状花序，常下垂，花簇稍稀疏间断，基部常有1~2花苞藏在托叶鞘内。瘦果卵形，双凸镜状，稀三棱形，暗褐色。', '生于土壤较瘠薄的溪边、沟边、沙漠旁及湿地中', '夏，秋季生长旺盛时采收，除去杂质，洗净，鲜用或干燥', '1', '暂无', '暂无', '暂无', null, null, '7', null, null, null, null, 'shuiliaoOther.PNG', 'shuiliao;sl;shuilaliao，laliao;sll，ll', '2018-07-17 16:38:46', null, '重复', null, '0');
