INSERT INTO `medicine` VALUES ('301', '扇叶铁线蕨', '乌脚枪', '阴药', '根状茎短，近直立，密被棕色、线状披针形有光泽的鳞片。叶簇生；叶柄亮紫黑色，向上光滑；叶片扇形至不整齐的阔卵形，长10~25cm，宽8~25cm，叶轴2~3回不对称的二叉分枝或鸟足状三叉分枝，羽片指向各方；小羽片8~15对，\r\n互生，平展，有短柄，斜方状椭圆形至扇形，上缘及外缘圆形，不育部分有细锯齿，基部圆楔形。叶脉扇形分叉，明显，伸达叶缘。叶坚纸质，无毛；叶轴、羽轴及小羽轴柄均为黑褐色，有光泽，上面疏生红棕色短细毛，下面无毛。孢子囊群着生于小羽片的上缘及外缘的小脉顶端，每小羽片有2~8个，囊群盖椭圆形，革质，黑褐色，全缘。', '生于疏林下或林缘灌木丛中。', '夏、秋季采收，洗净，鲜用或干燥。', '18_222_289', '暂无', '', '', null, null, '5', null, null, null, null, '', 'shanyetiexianjue;sytxj;wujiaoqiang;wjq', '2018-07-19 13:45:43', null, null, null, '1');
INSERT INTO `medicine` VALUES ('302', '三叶委陵菜', '白头翁;三叶青;三叶蛇毒', '阳药', '多年生草本。 根茎粗壮，呈串珠状。三出复叶，小叶片长圆形、卵形或椭圆形，先端急尖或圆钝，基部楔形或宽楔形，边缘有急尖锯齿;茎生托叶革质。伞房状聚伞花序顶生，多花，花瓣淡黄色。瘦果球形，表面有脉纹。', '多生于荒地、山坡草地或石缝上。', '夏季采收带根的全草，洗净，鲜用或干燥。', '215', '白头翁、田岸青根、雪里开各适量，水煎服。', '', '', null, null, '7', null, null, null, null, '三叶委陵菜.png', 'sanyeweilingcai;sywlc;baitouweng;btw;sanyeqing;syq;sanyeshedu;sysd', '2018-07-19 13:46:18', null, null, null, '1');
INSERT INTO `medicine` VALUES ('303', '蛇含委陵菜', '五叶草;五叶蛇扭', '阳药', ' 1年至多年生草本。多须根掌状复叶，基中、下部叶为5小叶，小叶片为倒卵形或长圈状倒卵形，先端圈钝，基部楔形，边缘锯齿尖锐或圆钝，茎上部叶为3小叶，小叶片与基生小叶片相似。聚伞花序,花密集枝顶如假伞形，  花瓣黄色，倒卵形，先端微凹。瘦果近圆形，一面稍平，具皱纹。', '多生长于山地、旷野，河边、路旁草地。', '夏、秋季采收带根的全草，洗净，鲜用或干燥。', '92_94', '(1) 甲沟炎:鲜五叶草适量，加白酒捣烂外敷。\r\n (2)手指生天蛇(甲沟炎);五叶草适量，盐卤适量。捣烂外敷。', '', '', null, null, '8', null, null, null, null, '', 'shehanweilingcai;shwlc;wuyecao;wyc;wuyesheniu;wysn', '2018-07-19 14:08:23', null, null, null, '1');
INSERT INTO `medicine` VALUES ('304', '梅', '梅树', '阳药', '落叶小乔木。叶片卵形或椭圆形，先端尾尖，基部宽楔形至圆形，边缘常具细锐锯齿。花单生或同时2朵同生于1芽内，有浓香，先叶开放，花瓣白色至粉红色。果实近球形，黄色或绿白色。', '栽培。', '深秋季采收根，洗净，鲜用或干燥。', '358', '梅树3g，打铙子根3g，毛冬青6g，竹米6g，蛤蟆衣15g，铁包金6g，  雷独草3g，水灯草3g，红百鸟不宿3g，山韭菜6g，坑香3g，野靛青3g，白马骨3g，水煎服。', '', '', null, null, '7', null, null, null, null, '梅.jpg', 'mei;m;meishu;ms', '2018-07-19 13:58:12', null, null, null, '1');
INSERT INTO `medicine` VALUES ('305', '虎尾铁角蕨', '鹅口药;墙春草', '阳药', '根状茎短而直立，顶部被黑褐色的狭披针形鳞片，叶簇生；叶柄长1~3cm亮栗色或红棕色，一面有1条纵沟，基部疏被鳞片，向上光滑；叶片阔披针形或倒披针形，先端渐尖，基部渐变狭，二回羽裂至二回羽状，羽片约20对，互生或近对生，平展，上部的较大三角状披针形或披针羽，先端渐尖，一回羽状或深羽裂；小羽片密接，基部1对较大、长圆形或卵形，基部宽楔形，圆头并具粗齿牙，下面羽逐渐缩短成卵形或半圆形，边缘有粗齿，叶脉羽状，侧脉二叉，不达叶边。叶薄草质，无毛，叶轴上面绿色，下面常为栗色。孢子囊群长圆形，着生于小脉上侧分枝近基部，靠近中脉，囊群盖长圆形，膜质，全缘。', '生于石塍缝隙、茶园四周等', '夏、秋季采收，洗净，鲜用或干燥', '226_362', '暂无', '', '', null, null, '5', null, null, null, null, '', 'huweitiejiaojue;hwtjj;ekouyao;eky;qiangchuncao;qcc', '2018-07-19 13:59:27', null, null, null, '1');
INSERT INTO `medicine` VALUES ('306', '铁角蕨', '墙串;吓草', '阳药', '根状茎短，直立，顶部密被黑褐色，现状披针形鳞片。叶簇生；叶柄栗褐色，有光泽，基部被鳞片，向上光滑，连同叶轴上面有1纵沟，沟的两侧各有1条全缘的膜质狭翅；叶片线形，先端渐尖。基部略变狭，一回羽状；羽片18~35对，互生或近对生，平展，长圆形或卵圆形，中部的较大、先端圆，基部为不对称的圆楔形，边缘具细齿，下部各对羽片渐缩小，基部1对常缩成耳状。叶脉羽状，不明显，侧脉二叉或单一。叶纸质，无毛。孢子囊群长圆形着生于小脉上侧分枝的中部，囊群盖长圆形，全缘。', '生于山地丘陵石上', '夏、秋季采收，洗净，鲜用或干燥', '56_362', '暂无', '', '', null, null, '5', null, null, null, null, '', 'tiejiaojue;tjj;qiangchuan;qc;xiacao;xc', '2018-07-19 14:07:32', null, null, null, '1');
INSERT INTO `medicine` VALUES ('307', '桃', '毛桃;野桃', '阳药', '落叶乔木。冬芽2~ 3个簇生，中间为叶芽，两侧为花芽。叶片长圆状披针形、卵状披针形或倒卵状披针形，先端渐尖，基部宽楔形，边缘具细锯传或粗锯齿。花单生，先叶开放，花瓣粉红色，稀白色。果实谈绿白色至橙黄色，向阳面具红晕。\r\n', '多生长于山坡、 溪沟边灌木丛中。有栽培。', '深秋季采收根，洗净，鲜用或干燥。', '327_328', '毛桃叶、猪肝各适量。捣烂敷患处', '', '', null, null, '7', null, '桃年幼期.jpg', null, null, '桃.jpg', 'tao;t;maotao;mt;yetao;yt', '2018-07-19 14:10:01', null, null, null, '1');
INSERT INTO `medicine` VALUES ('308', '石斑木', '牛眼睛;牛眼珠', '阳药', '常绿灌木。叶片薄革质，卵形、长圆形、稀倒卵形或长圆状披针形，先端圆钝、急尖、渐尖或长尾尖，基部渐狭下延至叶柄，边缘具细钝锯齿，上面光亮，细脉不明显或明显，下面叶脉稍隆起，细脉明显。圆锥花序或总状花序顶生，总花梗和花梗被锈色绒毛，花瓣白色或淡红色。果实紫黑色，球形。', '多生长于山坡、路旁或溪沟边灌木丛中。', '全年可采收， 洗净，鲜用或干燥。', '68', '牛眼睛适量，碾细粉，敷患处。', '', '', null, null, '7', null, null, null, null, '石斑木.jpg', 'shibanmu;sbm;niuyanjing;nyj;niuyanzhu;nyz', '2018-07-19 14:14:02', null, null, null, '1');
INSERT INTO `medicine` VALUES ('309', '菊叶三七', '土三七;三七;三七草', '阳药', '多年生草本。根肉质肥大,褐色,须根纤细。茎具纵条纹,稍被柔毛, 上部多分枝。基部叶簇生,叶片匙形,全缘,有锯齿或羽状深裂,花时凋落;中部叶互生,膜质,叶片长椭圆形,长l0~25cm,宽5~16cm,羽状深裂,裂片卵状披针形,长3 ~ 8cm,宽1 - 2cm,先端渐尖,基部楔形, 边缘具极不整齐的疏锯齿, 两面疏被柔毛或近无毛,中脉粗壮,侧脉纤细,两面均不明显,叶柄有2假托叶;上部叶片较小,近无柄,基部通常具2假托叶。头状花序多数, 在茎枝端排列呈疏伞房状或圆锥状聚伞花序式;总苞钟形,总苞片线状披针形,先端渐尖,边缘膜质,外面被疏柔毛或近无毛;管状花黄色,顶端5裂,裂片披针形,雄蕊内藏; 花柱伸出。瘦果圆柱形,被疏毛;冠毛白色。', '多栽培', '夏、秋季采收,洗净,鲜用或干燥。', '50_89', '暂无', '', '', null, null, '7', null, null, null, null, 'juyesanqi other.png', 'juyesanqi;jysq;tusanqi;tsq;sanqi;sq;sanqicao;sqc', '2018-07-19 14:15:44', null, null, null, '1');
INSERT INTO `medicine` VALUES ('310', '硕苞蔷薇', '大红袍;算盘子;轮鸡陀', '阳药', '常绿匍匐灌木。小枝密被黄褐色柔毛，并混生针刺和脉毛，皮刺扁弯，常成对生于托叶下方。复叶有小叶5一13枚,小叶片革质或薄革质，椭圆形或倒卵形，先端截形、圆钝或稍急尖，基部宽楔形或近圆形，边缘有紧贴圆钝锯齿，上面深绿色，无毛，有光泽，下面色较淡，沿脉有柔毛。花多单生，花瓣白色，倒卵形，心皮多数，果球形，密被黄褐色柔毛。', '多生长于山坡、 路边、溪沟边等向阳处。', '秋、冬季采收，洗净，鲜用或干燥。', '23_81', '大红袍根60一100g，猪蹄1只，水煎服。', '', '', null, null, '7', null, null, null, null, '硕苞蔷薇.jpg', 'shuobaoqiangwei;sbqw;dahongpao;dhp;suanpanzi;spz;lunjituo;ljt', '2018-07-19 14:26:46', null, null, null, '1');
INSERT INTO `medicine` VALUES ('311', '月季花', '月月红', '阳药', '常绿或半常绿直立灌木。茎无毛，有短粗的钩状刺，稀无刺。复叶有小叶3一5枚，小叶片宽卵形或卵状长圆形，先端长渐尖或渐尖，基部近圆形或宽楔形，边缘有锐锯齿，托叶大部贴生于叶柄，先端分离部分耳状，边缘常有脉毛。花数朵集生或单花，花瓣多红色或粉红色。果红色，卵球形或梨形。\r\n', '栽培', '秋、冬季采收，洗净，鲜用或干燥。', '124', '月月红根60g，老虎吊根50g，鸡蛋2个，水煎后，鸡蛋冲服。', '', '', null, null, '7', null, null, null, null, '月季花.jpg', 'yuejihua;yjh;yueyuehong;yyh', '2018-07-19 14:29:44', null, null, null, '1');
INSERT INTO `medicine` VALUES ('312', '向日葵', '日头花;日头花蒲', '阳药', '1年生高大草本。茎粗壮,有粗毛,髓部极发达。叶互生;叶片心状椭圆形或卵圆形,先端急尖或渐尖,基部楔形或心形,边缘有锯齿,两面被糙毛,具基出3出脉;有长柄。头状花序大,直径10~30cm,单生于茎端或枝端,常下倾;总苞片多层,叶质,覆瓦状排列,卵形至卵状披针形,先端尾状渐尖,被毛;花序托平或隆起,有膜质托片;缘花舌状,黄色,多数，舌片长圆状卵形或卵状长圆形，稍压扁,有细肋,常被白色短柔毛;冠毛膜片状,2枚,早落。', '栽培', '秋季采收,洗净,鲜用或干燥。', '294_382', '暂无', '', '', null, null, '7', null, null, null, null, 'xiangrkui other.png', 'xiangrikui;xrk;ritouhua;rth;ritouhuapu;rthp', '2018-07-19 14:40:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('313', '菊芋', '洋姜;广东芋;洋生姜', '阳药', '多年生草本。地下茎块状。茎被粗毛及刚毛。下部叶通常对生,上部叶互生; 下部叶片卵圆形或卵状椭圆形,长10~ 16cm,宽3 ~6cm,先端渐尖,基部宽楔形或圆形, 有时微心形, 边缘有粗锯齿, 上面有短粗毛, 下面被柔毛, 具离基3出脉,有长柄;上部叶长椭圆形至宽披针形,先端渐尖,基部渐狭,下延成具狭翅的短柄。头状花序直径5 ~9cm,单生于枝端,直立; 总苞片多层,披针形,先端长渐尖,外面被短伏毛,托片长圆形,先端不等3浅裂;缘花舌状,黄色,舌片长椭圆形,开展;盘花管状,黄色。瘦果楔形,上端有2~4校锥状扁芒。', '栽培。', '秋季采收,洗净,鲜用或千燥。', '50', '暂无', '', '', null, null, '7', null, null, null, null, 'juyu other.png', 'juyu;jy;yangjiang;yj;guangdongyu;gdy;yangshengjiang;ysj', '2018-07-19 14:51:40', null, null, null, '1');
INSERT INTO `medicine` VALUES ('314', '苦荬菜', '山苦菜', '阳药', '1年或2年生草本。茎多分枝,常带紫红色 。基生叶花期枯萎,叶片卵形、长圆形或披针形;茎生叶片舌状卵形或倒长卵形,长3 ~ 9cm,宽1.5 ~ 4cm,先端急尖,基部微抱茎,耳状,边缘具不规则锯齿。头状花序具梗, 排列呈伞房状; 总苞圆筒形,长6~9mm,总苞的外苞片小,卵形,总苞片8层, 1层,线状披针形,先端急尖;花全为舌状,黄色,顶端5齿裂。瘦果纺锤形,具11 ~ 14肋,黑褐色,有短喙,肋间有浅沟,肋上细点状粗糙; 冠毛刚毛状, 白色 。', '多生长于路边荒地、田野、山坡。', '夏、秋季采收，洗净，鲜用或干燥。', '168_235', '内服煎汤15~60g', '', '', null, null, '7', null, null, null, null, 'kumaicai other.png', 'kumaicai;kmc;shankucai;skc', '2018-07-19 15:06:32', null, null, null, '1');
INSERT INTO `medicine` VALUES ('315', '马兰', '田岸青;水苦益;温州青', '阳药', '多年生草本。茎多少有分枝,被短毛。基部叶片在花期枯萎;茎生叶片披针形至倒卵状长圆形,长3 ~ 7cm,宽1~2.5cm,先端钝或尖,基部渐狭,边缘从中部以上具2 ~ 4对浅齿或深齿,具长柄; 上部叶片渐小,全缘,两面有疏柔毛或近无毛,无柄。头状花序単生于枝端,并排列呈疏伞房状;总苞半球形,总苞片2 ~ 3层,覆瓦状排列,外层倒披针形,内层倒披针状长圆形,先端钝或稍尖,上部草质,有疏短毛,边缘膜质,有缘毛;缘花舌状,紫色, 1层;盘花管状,多数。瘦果倒卵状长圆形,极扁, 褐色,边缘有厚肋,上部被腺及短柔毛;冠毛短毛状,易脱落。', '多生长于山坡、沟边、湿地、路旁及田埂上。有作野菜零星栽培。', '夏、秋季采收,洗净,鲜用或干燥。', '44_235_406', '内服煎汤30~100g', '', '', null, null, '7', null, null, null, null, 'malan other.png', 'malan;ml;tiananqing;taq;shuikuyi;sky;wenzhouqing;wzq', '2018-07-19 15:19:09', null, null, null, '1');
INSERT INTO `medicine` VALUES ('316', '千里光', '木灵头;木米头;木莲头;九里明;千里桔;九里光', '阳药', '多年生攀援草本。根状茎通常木质。茎曲折,多分枝,具棱,初被疏短柔毛,花后渐脱落近无毛。叶互生;具短柄;叶片卵状披针形至长三角形,长3 ~ 10cm, 宽1.5~4.2cm,先端长渐尖,基部楔形至截形,边缘具不规则钝齿、波状齿、近全缘或叶的下部有1 ~ 2对裂片,两面疏被短柔毛或上面无毛;上部叶片新小,近无柄。头状花序多数, 在茎枝端排成开展的复伞房状或圆锥状聚伞花序式; 总花梗常反折或开展,被短柔毛,具线形苞叶;总苞杯状,外面被微柔毛,基部具数个条形小苞片,总苞片1层,条状披针形先端渐尖, 边缘膜质; 缘花舌状,黄色,少数,雌性,结实;盘花管状,黄色,顶端5裂,裂片开展,两性,结实。瘦果圆柱形, 被短毛; 冠毛白色或污白色 ', ' 多生长于山坡、山沟、林中灌木丛中 。', '秋季采收,洗净,鲜用或千燥。', '68_369', '内服煎汤15 ~50g;外用适量。', '', '', null, null, '7', null, null, null, null, 'qianliguang other.png', 'qianliguang;qlg;mulingtou;mlt;mumitou;mmt;muliantou;mlt;jiuliming;jlm;qianlijie;qlj;jiuliguang;jlg', '2018-07-19 15:51:28', null, null, null, '1');
INSERT INTO `medicine` VALUES ('317', '金樱子', '甜缸;鸡陀刺;酒罐刺', '阳药', '常绿攀缘灌木。小枝散生扁弯皮刺，无毛。复叶多3枚，稀5枚，小叶片革质，椭圆状卵形、倒卵形或披针状卵形，先端急尖或圆钝，稀尾状渐尖，边缘有锐锯齿:叶轴有皮刺和腺毛。花单生于叶腋，花瓣白色。果梨形或倒卵形，外面有密或疏的针刺。', '多生长于向阳山坡、田边、溪沟边、谷地疏林下或灌木丛中。', '秋季采收， 洗净，鲜用或干燥。', '222_413', '甜缸根50g,冰糖5g,水煎服。(2)痢疾:甜缸根50一60g，水煎服。', '', '', null, null, '7', null, null, null, null, '金樱子.jpg', 'jinyingzi;jyz;tiangang;tg;jituoci;jtc;jiuguanci;jgc', '2018-07-19 16:31:56', null, null, null, '1');
INSERT INTO `medicine` VALUES ('318', '野蔷薇', '七姐妹', '阳药', '落叶攀缘灌木。小枝无毛，有短粗稍弯曲皮刺。复叶有小叶5 ~ 9枚，近花序的有时小叶3枚，小叶片倒卵形、长圆形或卵形，先端急尖或圆钝，基部近圆形或楔形，边缘有尖锐锯齿，稀间有重锯齿;托叶篦齿状，边缘有腺毛。花多朵，排成圆锥状花序，花瓣白色。果红褐色或紫褐色，有光泽，近球形，无毛。', '多生长于向阳山坡、溪沟边、路旁或灌木丛中。', ' 深秋季采收，洗净，鲜用或干燥。', '89_125', '(1)无名肿毒:七姐妹、细叶冬青、牛目引叶、半边莲各等量。捣烂，加适量黄酒，敷患处。\r\n(2)崩漏:七姐妹根30~ 50g，煮嫩鸡，服汤食鸡。血止后滋补调理。\r\n', '', '', null, null, '7', null, null, null, null, '野蔷薇.jpg', 'yeqiangwei;yqw;qijiemei;qjm', '2018-07-19 17:48:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('319', '无毛野燕麦', '山麦;吊儿麦', '阳药', '1年生草本。秆光滑, 2~4节。叶鞘光滑或基部有毛,叶舌透明,膜质, 长1 ~ 5mm;叶片扁平,长10~ 30cm, 宽4~ 12mm,微粗糙。圆锥花序开展,长10~ 25cm,分枝有棱角,粗糙;小穂有2 ~ 3 朵小花;小穂轴易脱落,通常有毛;颖通常具9脉,革质;外稃近革质,背部光滑无毛。 基盘密生短茸毛 。 颖果腹面具纵沟 。', '多生长于荒地、路边或田间。', '夏季采收,洗净,鲜用或干燥。', '171', '内服煎汤10~30g。', '', '', null, null, '8', null, null, null, null, '', 'wumaoyeyanmai;wmyym;shanmai;sm;diaoermai;dem', '2018-07-21 20:30:21', null, null, null, '1');
INSERT INTO `medicine` VALUES ('320', '亮叶桦', '手指柴', '阳药', '乔木，树皮淡黄褐色，平滑，不裂，干皮有清香气；小枝具毛，疏生树脂腺体。叶片宽三角状卵圆形，或长卵形，先端长渐尖，基部圆形、近心形或略偏斜，边缘具有不规则的刺毛状重锯齿，叶下面具毛和腺体，沿脉疏生长柔毛；叶柄具柔毛及腺体。果序单生叶腋；小坚果倒卵状长圆形，黄色。', '山谷，向阳山坡的杂木林中', '夏季剥取树皮、冬季采挖根，洗净，切片，鲜用或干燥。', '388', '疥疮：鲜手指柴100g，加水500ml，煎至200ml，放凉，洗患处。', '', '', null, null, '1', null, null, null, null, '', 'liangyehua;lyh;shouzhichai;szc', '2018-07-19 21:15:47', null, '1', null, '0');
