INSERT INTO `medicine` VALUES ('325', '白栎', '白头菇', '阳药', '落叶乔木；小枝被褐色毛，后渐脱落。树叶倒卵形或倒卵状椭圆形，先端钝，基部楔形，边缘有波状钝齿，幼两面均被灰黄色星状绒毛，后尽下面有毛，侧脉8~12对；叶柄短，被毛。壳斗碗状，苞片卵状披针形，排列紧密，在壳斗边缘处稍伸出；坚果长椭圆形，果脐隆起', '生于丘陵，低山杂木林中', '秋季采收，鲜用或干燥', '307', '食积：白头菇20g，水煎服。', '', '', null, null, '7', null, null, null, null, '', 'baili;bl;baitougu;btg', '2018-07-19 21:32:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('326', '榔榆', '伤药，伤皮树', '阳药', '落叶乔木，树皮灰褐色，呈不规则鳞状剥落，而露出红褐色或绿褐色内皮；小枝红褐色，被柔毛。叶片窄椭圆形、卵形或倒卵形，先端短尖或略钝，基部偏斜。', '生于丘陵，山坡路边，溪边或山坡疏林中。', '深秋季采集根皮，夏，秋采收叶，洗净，鲜用或干燥。', '23_40', '腰痛：伤药皮50g，水煎服。\r\n疔疮：鲜伤药叶适量，加白酒。', '', '', null, null, '7', null, null, null, null, '', 'langyu;ly;shangyao，shangpishu;sy，sps', '2018-07-19 21:45:20', null, null, null, '1');
INSERT INTO `medicine` VALUES ('327', '藤葡蟠', '大料谷树皮，黄皮绳', '阳药', '灌木。枝蔓生弯曲，干后紫叶片长卵形或椭圆形，通常不裂，渐尖，基部渐心形，通常不对称，边锯齿，上有疏毛，下面毛较密；叶花单性，雌雄异株；雄花为葇夷花序，雌花为状花序。聚花果，成熟时小核果橙红，表面有小瘤状凸起。', '生于丘陵山坡、溪沟、路边，攀援于他物上', '夏、秋季采收叶，洗净，鲜用或干燥', '50_404', '外伤：鲜大料谷树皮适量，洗净与糯米饭一起捣烂敷患处\r\n肝炎：鲜大料谷树皮50~100g，喝汤', '', '', null, null, '7', null, null, null, null, '', 'tengpupan;tpp;daliaogushupi，huangpisheng;dlgsp，hps', '2018-07-19 21:56:39', null, null, null, '1');
INSERT INTO `medicine` VALUES ('328', '寒莓', '落泥泡;寒庙', '阴药', '蔓性常绿小灌木。茎有稀疏小皮刺，常伏地生根，长出新株。单叶，叶片纸质，卵形至近圆形，  先端圆钝或稍急尖，基部心形，边缘有不整齐锐锯齿，有不明显的3~ 5裂，裂片圆。短总状花序，腋生或顶生，总花梗和花梗密被灰白色绒毛和长柔毛及散生针刺，花瓣白色，倒卵形。聚合果成熟时紫黑色，近球形，无毛。', '多生长于山坡灌木丛及林下。', '夏、秋季采收，洗净，鲜用或干燥。', '275', '落泥泡、虎杖各等量，水煎服。', '', '', null, null, '7', null, null, null, null, '寒莓.jpg', 'hanmei;hm;luonipao;lnp;hanmiao;hm', '2018-07-19 21:57:37', null, null, null, '1');
INSERT INTO `medicine` VALUES ('329', '小构树', '谷皮柴，构树皮。', '阳药', '落叶灌木或有时蔓生。小枝暗紫红色，细长，疏生，幼时有短柔毛，后光滑无毛。叶片卵形或长卵形，先端长渐尖，基部圆，具2~3个乳头状腺体，基部三出脉，边缘有锯齿，不裂或2~3裂，上面绿色，覆糙状毛，下面淡绿色，有细毛。花单性，雌雄异株；头状花序。聚花果球形；小核果橙红色，核扁圆形，一侧有一纵沟，表面具瘤状突起。', '生于山坡路边，山谷沟边及田埂上。', '夏、秋采收叶片，洗净，鲜用或干燥', '386_388', '疥疮：鲜谷皮柴叶捣烂涂患处\r\n体癣：谷皮柴100g，水煎至100ml用纱布蘸药汁敷患处', '', '', null, null, '7', null, null, null, null, '', 'xiaogoushu;xgs;gupichai，goushupi。;gpc，gsp。', '2018-07-19 22:08:13', null, null, null, '1');
INSERT INTO `medicine` VALUES ('330', '山莓', '尖叶扭;三月扭;三月泡;细叶覆盆子', '阳药', '落叶灌木， 从地下部分蘖生新苗。茎具稀疏针状弯曲皮制。单叶，叶片卵形、卵状披针形，先端渐尖，基部心形至圆形，不裂或3浅裂，边缘有不整齐的重锯齿。花单生，稀数朵簇生于枝端，花筹白色，长圆形。聚合果球形，密被细柔毛。', '多生长于向阳山坡、路边，溪沟边或灌木丛中。', '秋、冬季采收，洗净，鲜用或干燥。', '272', '尖叶扭根50g，猪蹄1只，水煎服。', '', '', null, null, '7', null, null, null, null, '山莓.jpg', 'shanmei;sm;jianyeniu;jyn;sanyueniu;syn;sanyuepao;syp;xiyefupenzi;xyfpz', '2018-07-19 22:21:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('331', '葨芝', '黄鸡母，担米刺，石米刺，九重皮', '阳药', '常绿直立过蔓生。树皮灰褐色，略粗糙；枝灰褐色，皮孔散生，具粗壮、直立或略弯曲的枝刺。叶片革质，倒卵状或椭圆形，先端或渐尖或有凹缺，基部楔形，全缘，上面深绿色，下面淡绿色，两面无毛，侧脉6~10对。头状花序单生或成对腋生，不想等，被毛；雌蕊萼片4，顶端厚，有绒毛。聚花果球形，肉质，橙红色有毛，瘦果包在肉质的花萼和苞片中。', '生于山坡湿润林下或沟边灌木丛中', '秋、冬季采收，洗净，切片鲜用或干燥', '50', '跌打损伤：黄鸡母50g，水煎服', '', '', null, null, '7', null, null, null, null, '', 'weizhi;wz;huangjimu，danmici，shimici，jiuzhongpi;hjm，dmc，smc，jzp', '2018-07-19 22:24:12', null, null, null, '1');
INSERT INTO `medicine` VALUES ('332', '常叶覆盆子', '搁公扭根;搁工;公公扭;搁工扭', '阳药', '落叶灌木。幼枝有白粉，具少数皮刺。单叶，叶片近圆形，掌状5深裂，稀3或7裂，中裂片菱状卵形，基部近心形，边缘重锯齿或缺刻，两面脉上有白色短柔毛。花单生于短枝顶端，花瓣白色。聚合果红色，球形，密被白色柔毛。', '多生长于山坡疏林。 灌木丛或山麓林缘。', '秋，冬季采收，洗净，鲜用或干燥。', '70_71', '(1)结核性瘘管;搁工扭根10一20g，华山矾根20g，阴石蕨根茎6g，广东石豆兰10g，星宿菜根15g，水煎服。\r\n(2)结核病所致脊柱压迫症:搁工扭根20g，广东石豆兰10g，华山矾根20g，阴石蕨根茎6g，夏枯草10g，石吊兰20g，刺茎梅木10g，水煎服。', '', '', null, null, '7', null, null, null, null, '常叶覆盆子.jpg', 'changyefupenzi;cyfpz;gegongniugen;ggng;gegong;gg;gonggongniu;ggn;gegongniu;ggn', '2018-07-19 22:27:52', null, null, null, '1');
INSERT INTO `medicine` VALUES ('333', '蓬蘽', '布袋扭;牛奶扭;扭叶', '阳药', '半常绿小灌木。枝被腺毛、柔毛及散生稍直的皮刺。奇数羽状复叶，小叶了一5枚，顶生小叶片柄长1.5cm; 小叶片卵形或宽卵形，先端急尖或渐尖，基部圆形、心形或宽楔形，边缘有不整齐的重锯齿，两面散生白色柔毛。花单生侧枝顶端，花瓣白色，倒卵形或近圆形。聚合果红色，近球形，无毛。', '多生长于山沟、路旁或灌木丛中。', '秋，冬季采收，洗净，鲜用或干燥。', '270', '鲜布袋扭全草30 ~50g,  水煎服，用叶适量蒸浴。', '', '', null, null, '7', null, null, null, null, '蓬蘽.jpg', 'penglei;pl;budainiu;bdn;niunainiu;nnn;niuye;ny', '2018-07-19 22:34:49', null, null, null, '1');
INSERT INTO `medicine` VALUES ('334', '天仙果', '牛乳绳，牛奶绳，大叶牛奶绳', '阴药', '落叶小乔木或灌木。树皮灰褐色；小枝和叶柄被硬毛。叶片厚纸质，倒卵状椭圆形或长圆形，先端渐尖，基部圆形或浅心形，全缘希上部有疏齿，下有被柔毛，具有乳头状突起，基出脉三条，侧脉5~7对；叶柄纤细，密被白色短硬毛；托叶三角状披针形，早落。隐头花序单生或成对腋生，球形或近梨形，幼时被柔毛或短粗毛，顶生苞片突出，基生苞片3，卵状三角形，不脱落。隐花果成熟时暗红色，有淡红色斑点。瘦果三角形。', '生于山坡林下湿润处，山谷、沟边灌丛和梯田沟边', '秋、冬季采收，洗净，切片，鲜用或干燥。', '185', '脱力劳作：疳积。内服煎汤30~100g\r\n小儿疳积：牛乳绳根50~100g，煎汤，炖小公鸡（500g），喝汤（分2次服）', '', '', null, null, '7', null, null, null, null, '', 'tianxianguo;txg;niurusheng，niunaisheng，dayeniunaisheng;nrs，nns，dynns', '2018-07-19 22:40:10', null, null, null, '1');
INSERT INTO `medicine` VALUES ('335', '白叶莓', '空洞庙;乌柳绳', '阳药', '落叶灌木。枝拱曲，小枝密被绒毛状柔毛，疏生钩状皮刺。小叶通常3枚，稀5或7枚，顶生小叶通常卵形或近圆形，基部圆形或浅心形，边缘具不整齐圆钝而短尖的锯齿或缺刻状粗重锯齿。总状或圆锥花序顶生或腋生，花瓣紫红色。聚合果橘红色，近球形，成熟时无毛。', '多生 长于山坡林下灌木丛中。', '秋，冬季采收，洗净，鲜用或干燥。', '2_295', '空洞庙15g,水煎服。', '', '', null, null, '7', null, null, null, null, '白叶梅.jpg', 'baiyemei;bym;kongdongmiao;kdm;wuliusheng;wls', '2018-07-19 22:40:27', null, null, null, '1');
INSERT INTO `medicine` VALUES ('336', '高粱泡', '寒扭;冬泡', '阳药', '半常绿蔓性灌木。茎有棱，散生钩状小皮刺。单叶，叶片宽卵形，稀长圆状卵形，先端渐尖，基部心形，边缘明显3一5裂或呈波状，有微锯齿。圆锥花序顶生，花瓣白色，卵形，无毛。聚合果红色，球形，无毛。', '多生长于山谷溪沟边、林下或灌木丛中。', '秋、冬季采收，洗净，鲜用或干燥。', '274_357_431', '(1)风痛:寒扭根10 ~ 20g， 山桐子10~ 15g。加猪蹄，水煎服。\r\n(2)风气阴肿(睾丸肿大) :蛇葡萄根适量，与乌骨鸡的鸡蛋煮熟内服。\r\n(3)坐骨神经痛:寒扭根30g，与猪蹄共同煮，  食肉喝汤，连服6~ 7天。', '', '', null, null, '7', null, null, null, null, '高粱泡.jpg', 'gaoliangpao;glp;hanniu;hn;dongpao;dp', '2018-07-20 13:13:48', null, null, null, '1');
INSERT INTO `medicine` VALUES ('337', '茅莓', '种田白;山桃旦根;插田扭', '阳药', '落叶小灌木。小叶3枚，在新枝上偶有5枚，顶生小叶片菱状圆形至宽倒卵形，先端圆钝，基部圆形或宽楔形，边缘有重锯齿;侧生小叶片稍小，宽倒卵形至楔状圆形，先端急尖至圆钝，基部宽楔形或近圆形，边缘浅裂或不规则粗锯齿。伞房花序顶生或腋生，花少数，花瓣粉红色至紫红色。聚合果红色，卵球形。', '多生长于丘陵、 山坡、路边。', '秋、 冬季采收，洗净，鲜用或干燥。', '96', '鲜种田白叶适量，红糖少许，捣烂敷患处。', '', '', null, null, '7', null, null, null, null, '茅莓.jpg', 'maomei;mm;zhongtianbai;ztb;shantaodangen;stdg;chatianniu;ctn', '2018-07-20 13:18:41', null, null, null, '1');
INSERT INTO `medicine` VALUES ('338', '地榆', '山红枣;山枣仁;山荔枝', '阳药', '多年生草本。根粗壮，多呈纺锤状，外面棕褐色或紫褐色。茎直立，有棱。羽状复叶，基生叶有小叶9 ~ 13枚，小叶片卵形或长圆状卵形，先端圆钝，稀急尖，基部心形至找心形，边缘有圆钝或急尖粗大锯齿，茎生叶较少，小叶片狭长圆形至长國状披针形，先端急尖，基部微心形至圆形。穗状花序圆锥形、卵球形或圆柱形，尊片紫红色，4枚。果实包藏于宿存尊简内，外面有4棱。', '多生长于山坡草地，路旁、灌木丛中。', '秋、冬季来收，洗净，鲜用或干燥。', '61_62_171_175', '(1) 水、火烫伤:山红枣根适量，磨汁搽患处。\r\n(2)白带:山红枣根15 ~ 20g,水煎服。\r\n', '', '', null, null, '7', null, null, null, null, '地榆.jpg', 'diyu;dy;shanhongzao;shz;shanzaoren;szr;shanlizhi;slz', '2018-07-20 13:23:16', null, null, null, '1');
INSERT INTO `medicine` VALUES ('339', '中华绣线菊', '米筛花;新米花', '阳药', '灌木。小枝红褐色。冬芽卵形，顶端急尖，有数枚鳞片，外被柔毛。叶片菱状卵形至倒卵形，先端急尖至圆钝，基部宽楔形至圆形，边缘有缺刻状粗锯齿或不明显3裂，上面被短毛，下面密被黄色绒毛。伞形花序具花16~ 25朵，花瓣白色，近圆形。菁莞果开张，全体被短柔毛。', '多生长于山坡灌木丛中、山谷，溪沟边或荒野路旁等处。', '深秋采收， 洗净，鮮用或干燥。', '222', '米筛花15g,水煎服。', '', '', null, null, '7', null, null, null, null, '中华绣线菊.jpg', 'zhonghuaxiuxianju;zhxxj;mishaihua;msh;xinmihua;xmh', '2018-07-20 13:27:10', null, null, null, '1');
INSERT INTO `medicine` VALUES ('340', '田皂角', '稻挣;稻接草;禾青', '阴药', ' 1 年生半灌木状草本。茎直立，圆柱形，具细线楼，无毛。偶数羽状复叶，小叶40一50枚，小叶片线状长椭圆形，先端钝，具小尖头，基部圆形，仅具1脉，无小叶柄。总状花序腋生，花瓣黄色，带紫纹。荚果线状。', '多生长于潮湿地或水边。', '中秋节前后采收， 洗净，鲜用或干燥。', '242', '稻挣30g，猪肝50g，水煎服。', '', '', null, null, '7', null, null, null, null, '田皂角.jpg', 'tianzaojiao;tzj;daozheng;dz;daojiecao;djc;heqing;hq', '2018-07-20 13:36:49', null, null, null, '1');
INSERT INTO `medicine` VALUES ('341', '合欢', '细叶隔猛;隔夜柴', '阳药', '落叶乔木。树冠宽展。二回羽状复叶,羽片4~ 12对，栽培的可达20对，小叶20 ~ 60枚，小叶片镰形或斜长圆形，先端有小尖头，叶缘或下面中脉有短柔毛，中脉紧靠上部叶缘。头状花序多个排成伞房状圆锥花序，顶生或腋生，花粉红色，花丝上部粉红色。荚果带状，老荚无毛。', '多生长于溪沟边疏林中、林缘或荒山坡。有栽培。', '夏、 秋季采收，洗净，鲜用或干燥。', '3', '细叶隔猛30g，冰糖15g，水煎服。', '', '', null, null, '7', null, null, null, null, '合欢.jpg', 'hehuan;hh;xiyegemeng;xygm;geyechai;gyc', '2018-07-20 13:40:20', null, null, null, '1');
INSERT INTO `medicine` VALUES ('342', '土圞儿', '金丝吊葫芦;地雷;丝线吊葫芦', '阳药', '多年生缠绕草本。块根宽椭圆形或纺锤形。茎细长，被倒向的短硬毛。羽状复叶，小叶片，3~5枚，稀有7枚，顶生小叶片较大，宽卵形或卵状披针形，先端渐尖或尾尖，有小尖头，基部圆形或宽楔形，两面有糙伏毛，脉上尤密;  侧生小叶片常为斜卵形。总状花序，花冠淡黄绿色有时带紫晕。荚果线形，被短柔毛。', '多生长于向阳山坡林缘和灌草丛中，常缠绕其他植物上。', '深秋季采收，洗净，鲜用或千燥。', '362', '金丝吊葫芦、千年霜、苦爹菜、龙须草各9g，白茅根30g，水煎服。', '', '', null, null, '7', null, null, null, null, '土圞儿.jpg', 'tuluaner;tle;jinsidiaohulu;jsdhl;dilei;dl;sixiandiaohulu;sxdhl', '2018-07-20 13:44:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('343', '龙须藤', '龙须藤', '阳药', '常绿木质滕本。小枝被锈色短柔毛，老枝有明显红色小皮孔，卷须不分枝，单生或对生。叶片纸质或厚纸质，卵形、长卵形或卵状椭圆形，先端2裂达叶片的1/3或微裂，稀不裂，裂片先端渐尖，基部心形至圆形，掌状脉5~ 7条。总状花序1个与叶对生，或数个聚生于枝顶，花瓣白色，具瓣柄。英果厚革质，椭圆状倒披针形或带状，扁平，无毛。种子近圆形，无毛。', '多生长于海拔800m以下山坡灌木丛中，疏林或林缘中、岩石旁。', '深秋季采收，洗净，鲜用或干燥。', '72', '龙须藤30g，机木20g，坑香3g，钩藤15g，五加皮15g，鸭掌柴30g，木通10g，食凉茶根10g，水煎服。', '', '', null, null, '7', null, null, null, null, '龙须藤.jpg', 'longxuteng;lxt;longxuteng;lxt', '2018-07-20 13:53:21', null, null, null, '1');
INSERT INTO `medicine` VALUES ('344', '云实', '鸟不踏树;鸟不息树;山肥皂;山油皂', '阳药', '落叶攀缘灌木。 全体散生倒钩状皮刺，老枝红褐色。二回羽状复叶，羽片3~10对，小叶14一30枚，小叶片长圆形，两端钝圆，微偏斜，全缘，两面均被脱落性短柔毛。总状花序顶生，具多花，花冠黄色。荚果栗褐色，脆革质，长圆形，沿腹缝线有宽约3mm狭翅，成熟时 沿改缝线开裂，有6- 9粒长圆形种子。', '多生长于山谷、 山坡、路边村旁灌木丛中或林缘。', '深秋季采收， 洗净，鲜用或干燥。', '132_157', '鸟不踏树、益母草、白茅根，当归、鹿衔草、连翘、玄参各10g，海金沙6g，水煎服。', '', '', null, null, '7', null, null, null, null, '云实.jpg', 'yunshi;ys;niaobutashu;nbts;niaobuxishu;nbxs;shanfeizao;sfz;shanyouzao;syz', '2018-07-20 13:56:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('345', '春云实', '百鸟不歇', '阳药', '常绿木质藤本。全体密被锈色绒毛及倒钩皮刺。二回羽状复叶，羽片8~16对，小叶10一36枚，小叶片卵状披针形、卵形或椭圆形，先端急尖，基部圆形，上面有光泽，无毛，下面被锈色绒毛。圆锥花序着生枝条上部叶腋或顶生，具多数花， 花冠黄色。荚果黑紫色，木质，斜长圆形，腹缝线无翅，成熟时迟裂或不开裂，有斧形种子1~2粒。', '多生长于山沟湿润灌木丛中、疏林下及谷地岩石旁。', '深秋季采收，洗净，鲜用或干燥。', '289', '百鸟不歇、乌柳绳、空洞庙、铁包金、白马骨、水丁香、厚朴等各适量，水煎服\r\n', '', '', null, null, '7', null, null, null, null, '春云实.jpg', 'chunyunshi;cys;bainiaobuxie;bnbx', '2018-07-20 13:59:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('346', '锦鸡儿', '卵花草;鸡卵花;土黄芪', '阳药', '灌木。小枝多少有棱。一回羽状复叶有小叶4枚，上面1对通常最大，小叶片革质或硬纸质，  倒卵形、例卵状楔形或长园状倒卵形，先端圆或微凹，通常具短渐尖，叶轴先端硬化成针，托叶三角状技针形，先端硬化成针刺。花单生叶腋，花梗中部具关节，关节上有极细的小苞片，花冠黄色带红，调谢时红褐色。荚果稍廁，无毛。', '多生长于山坡、 山谷、路旁灌木丛中。有栽培。', '深秋季采收，洗净，鲜用或干燥。', '77', '卵花草l5g， 穿山龙12g，猪蹄120g， 水炖服。', '', '', null, null, '7', null, null, null, null, '锦鸡儿.jpg', 'jinjier;jje;luanhuacao;lhc;jiluanhua;jlh;tuhuangqi;thq', '2018-07-20 14:03:57', null, null, null, '1');
INSERT INTO `medicine` VALUES ('347', '野百合', '介狗铃;硬骨山花生;麦粒齐;大响铃', '阳药', '1年生草本。茎直立，基部有时木质化，被黄褐色丝质长糙毛。单叶互生，叶片线形或披针形，有时长圆形，先端急尖，基部略狭窄成短柄至无柄，下面密被绢毛。总状花序顶生，兼有腋生，密生2 ~ 20朵花，花冠淡蓝色或淡紫色，小苞片与花萼均被黄褐色长糙毛。荚果长圆形，无毛，外面包围宿等，有种子10~15粒。', '多生长于向阳山坡、林缘.短草丛中及裸岩旁。', '夏、秋季采收，洗净，鲜用或干燥。', '87_291', '(1)急性扁桃体炎:介狗铃、白鸡脚橙、白鸡聆各适量。米汤浸泡炖肉服。\r\n 2) 疔:介狗铃适量，捣烂外敷。', '', '', null, null, '8', null, null, null, null, '野百合.jpg', 'yebaihe;ybh;jiegouling;jgl;yinggushanhuasheng;ygshs;mailiqi;mlq;daxiangling;dxl', '2018-07-20 14:27:18', null, '重复', null, '0');
