INSERT INTO `medicine` VALUES ('356', '野百合', '介狗铃;硬骨山花生;麦粒齐;大响铃', '阳药', '1年生草本。茎直立，基部有时木质化，被黄褐色丝质长糙毛。单叶互生，叶片线形或披针形，有时长圆形，先端急尖，基部略狭窄成短柄至无柄，下面密被绢毛。总状花序顶生，兼有腋生，密生2 ~ 20朵花，花冠淡蓝色或淡紫色，小苞片与花萼均被黄褐色长糙毛。荚果长圆形，无毛，外面包围宿等，有种子10~15粒。', '多生长于向阳山坡、林缘.短草丛中及裸岩旁。', '夏、秋季采收，洗净，鲜用或干燥。', '87_291', '(1)急性扁桃体炎:介狗铃、白鸡脚橙、白鸡聆各适量。米汤浸泡炖肉服。\r\n(2) 疔:介狗铃适量，捣烂外敷。', '', '', null, null, '8', null, null, null, null, '野百合.jpg', 'yebaihe;ybh;jiegouling;jgl;yinggushanhuasheng;ygshs;mailiqi;mlq;daxiangling;dxl', '2018-07-20 14:29:56', null, null, null, '1');
INSERT INTO `medicine` VALUES ('357', '雀麦', '脚麦', '阳药', '1年或2年生草本。秆直立,丛生。叶鞘紧密抱茎,被白色柔毛;叶舌透明,膜质,长1.5~2mm,先端有不规则的裂齿;叶片长5 ~ 30cm,宽2 ~ 8mm,两面有毛或有时下面无毛。 圆锥花序开展, 下垂,长达30cm,每个节具3 ~ 7个分枝,每个分枝上部着生1 - 4个小穂;小穂幼时圆筒形,成熟后压扁,含7 ~ 14朵小花; 颖披针形,边缘膜质,第1颖长5 ~ 8mm,具3 ~ 5脉,第2颖长7 ~ 10mm,具7 ~ 9 脉;外稃卵圆形,边缘膜质,具7~9脉, 顶端微2裂,芒自其下2mm伸出,第1外稃长8~11mm;内桴较狭,短于外稃,背上疏生刺毛;花药长1 ~ 1.5mm。颖果压扁,长约7mm。', '多生长于山坡、路旁及草丛中。', '春、初夏季采收,洗净,鲜用或干燥。', '440', '内服煎汤20~50g。', '', '', null, null, '8', null, null, null, null, '', 'quemai;qm;jiaomai;jm', '2018-07-21 20:27:47', null, null, null, '1');
INSERT INTO `medicine` VALUES ('358', '菩提子', '介狗珠;米珠;佛珠草', '阴药', '多年生草本。秆多分枝。叶鞘光滑,上部者短于节间;叶舌质硬;叶片长而宽,线状披针形,长达30cm,宽1 ~ 4cm,先端渐尖,基部近心形,中脉粗厚而于下面凸起。总状花序腋生成束,长5~ 10cm,具总;雌小穂长7 ~ 10mm;总苞硬骨质,念珠状,圆球形,光滑,约与小穂等长;第1颖具10数脉,第2颖舟形;第1外稃略短于颖,内祥缺;第2外祥稍短于第1外祥, 具3脉，內稃与外稃相似而较小,具3枚退化雄蕊;雌蕊具长花柱,柱头分离;颖果极小;无柄小雄蕊长6~8m;颖草质,第1颖扁平, 两側内折或成嵴而具不等宽之翼,先端钝,具多数脉,第2颖舟形,具多脉：外稃与内祥均为膜质; 雄蕊3, 花药黄褐色, 有柄雄小蕊与无柄者相似,较小或退化。颖果外包坚硬的总苞。', '原栽培,現多逸为野生', '秋季采收,洗净,鲜用或干燥', '175_370', '内服煎汤10~ 30g。', '', '', null, null, '8', null, null, null, null, '', 'putizi;ptz;jiegouzhu;jgz;mizhu;mz;fozhucao;fzc', '2018-07-21 20:30:29', null, null, null, '1');
INSERT INTO `medicine` VALUES ('359', '条叶榕', '小康补，小香勾', '阳药', '落叶小灌木。小枝、叶柄幼时被白色短柔毛后变无毛。叶片厚纸质，狭披针形或线状披针形，先端渐尖，基部圆形至宽楔形。隐花果单身叶腋，椭圆形或球形，顶端脐部有明显突起', '山坡路边。溪沟边或林下阴湿处', '秋季采收，洗净，切段，鲜用或干燥', '8_104_185_309', '消化不良性腹泻：小康补30g，煎水100ml内服\r\n小儿疳积：小康补100g，加水500ml与鹌鹑蛋一起煮，每日食蛋4~8个，每次2~4个\r\n小儿疝气：小康补根及茎30~50g，赤木丹根20~30g，加猪蹄、桂圆、荔枝和大枣适量，水煎服', '', '', null, null, '7', null, null, null, null, '', 'tiaoyerong;tyr;xiaokangbu，xiaoxianggou;xkb，xxg', '2018-07-20 18:36:30', null, null, null, '1');
INSERT INTO `medicine` VALUES ('360', '全叶榕', '小香勾，小叶牛奶绳', '阳药', '落叶小灌木。小枝、叶柄幼时被白色短柔毛后变无毛。叶片纸质，狭卵形或倒披针形，先端渐尖，基部圆形至宽楔形，隐花果单身叶腋，椭圆形或近球形，顶端脐部突起不明显', '山坡路边。溪沟边或林下阴湿处', '秋季采收，洗净，切段，鲜用或干燥', '8_104_185', '消化不良性腹泻：小香勾30g，煎水100ml内服\r\n小儿疳积：小香勾100g，加水500ml与鹌鹑蛋一起煮，每日食蛋4~8个，每次2~4个\r\n小儿疝气：小先香勾根及茎30~50g，赤木丹根20~30g，加猪蹄、桂圆、荔枝和大枣适量，水煎服', '', '', null, null, '7', null, null, null, null, '', 'quanyerong;qyr;xiaoxianggou，xiaoyeniunaisheng;xxg，xynns', '2018-07-20 18:37:52', null, null, null, '1');
INSERT INTO `medicine` VALUES ('361', '水蜈蚣', '苦香子;一粒雪;细竹草', '阴药', '多年生草本，具匍匐根状茎。秆散生，纤细扁三棱形，下部具叶。叶长于\r\n秆或等长；叶片线形，宽1.5 ~ 3mm，前缘和背面上部中脉上稍粗糙，最下1 ~ 2片为无叶片的叶鞘；叶鞘通常淡紫红色，鞘口斜形。苞片通常3,叶状，开展；穗状花序单一，近球形或卵球形，淡绿色，密生多数小穗；小穗基部具关节，长椭圆形或长圆状披针形，长约3mm,宽约1mm,先端稍钝，具1两性花；鳞片卵形，膜质，在小穗下部的较短，淡绿色，具5~7脉，先端由中肋延伸成外弯的突尖，背面龙骨状突起上具数个白色透明的刺，两面常具锈色斑点；雄蕊3；花丝细长，柱头2。小坚果倒卵形状长圆形，褐色。', '多生长于山坡水沟边或潮湿的地方。', '夏、秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'shuiwugong;swg;kuxiangzi;kxz;yilixue;ylx;xizhucao;xzc', '2018-07-21 00:46:30', null, null, null, '1');
INSERT INTO `medicine` VALUES ('362', '棕榈', '棕树', '阳药', '常绿乔木。茎不分枝，残留的褐色纤维状老叶鞘层层包被于茎杆上，脱\r\n落后呈环状的节。叶片圆扇形，直径50 ~100cm，掌状深裂，裂片30~45枚，线状披针形，先端具2浅裂，硬直或老时常下垂，中脉明显突起，上面深绿色有光泽，下面微被白粉；叶柄坚硬，长50~ 100cm，具3棱，基部扩大成抱茎的鞘。肉穗花序圆锥状；佛焰苞革质，多数，被锈色绒毛；花小，淡黄色，单性，雌雄异株；萼片和花瓣均宽卵形；雄蕊花丝分离；心皮合生，子房3室，密被白色柔毛，柱头3。核果肾状球形，直径约1cm，成熟时黑色。', '多生长于山坡疏林中。有栽培。', '秋天采收果实；春未夏初采收花，均鲜用或千燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'zonglv;zl;zongshu;zs', '2018-07-21 00:45:28', null, null, null, '1');
INSERT INTO `medicine` VALUES ('363', '菖蒲', '水菖蒲', '阴药', '多年生常绿草本。根状茎粗大，直径0.5~2.5cm。叶鞘两侧的膜质部分宽4~5mm;叶片剑状线形，长90 ~ 15cm，宽1 ~ 3cm，两面均具明显隆起的中肋。总花梗长15 ~ 50cm；叶状佛焰苞剑状线形，长20〜40cm；肉穗花序锥状长圆柱形，长4~9cm,直径6~20mm;花黄绿色，花被长约2.5mm,宽1mm;子房长圆柱形，长3mm。浆果红色，长圆形。', '多生长于水边、沼泽湿地。有栽培.', '深秋或冬初采收，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'changpu;cp;shuichangpu;scp', '2018-07-21 00:44:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('364', '金钱蒲', '坑仙;小菖蒲', '阴药', '多年生常绿草本。根状茎直径2 ~5mm,节间长1 ~	5mm。叶销两侧的膜质部分宽2 ~ 3mm：叶片线形，长3〜20cm，宽6mm以下：先端长渐尖，无\r\n中肋，平行脉多数。总花梗长2.5 ~ 15cm；叶状佛焰苞片长3~9cm，通常短于至等长于肉穗花序；肉穗花序圆柱形，长3 ~ 9cm，直径3 ~ 5mm。果黄绿色。', '多生长于水沟边的岩石缝中或乱石中。', '深秋或冬初采收，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'jinqianpu;jqp;kengxian;kx;xiaochangpu;xcp', '2018-07-21 00:45:59', null, null, null, '1');
INSERT INTO `medicine` VALUES ('365', '石菖蒲', '坑韭;坑仙;坑香', '阴药', '多年生常绿草本。根状茎直径5 ~ 8mm，节间长3 ― 5mm。叶鞘两侧的膜质部分宽2 ~ 5mm；叶片线形，长10 ~ 40cm.宽 7 ~ 13mm；无中肋，平行\r\n脉多数。总花梗长4 ~ 15cm；叶状佛焰苞片长13 — 25cm，通常为肉穗花序的2倍以上；肉穗花序圆柱状，长2.5 ~ 10cm，直径3 ~ 7mm；花白色。果成熟时黄绿色或黄白色。', '多生长于溪沟石上。', '深秋或冬初采收，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'shichangpu;scp;kengjiu;kj;kengxian;kx;kengxiang;kx', '2018-07-21 00:44:43', null, null, null, '1');
INSERT INTO `medicine` VALUES ('366', '华东魔芋', '星菜;蛇公卵', '阴药', '多年生草本。块茎扁球形，直径3 ~ 20cm。鳞叶2片，卵形或披针状\r\n卵形，有青紫色、淡红色斑块。叶柄粗壮，绿色，具白色斑块，光滑；叶片绿色，掌状3全裂，裂片长达5cm，每裂片2歧分叉后再羽状深裂，小裂片通常8 ~ 30,狭卵形或卵形，长4 ~ 10cm，宽3 ~ 3.5cm。总花梗长25~45cm；佛焰苞长15 ~ 20cm，管部弯卷，长6 ~ 8cm，宽1 ~2cm,外面绿色，具白色斑纹，内面暗青紫色，基部有疣状突起，檐部展开为斜漏斗状，长I2 ~ 15cm以，外面淡绿色，内面淡红色，边缘带杂色，两面均有白色圆形斑块，先端长渐尖；肉穗花序圆柱形；雄花部分长3~4cm,雌花部分长2~4cm；附属物长圆锥形，长7 ~ 14cm，约与花序着花部分等长，深青紫色，散生长约1cm的紫黑色硬毛；雄蕊3 ~ 4,花药隔外凸；子房球形，无花柱，柱头盘状。浆果球形或扁球形，红色，成熟时呈蓝色。', '多生长于山地林下或灌木丛中。有栽培。', '深秋或冬初采收块茎；夏\r\n季或初秋采收茎叶；洗净，均鲜用或干燥。', '1', '暂无', '', '', null, null, '1', null, null, null, null, '', 'huadongmoyu;hdmy;xingcai;xc;shegongluan;sgl', '2018-07-21 00:44:07', null, null, null, '1');
INSERT INTO `medicine` VALUES ('367', '异叶天南星', '野蛇公卵', '阴药', '多年生草本。块茎近球形，上部扁平，常具侧生小块茎。鳞叶4 ~ 5,膜质。叶单1 ；叶柄圆柱形，长25 ~50cm，下部3/4鞘状；叶片鸟足状分裂，裂片7 ~ 19,倒披针形、长圆形、线状长圆形，先端渐尖，基部楔形，全缘，无柄或具短柄,侧裂片长7 ~ 22cm，宽2 ~ 6cm，中裂片比两侧短小。总花梗常短于叶柄；佛焰苞管部长3 ~ 8〇1〇,绿色，喉部截形，边缘稍反卷，檐部卵形或卵状披针形，常下弯呈盔状，淡绿色至淡黄色；肉穗花序轴与佛焰苞完全分离；肉穗花序有两性和单性雄花序2种；两性花序下部雌花序长约花密，上部雄花序长约3cm,花疏；单性雄花序长3 ~ 5cm，粗3 ~ 5mm，具柄；附属物绿白色，细长，长10 ~ 20cm，基部直径0.5 ~ 1cm，伸出佛焰苞外呈“之”字形上升。种子黄色，具红色斑点。', '多生长于山坡林下、灌木丛或草地。', '深秋或冬初采收，洗净，鲜用或干燥。', '1', '有毒。内服需先煎2小时', '', '', null, null, '8', null, null, null, null, '', 'yiyetiannanxing;yytnx;yeshegongluan;ysgl', '2018-07-21 00:42:36', null, null, null, '1');
INSERT INTO `medicine` VALUES ('368', '滴水珠', '岩芋;红坛芋', '阴药', '多年生草本。块茎球形、卵球形或长圆形。叶1；叶柄长8~25cm，绿\r\n色带紫斑或紫色，几无鞘，在中部以下生1珠芽；叶片长圆状卵形、长三角状卵形或心状戟形，幼叶长达1 ~ 5cm，宽0.8 ~ 2cm，多年后长可达10cm，宽可达8cm，先端长渐尖或有时呈尾尖状，基部深心形，常在弯\r\n曲处上面有1珠芽，上面绿色，下面淡或红紫色，全缘。总花梗短于叶柄，佛焰苞绿色、淡黄、紫色或青紫色，长2 ~ 7cm,管部长1.2 ~2cm，粗4 ~ 7mm，沿部椭圆形，长1.8 ~ 4.5cm；肉穗花序雄花部分长5 ~ 7mm，雌花部分长1 ~1.2cm,附属物绿色，常弯曲呈“之”字形上升。浆果长圆状卵形。', '多生长于山地溪沟边、潮 湿草地、岩石旁、岩隙中或岩壁上。', '夏、秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'dishuizhu;dsz;yanyu;yy;hongtanyu;hty', '2018-07-21 00:42:05', null, null, null, '1');
INSERT INTO `medicine` VALUES ('369', '谷精草', '稻栅;谷净草;耳朵刷;满田星', '阴药', '1年生草本，呈莲座状。叶基生；叶片线状披针形，长6 ~ 20cm，宽3 ~ 6mm，有横脉。头状花序球形，直径4 ~ 6mm；总苞片倒卵形或近圆形，麦秸黄色，背面上部被白色棒状毛；苞片倒卵形，先端骤尖，上部密生白色短毛。花序托具长柔毛。雄花萼片3,合生呈先端具3圆齿的佛焰苞状，花瓣3,合生呈上部3浅裂的高脚杯状，雄蕊6；雌花萼片3，合生叶佛焰苞状，先\r\n端3裂，花瓣3,离生，棍棒状，近先端有一黑色腺体，具细长毛，子房3室，柱头3。种子长椭圆形。', '多生长于稻田边阴湿处及 水沟边。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'gujingcao;gjc;daozha;dz;gujingcao;gjc;erduoshua;eds;mantianxing;mtx', '2018-07-21 00:40:05', null, null, null, '1');
INSERT INTO `medicine` VALUES ('370', '鸭跖草', '鸦雀草;百日晒;石桃砸;竹叶草', '阴药', '1年生草本。茎上部直立，下部匍匐，多分枝。单叶互生；叶片卵形至\r\n披针形，长3~10cm，宽1 ~ 2cm，先端急尖至渐尖，基部宽禊形，两面无毛或上面边缘处微粗糙，无柄或几无柄；叶鞘近膜质，紧密抱茎，散生紫色斑点，鞘口有长睫毛。聚伞花序单生于主茎或分枝的顶端；总苞片佛焰茵状，心状卵形，长1 ~ 2cm，折叠，边缘分离；萼片白色，狭卵形，长约5mm;花瓣卵形，后方的2枚较大，蓝色，有长爪，长1 ~ 1.5cm，前方的1枚较小，白色，无爪；发育雄蕊2 ~ 3,位于前方，退化雄蕊3 ~ 4,位于后方；子房2室，每室2颗胚珠。蒴果圆锥形，2瓣裂。种子近肾形，有不规则的窝孔。', '多生长于田边、路边或山 坡潮湿处。', '夏、秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'yazhicao;yzc;yaquecao;yqc;bairishai;brs;shitaoza;stz;zhuyecao;zyc', '2018-07-21 00:39:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('371', '灯心草', '水灯草;水灯心;草席草', '阴药', '多年生草本。根状茎横走。茎簇生，圆柱形，粗1.5~4mm，具多数细\r\n纵棱。叶基生或近基生；叶片大多退化殆尽；叶鞘中部以下紫褐色至黑褐色；叶耳缺。复聚伞花序假侧生，通常较密集；总苞片似茎和延伸，直立，长5 ~ 20cm；先出叶宽卵形，长约0.5mm，膜质；花被片披针形或卵状披\r\n针形，外轮的长2 ~ 2.5mm，内轮的有时稍短，边缘膜质；雄蕊3,稀6室，花药短于花丝；子房3室。蒴果三棱状椭圆形，成熟时稍长于花被片，顶端钝或微凹。种子黄褐色，椭圆形，长约0.5mm，无附属物。', '多生长于沟边、田边及路 边潮湿处。有栽培。', '秋季采收，洗净，顺茎划开皮部，剥取髓，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'dengxincao;dxc;shuidengcao;sdc;shuidengxin;sdx;caoxicao;cxc', '2018-07-21 00:38:56', null, null, null, '1');
INSERT INTO `medicine` VALUES ('372', '野灯心草', '水便草;水灯心草', '阴药', '多年生草本。根状茎横走。茎簇生，圆柱形，粗0.8~1.5mm，具细纵棱。\r\n叶基生或近基生；叶片大多退化呈芒刺状；叶鞘中部以下紫褐色至黑褐色；叶耳缺。复聚伞花序假侧生，通常较开展；总苞片似茎和延伸，直立，长5 ~ 15cm；先出叶三角形，长0.5〜0.8mm，膜质；花被片卵状披针形，\r\n近等长，长2 ~ 2.5mm，边缘膜质；雄蕊3,长约为花被片的2/3，花药稍短于花丝；子房不完全3室。蒴果三棱状卵球形，成熟时稍长于花被片，顶端钝。种子黄褐色，倒卵形，长约0.5mm，无附属物。', '多生长于沟边及路边潮  湿处。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'yedengxincao;ydxc;shuibiancao;sbc;shuidengxincao;sdxc', '2018-07-21 00:49:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('373', '粉条儿菜', '金丝吊白米;竹米根', '阳药', '多年生草本。根状茎粗短，有时略呈块茎状；根纤细，多分枝，具膨大\r\n米粒状的根毛。叶基生密集成丛，无柄，叶片宽线形，长10~ 25cm，宽3 ~ 4mm，上部有时稍弯斜，中部以下有时对折，具3脉。花葶粗壮，高30 ~ 60cm，直径1.5 ~ 3mm，其上部具数枚自下而上渐次变小的无花苞片；总状花序长8 ~ 20cm，有花15 ~ 50余朵，花轴密被柔毛；小苞片线形，稍长于花梗，位于花梗的近基部；花被片密被柔毛，中部以下合生，共被筒与子房贴生，裂片披针形，长3 ~ 3.5mm，膜质，上部淡红色，具1绿色的中脉；雄蕊着生于花被裂片的基部，花丝短，花药椭圆形；花柱圆柱形，柱头微3裂。蒴果倒卵形或倒圆锥形，有棱角，密被柔毛。', '多生长于山坡林缘或路边  草地。', '秋季采收，洗净，鲜用或干燥.', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'fentiaoercai;ftec;jinsidiaobaimi;jsdbm;zhumigen;zmg', '2018-07-21 00:55:17', null, null, null, '1');
INSERT INTO `medicine` VALUES ('374', '蜘蛛抱蛋', '单张箸', '阳药', '多年生常绿草本。根状茎横走，节上有覆瓦状的鳞片。叶单，生于根状茎的各节，彼此相距1 ~ 3cm；叶片近革质,近椭圆形或长圆状披针形，长可达80〇！11cm，宽可达11^，先端急尖，基部禊形，两面绿色，有时稍具黄白色的斑点或条纹；叶柄粗壮，长5 ~ 35cm。总花梗长0.5 ~ 2cm,苞片膜质，有3 ~ 4枚，其中2枚紧贴于花的基部；花紫色，肉质，钟状，长12 ~ 18mm,直径1 ~ 1.5cm；花被片8,稀6室，中部以下合生，裂片近三角形，开展或外弯，先端钝，内面有4条明显、肉质、光滑的峭状隆起；雄蕊着生于花被筒的基部，花丝短，花药椭圆形；子房4室，稀3室，花柱无关节，柱头裂片先端微凹，边缘常向上反卷。浆果卵球形，含种子1粒。', '栽培。', '夏、秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'zhizhubaodan;zzbd;danzhangzhu;dzz', '2018-07-21 01:09:49', null, null, null, '1');
INSERT INTO `medicine` VALUES ('375', '萱草', '金针根', '阴药', '多年生草本。根状茎极短，不明显，稍肉质，其中一部分顶端嘭大呈纺\r\n锤形。叶基生，2列；叶片宽线形至线状披针形，长40~80cm，宽 1.5-3.5cm，通\r\n常鲜绿色。花葶高可达1.2m，其上具少数无花的苞片；蝎尾状聚伞花序复组成圆锥状.\r\n具花6 ~ 12朵或更多；苞片卵状披针形；花橘红色或橘黄色，无香味，具短花梗；花\r\n被长7~12cm，下部2 ~3cm 合生成花被管；外轮花被裂片3,长圆形，宽达2.5cm1，\r\n具分枝的脉，中部具褐红色的色带，边缘波状皱褶，盛开时裂片反曲；雄蕊伸出，上弯\r\n比花丝裂片短；花柱伸出，上弯，比雄蕊长。蒴果长圆形，具钝3棱。种子黑色，有棱角。', '多生长于山坡林下或沟边 阴湿处。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'xuancao;xc;jinzhengen;jzg', '2018-07-21 01:13:16', null, null, null, '1');
INSERT INTO `medicine` VALUES ('376', '玉簪', '白玉簪', '阳药', '多年生草本。根状茎极粗短。叶基生；叶片卵状心形、卵圆形或卵形，\r\n长14〜24cm，宽8 ~16cm,先端短渐尖，基部心形或圆形，侧脉6 ~ 10对；叶柄长20 ~ 40cm。花葶高 40 ~80cm，其上具 1 ~ 3枚无毛的苞片；总状花序具花数朵至十余朵花；苞片膜质，白色，卵形或披针形，长2.5 ~ 7cm，宽 1 ~ 1.5cm；花白色，芳香，长10~13cm，单生或2 ~ 3朵簇生于苞片内；\r\n小苞片微小；花梗长约1cm；花被裂片长椭圆形，长3.5~4cm，宽1.2cm；雄蕊下部与花被筒贴生，与花被近等长。蒴果近圆柱形，具3棱。', '栽培。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'yuzan;yz;baiyuzan;byz', '2018-07-21 01:19:48', null, null, null, '1');
INSERT INTO `medicine` VALUES ('377', '紫萼', '山槲菜;山玉簪花', '阳药', '多年生草本。根状茎极粗短。叶基生；叶片卵状心形、卵圆形或卵形，长\r\n6 ~ 18cm，宽3 ~ 14cm，先端近短尾状或骤尖，基部心形、圆形或近截形，侧脉7 ~ 11对；叶柄长6 ~ 25cm。花葶高30 ~ 60cm，其上具1~2枚无花的苞片；总状花序具花10~30朵；苞片膜质，白色，长圆状披针形、长1 ~ 2cm，宽4 ~ 6mm；花淡紫色，无香味，长4~6cm，单生于苞片内；小苞片微小；花梗长7 ~ 10mm；花被裂片长椭圆形，长1.5 ~ 1.8cm，宽8mm；雄蕊着生于花被筒基部，稍伸出花被外。蒴果近圆柱形，先端具细尖。种子黑色。', '多生长于山坡林下、林缘或草丛中。', '夏、秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'zie;ze;shanhucai;shc;shanyuzanhua;syzh', '2018-07-21 01:24:28', null, null, null, '1');
INSERT INTO `medicine` VALUES ('378', '野百合', '百合花头;百合', '阳药', '多年生草本。鳞茎近球形；鳞片披针形。茎带紫色，有排列成纵行的小\r\n乳头状突起。叶互生；叶片线状披针形至披针形，长7 ~ 15cm，宽6 ~ 15mm，向上稍变小但不呈苞片状，基部渐狭成柄状，边缘有小乳头状突起。花单生或数朵排列，成顶生近伞房状花序，乳白色。喇叭形，稍下\r\n垂；叶状甚片披针形；花梗长3~10cm，中部有1枚小苞片；花被片倒卵状披针形，长13 ~ 18cm，宽3 ~ 4cm，背面稍带紫色，内面无斑点，上部张开或先端外弯但不反卷，密腺两侧有小乳头新衣突起；花丝长约1.5cm，背着；花柱长10~12cm。蒴果长圆形。', '多生长于山坡林缘、路边、 溪沟边。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'yebaihe;ybh;baihehuatou;bhht;baihe;bh', '2018-07-21 01:29:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('379', '山麦冬', '山麦冬', '阳药', '多年生草本。根近末端处常膨大成长圆形、椭圆形或纺锤肉质小块山麦冬\r\n根。叶基生，无柄；叶片革质，宽线形长15 ~ 40cm，宽 4 〜10mm，具 5 脉，中脉明显.边缘具细锯齿；叶鞘边缘膜质。花葶直立.高15 ~ 30cm，总状花序顶生，长达12cm.有花多数，常1 ~ 5朵簇生于苞片，花被黄白色或稍带紫色，长圆形或针形，花梗长2 ~ 4mm；子房上位。浆果球形，成熟\r\n时蓝黑色。', '多生长于山坡林下或路边 草地。有作花卉栽培。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'shanmaidong;smd;shanmaidong;smd', '2018-07-21 01:32:33', null, null, null, '1');
INSERT INTO `medicine` VALUES ('380', '麦冬', '山韭菜', '阳药', '多年生草本。根中部或近末端处常膨大呈椭圆形或纺锤形的小块根。叶基生；无柄；叶片线形，长15 ~ 50cm，宽1~ 4cm；叶鞘膜质，白色至褐色。花葶远短于叶；总状花序，长2 ~ 7cm，稍下弯；苞片披针形，下部的长于花梗；花紫色或淡紫色，1~2朵簇生于苞片内；花被片披针形，先端尖；雄蕊着生于花被片的基部，花丝不明显；花柱基部稍宽，高出雄蕊。浆果圆球形，成熟暗蓝色。', '多生长于山坡阴湿或沟边 草地。有栽培。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'maidong;md;shanjiucai;sjc', '2018-07-21 01:36:01', null, null, null, '1');
INSERT INTO `medicine` VALUES ('381', '华重楼', '金烛台;七层塔;七叶一枝花', '阴药', '多年生草本。根状茎粗壮，稍扁，不等粗，密生环节。茎常带紫红色或\r\n青紫色，基部有膜质鞘。叶通常6~ 8枚轮生于茎端；叶片长圆形、倒卵状椭圆形或倒卵状长圆形，长7 ~ 20cm，宽2.5 ~ 8cm，先端渐尖或短尾状，基部圆钝或宽楔形，具长0.5~3cm的叶柄。花被片每轮4 ~ 7枚，外轮花被片叶状，绿色，开展，内轮花被片宽线形，上部较宽，远短于外轮花被片；雄\r\n蕊基部稍合生，花丝长4 ~ 7mm，下部稍扃平；子房具棱，4 ~ 7室，花柱分枝4 ~ 7。种子具红色肉质的外种皮。', '多生长于山坡阴湿处或沟 边草丛中。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无 ', '', '', null, null, '8', null, null, null, null, '', 'huazhonglou;hzl;jinzhutai;jzt;qicengta;qct;qiyeyizhihua;qyyzh', '2018-07-21 01:39:34', null, null, null, '1');
INSERT INTO `medicine` VALUES ('382', '多黄花精', '千年运;山姜;九蒸姜;黄精', '阴药', '多年生草本。根状茎连珠状。叶互生；叶片椭圆形至长圆状披针形，\r\n长8~20cm，宽3 ~ 8cm，先端急尖至渐尖，基部圆钝，两面无毛。伞形花序常具2~7朵花，下弯；总花梗长7〜15mm；花绿白色，近圆筒形，长15〜20mm；花梗长7 ~ 15mm；花被筒基部收缩成短柄状，裂片宽卵形，长\r\n约3mm；雄蕊着生于花被筒的中部，花丝稍侧扃，被短绵毛，花药长圆形；花柱不伸出花被之外。浆果成熟时黑色。种子3 ~ 14粒。', '多生长于山坡的林下阴湿 处或沟边。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'duohuanghuajing;dhhj;qiannianyun;qny;shanjiang;sj;jiuzhengjiang;jzj;huangjing;hj', '2018-07-21 01:44:10', null, null, null, '1');
INSERT INTO `medicine` VALUES ('383', '长梗黄精', '千年运;山姜;九蒸姜;黄精', '阴药', '多年生草本。根状茎结节状。叶互生；叶片椭圆形至长圆形，长6 ~ 15cm，\r\n宽2~7cm，先端急尖，基部圆钝，上面无毛，下面脉上有短毛。伞形花序或伞房常具2~4朵花，稀更多，下垂；总花梗细丝状，长2.5 ~ 13mm；花绿白色，近圆筒形，长I5 ~ 20mm；花梗长 5 ~ 25mm；花被筒基部收缩成短柄状，裂片宽卵状三角形，长约4mm；雄蕊着生于花被筒的中部，花丝被短\r\n绵毛，花药长圆形；花柱稍伸出花被之外。浆果成熟时黑色。种子2~5粒。', '多生长于山坡的林下或灌 草丛中。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'changgenghuangjing;cghj;qiannianyun;qny;shanjiang;sj;jiuzhengjiang;jzj;huangjing;hj', '2018-07-21 01:49:48', null, null, null, '1');
INSERT INTO `medicine` VALUES ('384', '菝葜', '金刚刺;白兰刺;告告刺', '阳药', '攀援状灌木。根状茎粗壮，坚硬，表面有刺。茎具疏刺。叶片厚纸质或\r\n薄革质，近圆形、卵形或椭圆形，长3 ~ 10cm，宽1.5~8cm,萌发枝上的叶长和宽可达16cm和12cm，先端凸尖或骤尖，基部宽楔形或圆形，有时微心形，具3~7主脉;叶柄长7 ~ 25mm，具卷须，翅状鞘线状披针形或披针形，长为叶柄的1/2 ~ 4/5,狭于叶柄，几全部与叶柄合生，脱落点在于卷\r\n须的着生点。伞形花序叶尚幼嫩的小枝上，具多花，总花梗长15 ~ 30mm；花序托膨大；小苞片宿存；花黄绿色；雄花花被片长3.5 ~ 4.5mm，雄蕊6，花药长圆形；雌花与雄花大小相似，具6枚退化雄蕊。浆果直径6 ~15mm，成熟时红色，有粉霜。', '多生长于山坡的林下或灌 木丛中。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'baqia;bq;jingangci;jgc;bailanci;blc;gaogaoci;ggc', '2018-07-21 01:55:10', null, null, null, '1');
INSERT INTO `medicine` VALUES ('385', '菝葜', '金刚刺;白兰刺;告告刺', '阳药', '攀援状灌木。根状茎粗壮，坚硬，表面有刺。茎具疏刺。叶片厚纸质或\r\n薄革质，近圆形、卵形或椭圆形，长3 ~ 10cm，宽1.5~8cm,萌发枝上的叶长和宽可达16cm和12cm，先端凸尖或骤尖，基部宽楔形或圆形，有时微心形，具3~7主脉;叶柄长7 ~ 25mm，具卷须，翅状鞘线状披针形或披针形，长为叶柄的1/2 ~ 4/5,狭于叶柄，几全部与叶柄合生，脱落点在于卷\r\n须的着生点。伞形花序叶尚幼嫩的小枝上，具多花，总花梗长15 ~ 30mm；花序托膨大；小苞片宿存；花黄绿色；雄花花被片长3.5 ~ 4.5mm，雄蕊6，花药长圆形；雌花与雄花大小相似，具6枚退化雄蕊。浆果直径6 ~15mm，成熟时红色，有粉霜。', '多生长于山坡的林下或灌 木丛中。', '秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'baqia;bq;jingangci;jgc;bailanci;blc;gaogaoci;ggc', '2018-07-21 01:55:54', null, null, null, '1');
INSERT INTO `medicine` VALUES ('386', '牛筋草', '千斤拔;牛顿草;千人拔', '阳药', '1年生草本。秆丛生。叶鞘压扁,具崎,鞘口常有柔毛;叶舌长约1mm;叶片病平或卷折,长达15cm,宽3~5mm,无毛或有疣基的柔毛。穂状花序长3 ~ 10cm,宽3 ~5mm ，2至数枚指状排列于秆頂,有时其中1枝或2枚可生于其他花序之下;小穂长4 ~ 7mm,宽2 ~ 3mm, 含3~6朵小花,颖披针形, 嵴上粗糙,第1颖长约2mm,第2颖长约3mm;第1颖外稃长3~3.5mm,脊上具狭翼;内稃短于外稃, 嵴青上具小纤毛。种子卵形,长约1.5mm,有明显的波状皱纹。', '多生长于山坡、路旁及田野草丛中 。', '秋季采收,洗净,鲜用或干燥。', '196_306', '内服煎汤9 ~ 30g。', '', '', null, null, '8', null, null, null, null, '', 'niujincao;njc;qianjinba;qjb;niuduncao;ndc;qianrenba;qrb', '2018-07-21 20:27:13', null, null, null, '1');
INSERT INTO `medicine` VALUES ('387', '假俭草', '马鞭草', '阳药', '多年生草本。具贴地而生的横走匍匐茎。叶鞘扁平,多密集跨生于秆基,鞘口具短毛;叶片扁平,先端钝,无毛, 长3 ~ 15cm,宽2 ~ 6mm,顶生者退化。总花序直立或稍镰刀状弯曲,长4 ~ 6cm,宽约2mm;穂轴节间压扁,略呈棍棒状,长2~3mm;无柄小穂长圆形,长约4mm;第1颖与小穂等长,具5 ~ 7脉,嵴之下部具箆齿状短刺,上部具宽翼,第2颖略呈舟形, 厚膜质,具3脉,第1外稃长圆形,先端尖,几等长于颖,内稃等长于外稃而较窄;第2外稃短于第1外桴,先端钝;有柄小穂仅存的柄扁平锥形,长3 ~ 4mm。', '多生长于山坡、路旁、田边及草地 ', '夏、秋季采收,洗净,鲜用或干燥', '112', '内服煎汤30~100g。', '', '', null, null, '8', null, null, null, null, '', 'jiajiancao;jjc;mabiancao;mbc', '2018-07-21 20:26:18', null, null, null, '1');
INSERT INTO `medicine` VALUES ('388', '白茅', '毛筋草;毛根;黄茅草;茅草根', '阳药', '多年生草本。根茎密生鳞片,匍匐横走。秆丛生,直立,高20~90cm,具2 ~ 3节,圆柱形,节上具5 ~ 10mm柔毛。叶鞘无毛, 基部多数老叶常破碎呈纤维状; 叶舌干膜质,叶线形或线状披针形,先端渐尖,基部渐狭,下面及边缘粗糙,主脉在下面明显突出而渐向基部变粗而质硬。 圆锥花序圆柱状,分枝短缩密集,花序基部有时较疏松或间断; 小穂披针形或长圆形, 长约4mm,基盘及小穂柄均密生长10~ 15mm的丝状柔毛;第1颖较狭,具3 ~ 4脉,第2颖较宽,具4 ~ 6脉;第1外祥卵状长圆形,长1.5mm;第2外祥披针形,长1.2mm,宽约1.5mm;雄蕊2枝,花药黄色,长约3mm,雌蕊1,具较长的花柱,柱头羽毛状,颖果椭圆形,暗褐色,成熟的果序被白色长柔毛。', '多生长于山城、向阳干草地、路旁、田边及旷野荒草丛中。', '春或深秋采挖根茎,除去地上部分及鱗叶,洗净,扎把晒干或鲜用; 春季花将开放时采收花穂, 晒干 。', '158_196_288_350_365_375_405', '内服煎汤10~ 120g。', '', '', null, null, '8', null, null, null, null, '', 'baimao;bm;maojincao;mjc;maogen;mg;huangmaocao;hmc;maocaogen;mcg', '2018-07-21 20:25:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('389', '淡竹叶', '竹叶草;竹米;淡竹米', '阴药', '多年生草本,具木质短缩的根头。须根稀疏,中部可膨大呈纺锤形。秆少数丛生,直立,光滑,具5 ~6节。叶鞘光滑或一边缘具纤毛; 叶舌短小,质硬.  长0.5~ lmm;  叶片披针形,  长5 ~ 20cm,宽2 ~4cm,基部狭缩成柄,无毛或两面均有柔毛或小刺状疣毛 。 圆锥花序长l0~ 40cm;分枝长5 ~ 13cm,斜生或展开;小穗在花技分枝上排列疏散,长7~l2mm,宽l ~2mm；颖先瑞钝,通常具5脉,边缘膜质.第1 颖长3 ~ 4.5mm, 第2颖长4 ~ 5mm第1外稃长6 ~ 7mm, 宽3mm,先端具短尖头,内1手較短,其后有长3 ~ 4mm小穂轴节间,不育外稃自下而上逐渐狭小,先端各具长1~ 2mm短芒。 颖果纺锤形, 深褐色 。', '多生长于山坡、路旁树荫下或荫蔽处', '夏、秋季采收，洗净，鲜用或干燥', '210', '内服煎汤15~100g,鲜品可用至500g。', '', '', null, null, '8', null, null, null, null, '', 'danzhuye;dzy;zhuyecao;zyc;zhumi;zm;danzhumi;dzm', '2018-07-21 20:26:06', null, null, null, '1');
INSERT INTO `medicine` VALUES ('390', '狼尾草', '白毛根;介狗尾巴', '阴药', '多年生草本。须根较粗而硬。秆丛生,花序以下常密生柔毛。叶鞘两側压扁,基部者相互跨生,除鞘中有毛外,余均光滑无毛;叶舌短小,长不及0.5mm,其上具1圈纤毛;叶片线形,长15 ~ 20cm,宽2~6mm,通常内卷。圆锥花序紧缩呈圆柱状,长5~20cm,宽1 ~ 1.5cm。直立或弯曲,主轴硬,密生柔毛;小穂被针形,通常单生,长6~9mm;刚毛长1 ~2.5cm,具向上微小糙刺,成熟后通常呈黑紫色,颖草质,第1颖微小,卵形,第2颖长为小穂的1/3 ~ 2/3,具3 ~ 5脉;第1外稃与颖同质, 与小穗等长,具7 ~ 11脉,边缘常包巻第2 朵小花;第2外稃平滑,软骨质,等长或短于第1外稃,除先端外边缘全着同质的内稃。颖果长圆形, 长约3.5mm。', '多生长于山坡、路旁及田边。', '夏、秋季采收,洗净,鲜用或干燥', '365', '内服煎汤15~60g。', '', '', null, null, '8', null, null, null, null, 'langweicao other.png', 'langweicao;lwc;baimaogen;bmg;jiegouweiba;jgwb', '2018-07-21 20:25:46', null, null, null, '1');
INSERT INTO `medicine` VALUES ('391', '芦苇', '芦竹;苇', '阴药', '多年生高大草本。地下茎粗壮,横走,节间中空,  节上有芽。茎直立,中空。叶2列,互生,叶鞘圆筒状,叶舌有毛;叶片扁平,长15 ~45cm,宽1 - 3.5cm, 边缘粗糙。穗状花序排列呈大型圆锥花序, 顶生,长20~40cm,微下垂,下部梗叶间具白色柔毛;小穂通常4 ~ 7朵花,长10~ 16cm;第1朵花通常为雄花,颖片披针形,不等长,第1颖片长为第2颖片之半或更短;外祥长于内祥,光滑开展;两性花雄蕊3,雌蕊1,花柱2,柱头羽状。颖果椭圆形或长圆形, 与内桴分离。', '裁培于江河两岸。', '深秋或冬季采收,洗净,鲜用或干燥 。', '87', '芦竹嫩根茎适量,捣烂數患处。', '', '', null, null, '8', null, null, null, null, 'luwei other.png', 'luwei;lw;luzhu;lz;wei;w', '2018-07-21 20:43:37', null, null, null, '1');
INSERT INTO `medicine` VALUES ('392', '毛竹', '毛节竹', '阳药', '高大竹类,秆高可达20m,直径18cm,全竹约70余节,基部节问甚短,初时密被细柔毛和白粉, 尤以节下白粉环特别浓厚; 秆环不明显; 分枝以下捧环微隆起。秆捧厚革质, 密被糙毛和深褐色斑点和斑块, 具发达的捧耳和繸毛;择舌发达,先端微凸, 边缘密生细须毛; 捧片三角形至披针形, 向外反转。末级小枝具叶4 ~ 6枚;叶鞘无叶耳, 具脱落性肩毛;叶舌发达;叶片小,质地薄。', '多栽培于酸性土山上。', '全年采收,洗净,鲜用或干燥。', '211', '毛竹节1节,加冰糖适量, 水煎服。', '', '', null, null, '8', null, null, null, null, 'maozhu other.png', 'maozhu;mz;maojiezhu;mjz', '2018-07-21 20:48:22', null, null, null, '1');
INSERT INTO `medicine` VALUES ('393', '苦竹', '鸬鹚竹;雨伞竹', '阳药', '秆直立,高3~5m,直径1.5~2cm,初时绿色,被厚的黏性白粉, 老秆转绿黄色,被灰褐色粉质状斑;秆环隆起高于箨环,节内长达6mm; 分枝通常5枝。箨鞘深绿色,被紫红色脱落性小刚毛;箨耳和明显或无; 鞘口无毛或着生数枚直立䍁毛;箨舌截形;箨片披针形.録色。末级小枝具叶3 ~ 5枚;叶耳缺或微弱;鞘口无毛;叶舌紫红色;叶片披针形,长14~20cm,宽2.5 ~3cm。花序基部有苞片.花枝与小穗柄略扁平;小穂绿色或淡紫色,含8~12 朵小花,长4 ~ 6cm;小穂轴节同长4 ~ 5mm, 顶端膨大呈环状,有纤毛。颖果长圆形。', '多生长于向阳山坡。', '深秋或冬季采收,洗净，鲜用或干燥。', '61', '苦竹适量磨粉,撤患处。', '', '', null, null, '8', null, null, null, null, 'kuzhu other.png', 'kuzhu;kz;lucizhu;lcz;yusanzhu;ysz', '2018-07-21 20:56:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('394', '玉米', '包罗须;苞罗花', '阴药', '1年生高大草本植物:茎通常不分枝, 基部各节具气生支持根: 叶鞘具横脉;叶片宽大,长披针形.边缘波状褶皱，具强壮中脉。雄蕊小穗长7~10mm；两颖几等长,背部隆起.具9- 10脉；外稃与内稃与颖等长;花药橙黄色,长4~5mm,雌小穗孪生,成8 ~ 18行排列于粗壮而呈海绵状穂轴上;两颖等长,甚宽,无脉现时具纤毛;第1外稃内具内稃可缺;第2外稃似第1外稃,具内温 雌蕊具极长而细弱花柱。 颖果略呈球形, 成熟后超出颖片或稃片 。', '栽培。', '夏、秋季采收,洗净,鲜用或干燥。', '228', '包罗须、鱼腥草、仙鹤草各15g,水煎服。', '', '', null, null, '8', null, null, null, null, 'yumi other.png', 'yumi;ym;baoluoxu;blx;baoluohua;blh', '2018-07-21 21:06:31', null, null, null, '1');
INSERT INTO `medicine` VALUES ('395', '牯岭藜芦', '山棕榈;七里丹;野棕', '阳药', '多年生草本。鳞茎皮残存网状的横脉。茎下部叶片通常椭圆形或长椭圆形，长20~30cm，宽4~7cm，先端急尖或渐尖，基部渐狭呈柄状，两面无毛。圆锥花序金字塔形，长40~80cm，基部分枝长可达13cm，主轴至花梗密被白色短绵毛；花淡黄绿色、绿白色或淡褐色，雄花和两性花同株或全为两性花；花梗长5~8mm；小苞片短于或上部者等长于花梗；花被片椭圆形至长圆状披针形，长6~8mm，宽2mm，先端钝或稍尖，近全缘，外轮花被片背面被白色短绵毛；雄蕊长约为花被片的1/2；子房无毛。蒴果椭圆形或卵状椭圆形。', '暂无信息', '秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, 'D:\\sheyaoImg\\牯岭藜芦+other.jpg', null, '', 'gulinglilu;glll;shanzonglv;szl;qilidan;qld;yezong;yz', '2018-07-21 23:57:27', null, null, null, '1');
INSERT INTO `medicine` VALUES ('396', '文殊兰', '山海带', '阳药', '多年生草本。植株粗壮。叶片带状披针形,长达1m,宽7~12cm,先端渐尖,基部抱茎,边缘微皱纹状,叶脉平行。花茎直立,高约与叶相等;伞形花序有花10~24朵;佛焰苞状总苞片长约10cm;花白色,芳香,高脚碟状,基部有一线形的小苞片;花梗长约2cm;花被管纤细,伸直,淡绿色,花被裂片线状;花丝上部淡紫色,花药黄色,线形。蒴果。', '暂无信息', '夏、秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '文殊兰+other.jpg', 'wenshulan;wsl;shanhaidai;shd', '2018-07-21 23:59:15', null, null, null, '1');
INSERT INTO `medicine` VALUES ('397', '石蒜', '老鸦葱', '阳药', '多年生草本。叶秋季抽出,至翌年夏季枯死,叶片狭带状,长14~30cm,宽约5mm,先端钝,深绿色,中间有粉绿色带。伞形花序有4~7朵花;总苞片干膜质,深褐色,披针形;花鲜红色;花被管绿色,长5~6mm,裂片狭倒披针形;雄蕊比花被长1倍左右。', '暂无信息', '秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '石蒜+other.jpg', 'shisuan;ss;laoyacong;lyc', '2018-07-22 00:00:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('398', '参薯', '白苕;萁', '阳药', '多年生缠绕草本。地下块茎粗壮、肉质。茎右旋，具狭翅4条，无毛。单叶，茎下部者互生，中部以上者对生；叶片纸质，长卵形心形至三角状心形，长8~18cm，宽4.5~12cm，先端渐尖至凸尖，基部深心形至戟形，全缘，两面无毛，主脉7条；叶柄长为叶片的1/2~3/5，具棱翅。未见花果。', '暂无信息', '秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, 'D:\\sheyaoImg\\参薯+other.jpg', 'canshu;cs;baitiao;bt;qi;q', '2018-07-22 00:20:18', null, null, null, '1');
INSERT INTO `medicine` VALUES ('399', '黄独', '铁秤砣;假薯;黄狗头', '阳药', '多年生缠绕草本。地下块茎多呈陀螺形。茎左旋，具细纵槽，无毛。单叶互生；叶片宽卵状心形至圆心形，长9~15cm，宽6~13cm，先端尾尖，基部心形，边缘全缘，两面无毛，主脉7条，侧脉明显，细脉网状；叶柄长为叶片的1/3~9/10，叶脉腋珠芽球形或椭圆形，外皮紫棕色。花单性，雌雄异株；花被紫红色，花被片离生；雄花序穗状，单生或数个簇生，有时再排列成圆锥花序；雄花单生，密集，基部有苞片2枚；雄蕊6，全育，花丝与花药近等长；雌花序穗状，常数个簇生；雌花单生，具退化雄蕊6枚。果序直生，果梗反曲，果面向下；蒴果三棱状长圆形，两端钝圆，表面枯黄色而散生紫色斑点。种子合生于果梗顶端，扁卵形，淡棕色。', '暂无信息', '秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '黄独+other.jpg', 'huangdu;hd;tiechengtuo;tct;jiashu;js;huanggoutou;hgt', '2018-07-22 00:16:29', null, null, null, '1');
INSERT INTO `medicine` VALUES ('400', '粉萆薢', '野猪薯;山萆薢', '阳药', '多年生缠绕草本。地下茎横走,多结节,呈块姜状。茎左旋,具细纵槽,疏生细毛。单叶互生;叶片稍肉质,长心形、长三角状心形至长三角形,长7~19cm，宽4~15cm,先端渐尖,基部心形至平截,边缘微波状至全缘,上面有光泽,常具大块白斑,下面灰绿色,多少被白粉,折痕变黑色,主脉9条,两面脉上疏生短硬毛;叶柄长为叶片的1/3-3/5。花单性,雌雄异株；苞片卵状披针形;小苞片卵形,短于花被裂片;花被淡黄绿色:;雄花序穗状,单生或2~4个簇生,有时再排列成圆锥花序;雄花单生或2~3朵簇生;雄蕊6枚,3枚发育,花丝短;雌花序穗状,单生;雌花单生。果序下垂,果梗反曲,果面向上;蒴果三棱状圆球形,顶端微凹,基部钝圆,表面紫棕色而被白粉。种子生于果轴中部,扁椭圆形;种翅长圆形,棕色。', '暂无信息', '秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '', 'fenbixie;fbx;yezhushu;yzs;shanbixie;sbx', '2018-07-22 00:19:58', null, null, null, '1');
INSERT INTO `medicine` VALUES ('401', '尖叶薯蓣', '野萁;细叶山萁', '阳药', '多年生缠绕草本。地下块茎直生。茎右旋,具细纵槽,无毛。单叶互生,少数对生;叶片纸质,长三角状心形至披针形状心形,长6~18cm,宽2~9cm,先端渐尖,基部心形至箭形,有时平截,全缘,两面无毛,主脉7条;叶柄长为叶片的2/5-1/2;叶腋珠芽球形,表面紫绿色。花单性,雌雄异株;花被淡黄绿色;雄花序穗状,单生或2-3个簇生;雄蕊6枚,全育;雌花序穗状,单生或2~3个簇生;雌花具退化雄蕊6枚。果序下弯,果梗不反曲,果面向下;蒴果三状扁球形,表面枯黄色。种子着生于果轴中部;种翅长圆形,种子据其中央。', '暂无信息', '秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '尖叶薯蓣+other.jpg', 'jianyeshuyu;jysy;yeqi;yq;xiyeshanqi;xysq', '2018-07-22 00:19:45', null, null, null, '1');
INSERT INTO `medicine` VALUES ('402', '黄独', '铁秤砣;假薯;黄狗头', '阳药', '多年生缠绕草本。地下块茎多呈陀螺形。茎左旋，具细纵槽，无毛。单叶互生；叶片宽卵状心形至圆心形，长9~15cm，宽6~13cm，先端尾尖，基部心形，边缘全缘，两面无毛，主脉7条，侧脉明显，细脉网状；叶柄长为叶片的1/3~9/10，叶脉腋珠芽球形或椭圆形，外皮紫棕色。花单性，雌雄异株；花被紫红色，花被片离生；雄花序穗状，单生或数个簇生，有时再排列成圆锥花序；雄花单生，密集，基部有苞片2枚；雄蕊6，全育，花丝与花药近等长；雌花序穗状，常数个簇生；雌花单生，具退化雄蕊6枚。果序直生，果梗反曲，果面向下；蒴果三棱状长圆形，两端钝圆，表面枯黄色而散生紫色斑点。种子合生于果梗顶端，扁卵形，淡棕色。', '暂无信息', '秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '黄独+other.jpg', 'huangdu;hd;tiechengtuo;tct;jiashu;js;huanggoutou;hgt', '2018-07-22 15:26:14', null, null, null, '1');
INSERT INTO `medicine` VALUES ('403', '射干', '疳首;山芭扇;疳药;金绞剪', '阳药', '多年生草本。根状茎粗壮,鲜黄色。叶互生，扁平，两列，叶片剑形，长20~30cm，宽1~4cm，先端渐尖，先端渐尖无中脉，基部鞘状抱茎，苞片卵形至狭卵形，绿色带白粉。二歧状伞花序顶生，花梗与分枝基部均有数枚膜质苞片，苞片卵形至狭卵形；花梗长约1.5cm；花橙红色，散生暗红色斑点；直径4~5cm；外轮花被裂片倒卵形或长椭圆形，先端圆钝或微凹，基部楔形，外轮花被裂片较外轮的稍短而狭；雄蕊长1.8~2cm，花药线性；子房倒卵形，花柱顶端稍偏，裂片稍外卷，具细短毛。蒴果倒卵形或长椭圆形，顶端常宿存凋萎花被。种子圆球形，黑色，有光泽。', '暂无信息', '深秋季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '', 'shegan;sg;ganshou;gs;shanbashan;sbs;ganyao;gy;jinjiaojian;jjj', '2018-07-22 15:30:21', null, null, null, '1');
INSERT INTO `medicine` VALUES ('404', '山姜', '高良姜;山良姜;土砂仁', '阴药', '多年生草本。根状茎横生，节上具鳞片状叶，嫩部呈红色。茎丛生。叶通常2~5片；叶片披针形，长16~29cm，宽3~5cm，先端渐尖，具小尖头，基部渐狭，两面被短柔毛；叶舌二裂，长约2mm，于叶鞘被短柔毛。总状花序顶生，长15~30cm；总花梗被绒毛；苞片披针形；开花时脱落；小苞片极小，早落；花通常2朵成对聚生，白色带红色，在2朵花之间常有退化的小花残迹；萼筒被短柔毛，先端3齿裂；花冠筒长约1cm，被疏柔毛，花冠裂片长圆形，外被绒毛，后方的1枚兜状；侧生退化雄蕊线形，唇瓣卵形，白色具红色脉纹，先端2裂，边缘具不整齐的缺刻；发育雄蕊长1.2~1.4cm；子房密被绒毛。蒴果圆球形或椭圆形，被短柔毛，成熟时橙红色。种子多角形，有樟脑味。', '暂无信息', '深秋至冬季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '山姜+other.jpg', 'shanjiang;sj;gaoliangjiang;glj;shanliangjiang;slj;tusharen;tsr', '2018-07-22 15:33:01', null, null, null, '1');
INSERT INTO `medicine` VALUES ('405', '姜', '生姜', '阳药', '多年生草本。根状茎肉质，块状,稍扁平,有指状分枝。叶片披针形，长15-20cm,宽1.5-2.5cm,先端渐尖,基部狭,无毛;无叶柄,有长鞘;叶舌膜质,不裂。穗状花序;总花梗直立;苞片卵形,淡绿色或边缘淡黄色,先端有小、尖头;花冠筒黄绿色;侧生退化雄蕊较小,与唇瓣合生,卵形,唇瓣长圆状倒卵形,短于花冠裂片,有紫色条纹及淡黄色斑点;发育雄蕊暗紫色,花丝极短,花药长圆形;子房无毛,花柱淡紫色,由药隔处纵贯而出,柱头呈放射状。蒴果长圆形。', '暂无信息', '深秋至冬季采收，洗净，鲜用或干燥。', '1', '暂无信息', '', '', null, null, '7', null, null, null, null, '姜+other.jpg', 'jiang;j;shengjiang;sj', '2018-07-22 15:34:55', null, null, null, '1');
INSERT INTO `medicine` VALUES ('406', '地锦草', '奶奶草;奶疳草', '阳药', '1年生草本。茎匍匐，纤细，常带紫红色，近基部多分枝。叶对生，  叶片长圆形，先端钝圆，基部常偏斜，边缘有细锯齿;托叶深裂，裂片线形。杯状花序单生于叶腋;总苞浅红色，倒圆锥形，顶端4裂，具白色花瓣状附属物。蒴果三棱状球形，光滑无毛。种子紫褐色卵形。\r\n', '多生长于荒地、路旁、田间', '初秋时采收，洗净，鲜用或干燥', '1', '无', '', '', null, null, '1', null, null, null, null, '', 'dijincao;djc;nainaicao;nnc;naigancao;ngc', '2018-09-03 19:59:59', null, '重复', null, '0');
INSERT INTO `medicine` VALUES ('407', '地锦草', '奶奶草;奶疳草', '阳药', '1年生草本。茎匍匐，纤细，常带紫红色，近基部多分枝。叶对生，  叶片长圆形，先端钝圆，基部常偏斜，边缘有细锯齿;托叶深裂，裂片线形。杯状花序单生于叶腋;总苞浅红色，倒圆锥形，顶端4裂，具白色花瓣状附属物。蒴果三棱状球形，光滑无毛。种子紫褐色卵形。\r\n', '多生长于荒地、路旁、田间', '初秋时采收，洗净，鲜用或干燥', '1', '无', '', '', null, null, '1', null, null, null, null, '', 'dijincao;djc;nainaicao;nnc;naigancao;ngc', '2018-09-03 19:59:59', null, '重复', null, '0');
