INSERT INTO `medicine` VALUES ('414', '白背叶', '白山岗子;白叶山桐子;白桐柴;假桐子', '阳药', '直立灌木或小乔木。小枝、叶柄及花序均密被白色或淡黄色星柔毛和散生橙红色腺体。单叶互生，叶片宽卵形，不分裂或3浅裂，先端渐尖，基部圆形或宽楔形，边缘有稀疏的锯齿，，上面暗绿色，无毛或散生星柔毛，下面灰白色，密被星柔毛，叶脉3出，基部有2腺体。穗状花序顶生;花单性同株。蒴果近球形，密生软刺及星柔毛。种子黑色，近球形，有光泽。\r\n', '多生长于低山杂木林中', '深秋采收根，夏秋采收叶，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'baibeiye;bby;baishangangzi;bsgz;baiyeshantongzi;bystz;baitongchai;btc;jiatongzi;jtz', '2018-09-03 20:30:33', null, null, null, '1');
INSERT INTO `medicine` VALUES ('415', '叶下珠', '矮骨水隔猛', '阳药', '1 年生草本，茎直立，具翅状条棱，常带紫红色。单叶互生，呈2列;叶片长圆形，先端钝或有小尖头，基部圆形或宽楔形，常偏斜，全缘，上面绿色，下面灰白色，两面近无毛。花单性同株，几无花梗，无花瓣。蒴果成熟时赤褐色，扁球形，表面有小鳞片状凸起。种子三角状卵形，表面有横纹。\r\n', '多生长于山坡、田间，路旁草丛中。', '夏，秋季时采收，洗净，鲜用或干燥。\r\n', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'yexiazhu;yxz;aigushuigemeng;agsgm', '2018-09-03 20:33:45', null, null, null, '1');
INSERT INTO `medicine` VALUES ('416', '乌桕', '更子树;仲子树', '阳药', '落叶乔木，有乳计。叶片纸质，菱形或蔓状卵形，先端突尖或渐尖，基部楔形，全缘，无毛。总状花序顶生，雄花小，生于花序上部的苞片内，雌花少数，单生于花序下部的苞片内。荫果木质，梨状球形，种子黑色，圆球形，外被白色蜡质假种皮。\r\n', '多生长于山坡林中。有栽培', '多夏、秋季时采收，洗净，鲜用或干燥。\r\n', '1', '暂无', '', '', null, null, '1', null, null, null, null, '', 'wujiu;wj;gengzishu;gzs;zhongzishu;zzs', '2018-09-03 20:37:07', null, '注意事项漏填', null, '0');
INSERT INTO `medicine` VALUES ('417', '乌桕', '更子树;仲子树', '阳药', '落叶乔木，有乳计。叶片纸质，菱形或蔓状卵形，先端突尖或渐尖，基部楔形，全缘，无毛。总状花序顶生，雄花小，生于花序上部的苞片内，雌花少数，单生于花序下部的苞片内。荫果木质，梨状球形，种子黑色，圆球形，外被白色蜡质假种皮。\r\n', '多生长于山坡林中。有栽培', '多夏、秋季时采收，洗净，鲜用或干燥。', '1', '体虚、孕妇及溃疡病者禁服。\r\n', '', '', null, null, '7', null, null, null, null, '', 'wujiu;wj;gengzishu;gzs;zhongzishu;zzs', '2018-09-03 20:38:40', null, null, null, '1');
INSERT INTO `medicine` VALUES ('418', '匙叶黄杨', ' 万年青;黄杏树', '阴药', '常绿灌木，多分枝，密集成丛。小枝纤细，四棱形。叶对生，叶片薄革质，匙形、狭倒卵形或倒披针形，先端圆钝，常微凹，基部狭楔形，全缘，上面淡绿色，光亮，下部中脉处有微细毛，下面苍绿色，无毛。头状花序腋生;  花单性，雌雄同序，雄花生于花序下部，雌花生于花序顶端。荫果球形，宿存花柱直立。\r\n', '多生长于溪流旁石缝中或林下。有裁培。', '深秋采收， 洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'chiyehuangyang;cyhy; wannianqing; wnq;huangxingshu;hxs', '2018-09-03 20:41:52', null, null, null, '1');
INSERT INTO `medicine` VALUES ('419', '盐肤木', '盐芋根;盐肤柴;盐葡萄', '阳药', '落叶灌木或小乔木，高2一10m。小枝棕褐色，被锈色柔毛，具圆形小皮孔。奇数羽状复叶互生，长25 ~ 45cm,叶轴及叶柄常有翅，小叶5~ 13 枚:小叶片纸质，多形，常卵形至卵状长圆形，长3一12cm，宽2~ 7cm，先端急尖，基部宽楔形或圆形，稍偏斜，边缘具粗锯齿，上面暗绿色，沿中脉被锈色短柔毛或近无毛，下面粉绿色，被白粉，密被锈色柔毛，无柄或近无柄。圆锥花序宽大，顶生，多分枝，雄花序长20一40cm,雌花序较短，密被锈色柔毛，雄花花尊5裂，尊片广卵形，长约1mm,花瓣白色，5枚，倒卵状长圆形，长约2mm，开放时外卷，雄蕊伸出，花丝线形，花药卵形;雌蕊花夢，花瓣均比雄蕊小，子房卵形，密被白色柔毛，花柱3,柱头头状。核果球形，略压扁，成熟时橙红色，直径4一5mm，具节柔毛或腺毛，果柄3~ 4mm。\r\n\r\n', '多生长于向阳山坡、林缘。沟谷或灌木丛中。', '深秋采收，洗净，鲜用或干燥。\r\n', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'yanfumu;yfm;yanyugen;yyg;yanfuchai;yfc;yanputao;ypt', '2018-09-03 20:43:37', null, null, null, '1');
INSERT INTO `medicine` VALUES ('420', '枸骨', '野黄柏;八角刺', '阳药', ' 常绿灌木或小乔木。单叶互生，硬革质，四方状长园形而具宽三角形，先端有硬针刺的齿，或长圆形、倒卵状长圆形而全缘，但先端仍具硬针刺，先端2~ 3个尖刺状急尖，或短渐尖，基部圆形或截形，每边具1 ~ 3个硬针刺，上面有光泽。花序簇生叶腋，花小，黄绿色。核果球形，成熟时红色。\r\n\r\n', '多生长于荒地、山坡沟边杂木林或灌木丛中。有栽培。', '深秋采收根，夏、秋季采收叶，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'gougu;gg;yehuangbai;yhb;bajiaoci;bjc', '2018-09-03 20:47:49', null, null, null, '1');
INSERT INTO `medicine` VALUES ('421', '大叶冬青', '苦丁茶', '阳药', '常绿大乔木。单叶互生，叶片厚革质，长圆形或卵状长圆形，先端短渐尖或钝，基部宽楔形或圆形，边缘有疏锯齿，中脉上面凹人，下面强隆起，上面深绿色，有光泽，下面淡绿色。圆锥花序簇生于叶腋。果实球形，成熟时红色。', '多生长于山坡、山谷的常绿阔叶林中。有栽培。', '多夏、秋季时采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'dayedongqing;dydq;kudingcha;kdc', '2018-09-03 21:02:09', null, null, null, '1');
INSERT INTO `medicine` VALUES ('422', '毛冬青', '火柴头;细叶冬青;风寒头', '阳药', '常绿灌 木或小乔木。小枝有棱，密被粗毛。单叶互生，叶片纸质或膜质， 卵形或椭圆形，先端短渐尖或急尖，基部宽楔形或圆飩，边緣有稀疏的小尖齿或近全缘，中脉上面凹下，两面有疏粗毛，沿脉有襇密短粗毛。花序演生叶腋。果球形，成熟时红色，宿存花柱明显。\r\n', '多生长 于山坡灌木从或荒山草丛中。', '深秋采收根+ 夏、秋季采收叶，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'maodongqing;mdq;huochaitou;hct;xiyedongqing;xydq;fenghantou;fht', '2018-09-03 21:03:37', null, null, null, '1');
INSERT INTO `medicine` VALUES ('423', '过山枫', '穿山龙;过山虎', '阳药', '常绿木质藤本。冬芽圆锥形， 最外两枚芽鳞片特化成三角形刺。单叶互生，叶片近革质，椭圆形或宽卵状椭圆形，先端急尖，基部楔形或圆形，边缘有疏细锯齿，近基部全缘。聚伞花序腋生或侧生，黄绿色。蒴果近球形。种子新月形或半圆形，一端圆钝，另一端略尖，深褐色，具稠密的疣点，有橙红色假种皮。\r\n', '多生长于山坡灌木丛中。', '深秋或冬季采收，洗净，鲜用或千燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'guoshanfeng;gsf;chuanshanlong;csl;guoshanhu;gsh', '2018-09-03 21:04:56', null, null, null, '1');
INSERT INTO `medicine` VALUES ('424', '卫矛', '四面风;擂毒柴', '阳药', '落叶灌木。小枝具四棱，通常具棕褐色宽阔木栓翅，少数无翅。叶片纸质，无毛，倒卵形、椭園形或菱状倒卵形，先端急尖，基部楔形、阔楔形至近圆形，聚伞花序腋生，有花3一5朵。花淡黄绿色。蒴果棕褐色带紫，几全裂呈分果状。种子紫褐色，椭圆形，橙红色假种皮全部包围种子。\r\n', '多生长于沟谷、山坡国叶混交林中及林缘或草地。', '夏至深秋季节采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'weimao;wm;simianfeng;smf;leiduchai;ldc', '2018-09-03 21:06:21', null, null, null, '1');
INSERT INTO `medicine` VALUES ('425', '扶芳藤', '墙头藤;爬墙老虎', '阳药', '常绿仰匐或攀缘灌木。枝上通常有细根，小枝上密市细瘤状皮孔，冬芽卵形，芽鳞有紫红色边缘。叶片革质，宽椭圆形至长圆状倒卵形，先端短锐尖或短渐尖，基部宽楔形或近圆形，边缘有钝锯齿。聚伞花序具多数花，花序及果序较密集。蒴果近球形，成熟时淡红色。种子卵形，有橙红色假种皮。\r\n', '多生长于村庄墙上、 山谷林缘，树上或岩石上。', '夏季至深秋季节采收，洗净，鲜用或干燥。\r\n', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'fufangteng;fft;qiangtouteng;qtt;paqianglaohu;pqlh', '2018-09-03 21:07:22', null, null, null, '1');
INSERT INTO `medicine` VALUES ('426', '扶芳藤', '墙头藤;爬墙老虎', '阳药', '常绿仰匐或攀缘灌木。枝上通常有细根，小枝上密市细瘤状皮孔，冬芽卵形，芽鳞有紫红色边缘。叶片革质，宽椭圆形至长圆状倒卵形，先端短锐尖或短渐尖，基部宽楔形或近圆形，边缘有钝锯齿。聚伞花序具多数花，花序及果序较密集。蒴果近球形，成熟时淡红色。种子卵形，有橙红色假种皮。\r\n', '多生长于村庄墙上、 山谷林缘，树上或岩石上。', '夏季至深秋季节采收，洗净，鲜用或干燥。\r\n', '1', '暂无', '', '', null, null, '8', null, null, null, null, '', 'fufangteng;fft;qiangtouteng;qtt;paqianglaohu;pqlh', '2018-09-03 21:08:30', null, null, null, '1');
INSERT INTO `medicine` VALUES ('427', '雷公藤', '雷公藤;菜虫药', '阳药', '落叶蔓生灌木。小枝红褐色，具4~6棱，密被锈色短毛，皮孔瘤状突起。叶片纸质，宽椭圆形、宽卵形或卵状椭圆形，先端短尖或渐尖，基部圈形或宽楔形，边缘有细锯齿。圆锥状聚伞花序顶生或腋生，总花梗密被锈色短毛，花淡绿色，花瓣边缘略带虫蚀状。翅果不裂，长圆形，具3翅。种子1粒，细柱状，黑色。\r\n', '多生长于山坡林中或路旁。有栽培。', '深秋或冬季采收根，除去根皮，鲜用或干燥。', '1', '有大毒。应先煎2一3小时。\r\n', '', '', null, null, '7', null, null, null, null, '', 'leigongteng;lgt;leigongteng;lgt;caichongyao;ccy', '2018-09-03 21:09:44', null, null, null, '1');
INSERT INTO `medicine` VALUES ('428', '野鸦椿', '白鸡聆;粪缸柴;木鱼柴', '阳药', '落叶灌木或小乔木。枝叶揉碎后发出恶臭气味。奇数羽状复叶小叶多5一9，叶片厚纸质，椭圆形或卵形至长卵形，先端渐尖至长渐尖，基部圆形至宽楔形，常偏斜，边缘具细锐锯齿，齿尖有腺体。圆唯花序顶生，花黄白色。普黄果果皮软革质，成熟时紫红色，有纵脉纹。种子近圆形，假种皮黑色。\r\n', '多生长于山谷、山坡、溪沟边，路旁及杂木林中。', '深秋季采收，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'yeyachun;yyc;baijiling;bjl;fengangchai;fgc;muyuchai;myc', '2018-09-03 21:11:11', null, null, null, '1');
INSERT INTO `medicine` VALUES ('429', '凤仙花', '指甲花', '阳药', '年生草本。茎租壮，肉质，直立，下部节常膨大。叶互生，最下部叶有时对生，叶片披针形、狭椭圈形或倒披针形，先端尖或渐尖，基部楔形，边缘有锐锯齿，向基部常有数对无柄的黑色腺体。花单生或2~ 3朵簇生于叶腋，无总花梗，色泽多样。蒴果宽纺锤形，两端尖，密被柔毛。种子多数，圆球形，黑褐色。\r\n\r\n', '栽培', '夏、秋季采收，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'fengxianhua;fxh;zhijiahua;zjh', '2018-09-03 21:12:19', null, null, null, '1');
INSERT INTO `medicine` VALUES ('430', '多花勾儿茶', '铁包金;打串子;清水;画眉杠;打铳子', '阳药', '藤状灌木。上部叶片较小，卵形、卵状椭圆形至卵状披针形，先端纯或圆，稀短渐尖，基部圆形、稀心形。花排成具长分枝的宽大的聚伞圆锥花序，有的下部肢生聚伞总状花序。核果圆柱形。\r\n', '多生长于溪沟边或山坡灌木丛中。', '夏、秋季采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'duohuagouercha;dhgec;tiebaojin;tbj;dachuanzi;dcz;qingshui;qs;huameigang;hmg;dachongzi;dcz', '2018-09-03 21:13:36', null, null, null, '1');
INSERT INTO `medicine` VALUES ('431', '牯岭勾儿茶', '铁包金', '阳药', '藤状灌木。叶片纸质，卵状椭圆形或卵状长圆形，先端钝圆或尖，具小尖头，基部圆形或心形，花序为不分枝的聚伞总状花序。核果长圆形，红色，成熟时紫红色。\r\n', '多生长于沟谷、山坡灌木丛中。', '深秋采收，洗净，鲜用或千燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'gulinggouercha;glgec;tiebaojin;tbj', '2018-09-03 21:14:26', null, null, null, '1');
INSERT INTO `medicine` VALUES ('432', '枳机', '鸡爪梨;解酒梨', '阳药', ' 落叶乔木。叶互生，叶片纸质，椭國状卵形、卵形或寬椭國状卵形，先端短渐尖或渐尖，基部圆形或微心形，边缘具不整齐锯齿。聚伞国维花序顶生或腋生，花黄绿色。花序轴果期膨大。核果近球形，成熟时黑色。种子深果色或黑紫色，有光泽。\r\n', '多 栽培。', '深秋采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'zhiji;zj;jizhaoli;jzl;jiejiuli;jjl', '2018-09-03 21:15:27', null, null, null, '1');
INSERT INTO `medicine` VALUES ('433', '长叶冻绿', '鸡骨柴;黄烂;山绿柴', '阳药', '落叶灌木或乔木。枝端有密被铁锈色柔毛的裸芽。叶互生，倒卵状椭圆形、椭圆形或倒卵形，先端渐尖，短突尖， 长叶冻绿基部楔形，边缘具圆细锯齿，下面被柔毛。聚伞花序腋生，花两性，5基数，花柱不分裂。核果球形，成熟时紫黑色，具3粒分核。种子无沟。\r\n\r\n', '多生长于山地、 林下或灌木丛中。', '深秋采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'changyedonglv;cydl;jiguchai;jgc;huanglan;hl;shanlvchai;slc', '2018-09-03 21:16:43', null, null, null, '1');
INSERT INTO `medicine` VALUES ('434', '冻绿', '鸭绿柴;铁链', '阳药', '落叶灌木或小乔木。幼枝顶端常具针刺;无顶芽，腋芽鳞片边缘有白色缘毛。叶簇生于枝端上，对生或近对生，叶片狭长椭圆形，长圆形或倒卵状椭圆形，先端突尖或锐尖，  基部楔形，边缘具细锯齿，下面沿脉或脉腋有金黄色柔毛。花单性异株。核果球形，成熟时黑色，有2粒分核。种子背侧基部有短沟。\r\n', '多生长于低山杂木林或灌木丛中。', '深秋采收，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'donglv;dl;yalvchai;ylc;tielian;tl', '2018-09-03 21:17:47', null, null, null, '1');
INSERT INTO `medicine` VALUES ('435', '雀梅藤', '酸梅根', '阳药', '灌木或呈藤状。叶对生或互生，叶片纸质，椭圆形、长圆形或卵状椭圆形，先端钝、钝尖或圆，中脉常伸出，基部圆形或近心形边缘有密的细锯齿。花无梗，黄色。核果圆，有1~3粒分核。种子扁平，两端微凹。\r\n', '多生长于山坡灌木丛中。有栽培。', '深秋采收，洗净，鲜用或干燥。\r\n', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'quemeiteng;qmt;suanmeigen;smg', '2018-09-03 21:18:36', null, null, null, '1');
INSERT INTO `medicine` VALUES ('436', '蛇葡萄', '山天罗;大叶小葡萄;岩盖山;白山天罗', '阳药', ' 木质藤本。根较粗壮，卷须分叉。叶片纸质，阔卵状心形，长与宽几相等，常三浅裂，侧裂片很小，三角状卵状，有时不分裂，基部多心形，边缘有浅圆齿。聚伞花序，花小，黃绿色，两性。浆果近球形，由深绿变紫至鲜蓝色，\r\n', '多生长于山坡、 山溪沟旁灌木丛中。', '深秋或初冬采收，洗净，鲜用或干燥。\r\n', '1', '作用相同的还有牯岭蛇简萄的根', '', '', null, null, '7', null, null, null, null, '', 'sheputao;spt;shantianluo;stl;dayexiaoputao;dyxpt;yangaishan;ygs;baishantianluo;bstl', '2018-09-03 21:20:04', null, null, null, '1');
INSERT INTO `medicine` VALUES ('437', '乌蔹莓', '五爪金龙;细叶南绳;猪娘菜', '阳药', '多年生草质藤本。幼枝绿色，老枝紫绿色，具纵棱，卷须分叉。鸟足状复叶，小叶5,纸质或膜质，小叶片椭圆形或狭卵形， 先端急尖至短渐尖，有小尖头，基部楔形至宽楔形，边缘具8一15枚锯齿，中间小叶片较大。聚伞花序腋生或假腋生，伞房状;花小，黄白色，具短柄。浆果卵形，成熟时黑色。\r\n\r\n', '多生长于山坡、路边杂草丛中及墙边等。', '夏、秋季采收全草;秋。冬季采收根，洗净，鲜用或千燥。\r\n', '1', '畲民一般用红色叶的品种根人药。\r\n', '', '', null, null, '7', null, null, null, null, '', 'wulianmei;wlm;wuzhaojinlong;wzjl;xiyenansheng;xyns;zhuniangcai;znc', '2018-09-03 21:21:20', null, null, null, '1');
INSERT INTO `medicine` VALUES ('438', '爬山虎', '穿山龙;钻地风', '阳药', ' 落叶大型攀缘木质藤本。叶片异形，发育枝上的叶片宽卵形，先端通常3浅裂，基部心形，边缘有粗锯齿;不育枝上有叶片3全裂或三出复叶，中间小叶片倒卵形，两侧小叶片斜卵形，有粗锯齿，幼枝上的叶片则又较小而不裂。聚伞花序通常生于具2叶的短枝上,花绿色。浆果成熟时蓝色。\r\n\r\n', '多生长于山坡岩石上或墙壁上。', '深秋或初冬采收，洗净，鲜用或千燥。\r\n', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'pashanhu;psh;chuanshanlong;csl;zuandifeng;zdf', '2018-09-03 21:22:19', null, null, null, '1');
INSERT INTO `medicine` VALUES ('439', '三叶崖爬藤', '三叶青;金线(丝)吊葫芦', '阳药', '多年生常绿革质蔓生藤本。块根卵形或椭圆形，表面深棕色，里而白色。茎下部节下生根，卷须不分枝，与叶对生。掌状复叶互生，小叶3，中间小叶片稍大，近卵形或披针形，先端渐尖，有小尖头，边缘疏生具腺状尖头的小锯齿，侧生小片基部偏斜。聚伞花序生于当年新枝上，总花梗短于叶柄，花小黄绿色。浆果球形，初红褐色，成熟时黑色。种子1粒。\r\n\r\n', '多生长于山坡或山沟、 溪谷两旁林下阴湿处。', '深秋或初冬采收根割取全草，洗净，鲜用或干燥。', '1', '暂无', '', '', null, null, '7', null, null, null, null, '', 'sanyeyapateng;syypt;sanyeqing;syq;jinxian(si)diaohulu;jx(s)dhl', '2018-09-03 21:23:33', null, null, null, '1');
