-- Table structure for medicine
-- ----------------------------
DROP TABLE IF EXISTS `medicine`;
CREATE TABLE `medicine` (
  `Medicine_ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '畲药序号，主键，自增',
  `Medicine_name` varchar(10) NOT NULL COMMENT '畲药名称（由于最大为五个中文字符，故而设置长度为10）',
  `Medicine_anotherName` varchar(27) DEFAULT NULL COMMENT '畲药别名，每个名字之间用英文符号“_”隔开（由于最多为27个字符，因此初始值设置为27）',
  `Medicine_property` varchar(100) NOT NULL COMMENT '畲药药性，根据中华本草中的药性来填入',
  `Medicine_introduce` varchar(5000) NOT NULL COMMENT '畲药外貌介绍（由于内容较多，因此初始长度设置为500）',
  `Medicine_distribution` varchar(300) NOT NULL COMMENT '畲药生长分布',
  `Medicine_CollectionProcessing` varchar(255) NOT NULL COMMENT '畲药采集加工',
  `Medicine_disease` varchar(500) DEFAULT NULL COMMENT '畲药应用的病症，用“病症ID_病症ID”来输入这种畲药能应用的病症；病症ID来源Disease表',
  `Medicine_precautions` varchar(250) DEFAULT NULL COMMENT '畲药的使用注意',
  `Medicine_ChemicalComponent` varchar(500) DEFAULT NULL COMMENT '畲药的化学成分，由于不好做系统的分类，因此将内容全部输入',
  `Medicine_ReferencesChemical` varchar(500) DEFAULT NULL COMMENT '化学成分的参考文献，输入格式按照参考文献格式，每个文献之间用“；”分割，符号均为英文状态下的符号',
  `Medicine_ReferencesClinic` varchar(500) DEFAULT NULL COMMENT '现代临床研究的参考文献，输入格式按照参考文献格式，每个文献之间用“；”分割，符号均为英文状态下的符号',
  `Medicine_ReferencesPharmacology` varchar(500) DEFAULT NULL COMMENT '药理的参考文献，输入格式按照参考文献格式，每个文献之间用“；”分割，符号均为英文状态下的符号',
  `Medicine_planCategory` int(1) NOT NULL COMMENT '植物类别（1：藻类植物；2：菌类植物；3：地衣类植物；4：苔藓类植物；5：蕨类植物；6：裸子植物；7：双子叶植物）',
  `Medicine_plantSeed` varchar(400) DEFAULT NULL COMMENT '畲药种子期照片路径',
  `Medicine_plantJuvenile` varchar(400) DEFAULT NULL COMMENT '畲药幼年期图片路径',
  `Medicine_plantAdult` varchar(400) DEFAULT NULL COMMENT '畲药成年期图片路径',
  `Medicine_plantWilting` varchar(400) DEFAULT NULL COMMENT '畲药枯萎期照片',
  `Medicine_plantImg` varchar(400) DEFAULT NULL COMMENT '畲药其他图片路径，路径之间中间用“；”分割',
  `Medicine_forSelect` varchar(800) DEFAULT NULL COMMENT '畲药的全拼和简拼（每个拼音之间用英文的“；”隔开，需要输入全部名字的拼音和简拼音）',
  `Medicine_date` datetime DEFAULT NULL COMMENT '修改时间',
  `Medicine_deleteTag` varchar(20) DEFAULT NULL COMMENT '删除标记，分为可用和不可用',
  `Medicine_deleteReason` varchar(255) DEFAULT NULL COMMENT '删除原因',
  `Medicine_mainUse` varchar(255) DEFAULT NULL COMMENT '主要用途',
  `Medicine_State` char(1) DEFAULT '1' COMMENT '是否存在，存在：1；不存在：0',
  PRIMARY KEY (`Medicine_ID`)
) ENGINE=InnoDB AUTO_INCREMENT=440 DEFAULT CHARSET=utf8;

